// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdRoundedBox( in vec2 p, in vec2 b, in vec4 r )
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdPie( in vec2 p, in vec2 c, in float r )
{
    p.x = abs(p.x);
    float l = length(p) - r;
    float m = length(p-c*clamp(dot(p,c),0.0,r)); // c=sin/cos of aperture
    return max(l,m*sign(c.y*p.x-c.x*p.y));
}

float dTri(vec2 p, vec2 s){
    p*=-1.0;
    return Tri(p,s,radians(30.0));
}

float animationMask(vec2 p, float speed){
    float t = mod(iTime*speed,360.0);
    float rad = radians(t);
    float d = sdPie(p,vec2(sin(rad),cos(rad)),0.15);
    return d;
}

float charT(vec2 p, int animate, float speed) {
    vec2 prevP = p;
    float w = 0.03;
    float size = 0.1;
    float d = sdBox(p,vec2(size));

    float mask = sdBox(p-vec2(0.031,-0.031),vec2(0.071));
    d = max(-mask,d);

    p*=Rot(radians(45.0));
    p.x+=0.075;
    d = max(-p.x,d);

    p = prevP;
    p*=Rot(radians(45.0));
    d = max(p.x,d);

    p = prevP;
    if(animate == 1){
        mask = animationMask(p,speed);
        d = max(-mask,d);
    }
    return d;
}

float charD(vec2 p, int animate, float speed) {
    vec2 prevP = p;
    float w = 0.03;
    float size = 0.1;
    float d = sdRoundedBox(p,vec2(size),vec4(0.05,0.05,0,0));
    float d2 = sdRoundedBox(p,vec2(0.04,0.04),vec4(0.025,0.025,0,0));
    d = max(-d2,d);
    p*=Rot(radians(45.0));
    p.x+=0.04;
    d = max(-p.x,d);

    p = prevP;

    if(animate == 1){
        float mask = animationMask(p,speed);
        d = max(-mask,d);
    }

    return d;
}

float charF(vec2 p, int animate, float speed) {
    vec2 prevP = p;
    float size = 0.1;
    float d = sdBox(p,vec2(size));
    p*=Rot(radians(45.0));
    d = max(p.x,d);
    p = prevP;
    float mask = sdBox(p-vec2(0.06,0.025),vec2(0.1,0.015));
    d = max(-mask,d);

    if(animate == 1){
        mask = animationMask(p,speed);
        d = max(-mask,d);
    }
    return d;
}

float drawTDFLogo(vec2 p, int animate, float speed){
    float d = charT(p-vec2(-0.13,0.0),animate,speed);
    float d2 = charD(p-vec2(-0.05,0.0),animate,speed);
    d = min(d,d2);
    d2 = charF(p-vec2(0.17,0.0),animate,speed);

    return min(d,d2);
}

float drawTDFLogoPararell(vec2 p, int animate, float speed){
    float d = charT(p-vec2(-0.13,0.0),animate,speed);
    float d2 = charD(p-vec2(-0.05,0.0),animate,speed*0.5);
    d = min(d,d2);
    d2 = charF(p-vec2(0.17,0.0),animate,speed*0.75);

    return min(d,d2);
}

vec3 drawHUD(vec2 p, vec3 col){
    vec2 prevP = p;

    p*=Rot(radians(-iTime*10.0));
    p = DF(p,16.0);
    p = abs(p);
    p -= vec2(0.215);
    p*=Rot(radians(45.0));
    float d = B(p,vec2(0.001,0.005));
    col = mix(col,vec3(1.0),S(d,0.0));

    p = prevP;
    p*=Rot(radians(-iTime*10.0));
    p = DF(p,4.0);
    p = abs(p);
    p -= vec2(0.21);
    p*=Rot(radians(45.0));    
    d = B(p,vec2(0.001,0.015));
    col = mix(col,vec3(1.0),S(d,0.0));

    p = prevP;
    p*=Rot(radians(iTime*20.0));
    d = abs(length(p)-0.32)-0.005;
    d = max(-B(p,vec2(1.0,0.2)),d);

    p = mod(p,0.005)-0.0025;
    d = max(-B(p,vec2(0.003)),d);

    col = mix(col,vec3(1.0),S(d,0.0));

    p = prevP;
    p*=Rot(radians(sin(iTime*1.0)*45.0));
    p = DF(p,1.0);
    p = abs(p);
    p -= vec2(0.24);
    p*=Rot(radians(45.0));   
    d = dTri(p,vec2(0.015));
    col = mix(col,vec3(1.0),S(d,0.0));

    return col;
}

vec3 drawUI(vec2 p, vec3 col){
    vec2 prevP = p;

    p*=3.5;
    p = abs(p);
    p.x -=2.7;
    p.y -=1.3;
    p*=Rot(radians(-90.0));

    float d = drawTDFLogo(p,1,0.0);
    col = mix(col,vec3(1.0),S(d,0.0));
    p = prevP;
    p*=0.8;

    float scene = getSceneTime();
    if((scene>=SCENE0 && scene<SCENE1)||(scene>=SCENE2 && scene<SCENE3)){
        col = drawHUD(p,col);
    }
    return col;
}

vec3 startSceneBg(vec2 p, vec3 col){
    p = mod(p,0.2)-0.1;
    float d = B(p,vec2(0.00001,0.01));
    float d2 = B(p,vec2(0.01,0.00001));
    d = min(d,d2);
    col = mix(col,vec3(0.9,0.9,0.1)*0.5,S(d,0.0));

    return col;
}

vec3 endSceneBg(vec2 p, vec3 col){
    vec2 gv = fract(p)-0.5;
    vec2 id = floor(p);

    float n = Hash21(id);

    gv*=0.3;
    float d = charT(gv,1,100.0);    
    if(n>=0.4 && n<0.7){
        d = charD(gv,1,120.0*n);
    } else if(n>=0.7){
        d = charF(gv,1,140.0*n);
    }

    col = mix(col,vec3(0.9,0.9,0.1),S(d,0.0));

    return col;
}

vec3 postEffect(vec2 p, vec3 col){
    p*=8.0;

    float y = fract(p.y)-0.5;
    vec2 gv = fract(p)-0.5;
    vec2 id = floor(p);
    float n = Hash21(id);

    p.y = y;

    float d = B(p,vec2(cos(iTime*n*20.0)*100.0*n*n,sin(iTime*n*20.0)*100.0*n*n));
    col = mix(col,vec3(0.9,0.9,0.1)*0.75,S(d,0.0));
    return col;
}

vec3 logoAnimationBg(vec2 p, vec3 col){
    float brightness = 0.2;
    vec2 prevP = p;
    p.x+=iTime*0.1;
    p.x = mod(p.x,0.54)-0.27;
    p.y = mod(p.y,0.45)-0.225;
    float d = drawTDFLogoPararell(p,1,100.0);
    col = mix(col,vec3(0.9,0.9,0.1)*brightness,S(d,0.0));
    p = prevP;
    p.x-=iTime*0.1;
    p.y+=0.225;
    p.x = mod(p.x,0.54)-0.27;
    p.y = mod(p.y,0.45)-0.225;
    d = drawTDFLogoPararell(p,1,120.0);
    col = mix(col,vec3(0.9,0.9,0.1)*brightness,S(d,0.0));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 prevUV = uv;

    float scene = getSceneTime();

    if(scene>=SCENE1 && scene<SCENE2){
        uv.y+=scene*0.1;
        uv*=2.4;
        uv = fract(uv)-0.5;
    } else if(scene>=SCENE3){
        uv.y+=scene*0.1;
        uv*=1.4;
        uv.y = fract(uv.y)-0.5;
    }

    vec2 m = iMouse.xy/iResolution.xy -.3;

    float cz = 0.85;
    if(scene>=SCENE0 && scene<=SCENE1){
        cz = 1.;
    }  else if(scene>=SCENE1 && scene<=SCENE2){
        cz = 1.2;
    } else if(scene>=SCENE2){
        cz = 1.0;
    }

    vec3 ro = vec3(0, 0, cz);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    if(scene<SCENE0){
        ro.yz *= Rot(radians(90.0));
        ro.xz *= Rot(radians(-90.0));
    } else {
        if(scene>=SCENE1 && scene<SCENE2){
            ro.yz *= Rot(radians(-35.0));
            ro.xz *= Rot(radians(-5.0+scene*20.0));
        } else if(scene>=SCENE2 && scene<SCENE3){
            ro.yz *= Rot(radians(30.0));
            ro.xz *= Rot(radians(-5.0+scene*20.0));
        } else if(scene>=SCENE3){

            ro.xz *= Rot(radians(-25.0+scene*20.0));
            ro.yz *= Rot(radians(85.0));

        } else {
            ro.xz *= Rot(radians(-5.0+scene*20.0));
        }

    }
    #endif

    vec3 rd = R(uv, ro, vec3(0,0.0,0), 1.0);
    vec2 d = RayMarch(ro, rd, 1.);
    vec3 col = vec3(1.0);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
        int mat = int(d.y);
        col = materials(mat,n,rd,p,col);
        //col *= exp( -0.038*d.x*d.x*d.x );//fog
    } else {
        //col = upperTex(uv,col);
        uv = prevUV;

        col = vec3(0.0);

        uv.y+=scene*0.1;
        uv*=0.5;

        if(scene<SCENE0){
            uv = prevUV;
            uv.y+=scene*0.1;
            col = startSceneBg(uv,col);
        }

        if(scene>=SCENE0 && scene<SCENE1){
            uv.y = mod(uv.y,0.4)-0.2;
            float ld = drawTDFLogo(uv-vec2(0.0,0.08),0,0.0);
            col = mix(col,vec3(0.9,0.9,0.1)*0.25,S(ld,0.0));
            ld = drawTDFLogo(uv,0,0.0);
            col = mix(col,vec3(0.9,0.9,0.1)*0.5,S(ld,0.0));
            ld = drawTDFLogo(uv-vec2(0.0,-0.08),0,0.0);
            col = mix(col,vec3(0.9,0.9,0.1),S(ld,0.0));
        }

        uv = prevUV;
        if(scene>=SCENE2 && scene<SCENE3){
                uv.xy *= Rot(radians(sin(scene*.3)*20.0));
                float d2 = abs(uv.y);
                float k = 0.5 / d2;
                uv = uv * k + vec2(0, k);
                uv-=iTime*0.5;
                col = endSceneBg(uv,col);
                col*=d2*2.0;
        }

        if(scene>=SCENE3){
            col = logoAnimationBg(uv,col);
        }
    }

    // UI
    //float ld = drawTDFLogo(uv,1,60.0);
    if(scene<SCENE0){
        uv = prevUV;
        float ld = drawTDFLogo(uv,0,0.0);
        col = mix(col,vec3(0.9,0.9,0.1),S(ld,0.0));
    }

    // gamma correction
    col = pow( col, vec3(0.9545) );    

    uv = prevUV;
    col = drawUI(uv,col);

    if(scene>=SCENE0-0.2 && scene<SCENE0+0.1){
        col = postEffect(uv,col);
    } else if(scene>=SCENE1-0.2 && scene<SCENE1+0.1){
        col = postEffect(uv,col);
    } else if(scene>=SCENE2-0.2 && scene<SCENE2+0.1){
        col = postEffect(uv,col);
    } else if(scene>=SCENE3-0.1 && scene<SCENE3+0.1){
        col = postEffect(uv,col);
    } else if(scene>SCENE_LENGTH-0.2 || scene<0.1){
        col = postEffect(uv,col);
    }

    fragColor = vec4(col,1.0);
}

float rectshape(vec2 position, vec2 scale){
	scale = vec2(0.5) - scale * 0.7;
	vec2 shaper = vec2(step(scale.x, position.x), step(scale.y, position.y));
    shaper *= vec2(step(scale.x, 1.0 - position.x), step(scale.y, 1.0 - position.y));
	return shaper.x * shaper.y; 
}

mat2 rotate(float angle){
	return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 coord = fragCoord/iResolution.y;

    vec3 color = vec3(0.0);

	coord -= vec2(0.5);
    coord = (fragCoord - .5* iResolution.xy ) /iResolution.y;
	coord = rotate(0.3*sin(iTime))*coord ;
	coord += vec2(0.5);

	color += vec3(rectshape(coord, vec2(0.3, 0.3)));

    // Output to screen
    fragColor = vec4(color,1.0);
}

// Watch the recording of the live coding on YouTube.
// https://youtu.be/bp37xTVNRrM?t=16035

#define time iTime
#define hash(x) fract(sin(x)*1763.2632)
#define saturate(x) clamp(x,0.,1.)
const float pi=acos(-1.);
const float pi2=acos(-1.)*2.;
const float N=50.;

mat2 rot(float a){
  float s=sin(a),c=cos(a);
  return mat2(c,s,-s,c);
}

float rt=1e5;
vec3 rn;
float rid;
vec2 ruv;
void intersect(vec3 ro,vec3 rd,vec3 ce,mat2 M,float id,float s){
  M[0][1]*=s;
  M[1][0]*=s;
  vec3 n=vec3(vec2(0,1)*M,0);
  float t=dot(ce-ro,n)/dot(rd,n);
  if(t<0.||t>rt)return;
  vec3 q=ro+t*rd-ce;
  q.yx*=M;
  if(q.x*s<0.)return;
  vec2 p=q.xz;

  p.x=abs(p.x)*.8;
  if(p.x>sin(p.y*50.)*.025+.4+p.y*.4)return;
  if(p.y<0.)p*=1.5;
  p.y=abs(p.y);
  if(length(p)>sin(atan(p.y,p.x)*2.)+smoothstep(.1,0.,p.y)*.3)return;
  if(dot(rd,n)>0.)n*=-1.;

  rt=t;
  rn=n;
  rid=id;
  ruv=q.xz;
}

vec3 hsv(float h,float s,float v){
  vec3 res=fract(h+vec3(0,2,1)/3.)*6.-3.;
  res=saturate(abs(res)-1.);
  res=(res-1.)*s+1.;
  res*=v;
  return res;
}

float n3d(vec3 p){
  vec3 i=floor(p);
  vec3 f=fract(p);
  vec3 b=vec3(13,193,9);
  vec4 h=vec4(0,b.yz,b.y+b.z)+dot(i,b);
  f=f*f*(3.-2.*f);
  h=mix(hash(h),hash(h+b.x),f.x);
  h.xy=mix(h.xz,h.yw,f.y);
  return mix(h.x,h.y,f.z);
}

float fbm(vec3 p){
  float ac=0.,a=1.;
  for(int i=0;i<5;i++){
    ac+=n3d(p*a)/a;
    a*=2.;
  }
  return ac-.5;
}

float density(vec3 p){
  return saturate(fbm(p*.5)-p.y*.03-.7);
}

#define odd(x) step(1.,mod(x,2.))
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(gl_FragCoord.x / iResolution.x, gl_FragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1)*.5;
  vec3 col=vec3(0);

  float cam=odd(time*.2);
  float L=4.+odd(time*.4-1.)*4.;
  vec3 ro=vec3(0,0,time);
  ro.xy=mix(vec2(0,L),vec2(L*.5,0),cam);
  vec3 rd=normalize(vec3(uv,-2));
  rd=mix(vec3(-rd.x,rd.z,rd.y),vec3(rd.z,rd.y,-rd.x),cam);
  //rd=vec3(-rd.x,rd.z,rd.y);

  for(float i=0.;i<N;i++){
    float T=i/N+time*.1;
    float id=i/N+floor(T);
    vec3 ce=vec3(0,0,ro.z+fract(T)*14.-7.);
    mat2 M=rot(sin(T*50.+hash(id)*pi2));
    ce.xy+=hash(vec2(1.1,1.2)*id)*6.-3.;
    ce.xy+=sin(vec2(5,7)*time*.2+hash(id*1.3)*pi2);

    intersect(ro,rd,ce,M,id,1.);
    intersect(ro,rd,ce,M,id,-1.);
  }

  /*vec2 p=uv;

  p.x=abs(p.x)*.8;
  if(p.x>sin(p.y*50)*.025+.4+p.y*.4)return;
  if(p.y<0)p*=1.5;
  p.y=abs(p.y);
  if(length(p)>sin(atan(p.y,p.x)*2)+smoothstep(.1,0.,p.y)*.3)return;*/

  vec3 ld=normalize(vec3(-5,2,-2));

  if(rt<100.){
    float h=hash(rid);
    ruv.x=abs(ruv.x);
    float w=fbm(vec3(ruv,hash(rid*1.2)*500.));
    h+=fbm(vec3(ruv+w*5.,hash(rid*1.1)*500.))*.3;
    col+=hsv(h,.8,fract(h*5.+hash(rid*1.3)));
    col*=smoothstep(-1.,-.93,sin(atan(ruv.y,ruv.x)*40.));
    rn.x+=fbm(vec3(ruv*10.,hash(rid*1.3)*500.));
    rn=normalize(rn);                                       // Added.
    float diff=max(dot(ld,rn),0.);
    float spec=pow(max(dot(reflect(ld,rn),rd),0.),20.);
    float m=.6;
    float lp=5.;

    col*=diff*(1.-m)*lp+spec*m*lp+.3;
  }else{
    col+=vec3(.5,.6,.9)*.1;
    col=mix(col,vec3(1),pow(max(dot(ld,rd),0.),100.)*2.);
  }

  vec3 rp=ro;
  float tra=1.,rs=1.,t=0.,den,ac=0.;
  for(int i=0;i<20;i++){
    if(t>rt)break;
    den=density(rp+t*rd);
    ac+=tra*den;
    tra*=1.-den;
    if(tra<.001)break;
    t+=rs;
  }
  col+=ac;

  col=pow(col,vec3(1./2.2));

  fragColor = vec4(col,1);
}

#define fs(i) (fract(sin((i)*114.514)*1919.810))
#define lofi(i,j) (floor((i)/(j))*(j))

const float PI=acos(-1.);
const float TAU=PI*2.;

float time;
float seed;

float random(){
  seed=fs(seed);
  return seed;
}

mat3 orthBas(vec3 z){
  z=normalize(z);
  vec3 up=abs(z.y)>.99?vec3(0,0,1):vec3(0,1,0);
  vec3 x=normalize(cross(up,z));
  return mat3(x,cross(z,x),z);
}

mat2 r2d(float t){
  return mat2(cos(t),sin(t),-sin(t),cos(t));
}

vec3 sampleLambert(vec3 n){
  float phi16=TAU*random();
  float ct=sqrt(random());
  float st=sqrt(1.0-ct*ct);
  return orthBas(n)*vec3(
    cos(phi16)*st,
    sin(phi16)*st,
    ct
  );
}

vec4 ibox(vec3 ro,vec3 rd,vec3 s){
  vec3 src=ro/rd;
  vec3 dst=abs(s/rd);
  vec3 fv=-src-dst;
  vec3 bv=-src+dst;
  float f=max(max(fv.x,fv.y),fv.z);
  float b=min(min(bv.x,bv.y),bv.z);
  if(f<0.||b<f){return vec4(1E2);}
  vec3 n=-sign(rd)*step(fv.zxy,fv)*step(fv.yzx,fv);
  return vec4(n,f);
}

struct QTR{
  vec3 cell;
  vec3 size;
  float len;
  bool hole;
};

QTR qt(vec3 ro,vec3 rd){
  QTR r;
  r.size=vec3(1,1E3,1);
  for(int i=0;i<4;i++){
    r.size/=2.;
    r.cell=lofi(ro+rd*1E-2*r.size,r.size)+r.size/2.;
    float d1=fs(dot(vec3(.2,1.4,-2.),r.cell));
    r.hole=(
      r.cell.y>0.
      || d1>.8
    );
    if(r.hole){break;}
    float d2=fs(dot(vec3(4,5,6),r.cell));
    if(d2>.5){break;}
  }


  vec3 src=(ro-r.cell)/rd;
  vec3 dst=abs(r.size/2./rd);
  vec3 bv=-src+dst;
  float b=min(min(bv.x,bv.y),bv.z);
  r.len=b;

  return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  vec2 p=uv*2.-1.;
  p.x*=iResolution.x/iResolution.y;

  time=iTime;
  seed=texture(iChannel0,uv*8.).x;
  seed+=fract(time);

  vec3 col=vec3(0);
  vec3 colRem=vec3(1);

  p.xy*=r2d(.4);

  vec3 co=vec3(0,1,2);
  co.zx*=r2d(.2*time);
  vec3 ct=vec3(.2,-1,0);
  vec3 cz=normalize(co-ct);

  vec3 ro=co;
  vec3 ro0=ro;
  vec3 rd=orthBas(cz)*normalize(vec3(p,-2));
  vec3 rd0=rd;

  bool shouldInit=true;
  float samples=0.;

  for(int i=0;i<99;i++){
    if(shouldInit){
      shouldInit=false;
      ro=ro0;
      rd=rd0;
      colRem=vec3(1);
      samples++;
    }

    QTR qtr=qt(ro,rd);

    vec4 isect=vec4(1E2);
    vec3 off=vec3(0);
    if(!qtr.hole){
      off.y-=1.;
      float d1=fs(dot(qtr.cell,vec3(.2,.8,.6)));
      off.y-=sin(d1*6.+time);
      vec3 size=vec3(qtr.size/2.-.02);
      isect=ibox(ro-qtr.cell-off,rd,size);
    }

    if(isect.w<1E2){
      ro+=rd*qtr.len; // fuck fuck fuck fuck fuck fuck fuck fuck fuck fuck
      // intended: ro+=rd*isect.w;

      if((ro-off).y>-1.*qtr.size.x){
        col+=colRem*5.;
        colRem*=0.;
      }

      vec3 N=isect.xyz;
      colRem*=.3;

      rd=mix(
        sampleLambert(N),
        reflect(rd,N),
        .5
      );
    }else{
      ro+=rd*qtr.len;
    }

    if(colRem.x<.01){
      shouldInit=true;
    }
  }

  col/=samples;
  col*=1.0-length(p)*.3;
  col=vec3(
    smoothstep(.1,.9,col.x),
    smoothstep(.0,1.,col.y),
    smoothstep(-.1,1.1,col.z)
  );

  fragColor = vec4(col,1);
}

/*

    Isometric Grid Height Map
    -------------------------

    Rendering some overlapping hexagons in an isometric grid fashion 
    to produce a height map of extruded blocks. Isometric grid 
    renderings are certainly not new, but they're fun and simple to 
    code.

    I had a few versions of this lying around for ages, but wasn't 
    inspired to do anything with them until Bitless posted his really 
    cool "Cyberspace data warehouse" example. He put isometric height
    maps on the faces of isometric cubes, which the isometric design 
    crowd have probably done before, but it was new to me. For anyone 
    who hasn't seen it, the link is below.

    A lot of people are familiar with the isometric height map concept, 
    but for anyone who isn't, you render cubes (or an extruded version) 
    in back to front order in a diamond pattern, or corresponding 
    patterns to cover the amount of overlap. In this case, I'm rendering 
    seven cells in a hexagon pattern.

    Just to mix things up a little and break the visual monotony, I
    rendered columns of differing width and offset them a bit. Other 
    that that, there's not a lot to it.

    By the way, rendering in this way is fun, novel and fast, but there 
    are way better 3D methods that will achieve the same.  I've attached 
    a link to a  much simpler version of this for anyone interested in 
    the process, or code golfing an isometric heightmap, maze, etc. :)



    Inspired by the following:

    // Very cool, and unique.
    Cyberspace data warehouse - bitless
    https://www.shadertoy.com/view/NlK3Wt

    // Here's a more sophisticated example. It requires more
    // work, but is based on a similar concept.
    Isometric City 2.5D - knarkowicz
    https://www.shadertoy.com/view/MljXzz

    // A less interesting, but easier to understand, verion:
    Isometric Height Map - Shane
    https://www.shadertoy.com/view/flcSzX

*/

// Standard 2D rotation formula.
mat2 rot2(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }


// IQ's vec2 to float hash.
float hash21(vec2 p){  return fract(sin(dot(p, vec2(27.619, 57.583)))*43758.5453); }


// Using two hexagons to creat an extruded box of varying height.
// I hacked in some logic to make it happen, but there might be
// more efficient ways. Either way, it works, so it'll do.
float dist(vec2 p, float h, float sz){

    //return length(p);

     // The vertical height component of the hexagon. We're taking
    // a little off to get rid of artifacts.
    float szD = sz/.8660254 - .015;

    // Height factor. More height looks more interesting, but too much
    // sends the blocks out of the cell range and causes artifacts.
    const float hFact = .75;

    // Moving the blocks down a bit to maximize the range. I've hack a 
    // number in, but you could probably calculate something.
    p.y -= -.1;


    // I should be rendering three calculated polygons consisting of
    // the top and two sides, but that requires more effort and GPU
    // power, so I've merely merged a base hexagon and a hexagon on
    // top that moves in the Y direction according to the height,
    // which is good enough for this example.
    vec2 q = abs(p);
    float hxB = max(q.y*.8660254 + q.x*.5, q.x);

    q = abs(p - vec2(0, szD - h)*hFact);
    float hxH = max(q.y*.8660254 + q.x*.5, q.x);

    // If the block is less that the size of a hexagon, you need the
    // maximum overlay of the two. If it's above the height of the
    // base hexagon, fuse the two together... It took me while to 
    // figure that out, but it works.
    float d = max(hxB, hxH);
    if(h - szD<0.) d = min(hxB, hxH);
    return d;

}

// If you're rendering triangles, hexagons, isometric grids, etc, the number
// 1.732 (sqrt(3)) will come up all the time.
const vec2 s = vec2(1.7320508, 1);

// Hexagon grid.
vec4 getGrid(vec2 p){

    // Finding the local coordinates and ID of the nearest hexagon center.
    vec4 ip = floor(vec4(p/s, p/s + .5));
    vec4 q = p.xyxy - vec4(ip.xy + .5, ip.zw)*s.xyxy;
    return dot(q.xy, q.xy)<dot(q.zw, q.zw)? vec4(q.xy, ip.xy) : vec4(q.zw, ip.zw - .5);

}

float height(vec2 p){

    //float rnd = hash21(p);
    //return smoothstep(.5, .95, sin(rnd*6.2831 + iTime)*.5 + .5);
    float tm = mod(iTime/1.5, 6.2831);
    float h = dot(sin(p*.73/1.25 - cos(p.yx*.97/1.25 - tm)*2.3), vec2(.25)) + .5;
    p *= 2.;
    float h2 = dot(sin(p*.73/1.25 - cos(p.yx*.97/1.25 - tm*2.)*2.3), vec2(.25)) + .5;
    h = mix(h, h2, .333);
    return smoothstep(0., 1., h);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    // Aspect correct coordinates.
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;

    // Scale and smoothing factor.
    const float sc = 16.;
    float sf = sc/iResolution.y;


    // Scaling and translation.
    vec2 p = uv*sc + s*iTime/3.;
    // Including rotation for a side wall perpective.
    //vec2 p = rot2(-3.14159/2.)*uv*vec2(-1, 1)*sc + s.yx*iTime/4.;


    // Scene field calculations.

    // Light direction. Shining down and to the left.
    vec2 ld = normalize(s);



    // Rendering in a diamond grouping. Top first, the two below that, and the bottom.
    //vec2[4] cntr = vec2[4](vec2(0, .5), vec2(-.5, 0), vec2(.5, 0), vec2(0, -.5));


    // Rendering in a seven cell grouping, and taking rendering order in account:
    // Top cell first, then the two below, the one below those, two more below
    // that and the one on the bottom.
    vec2[7] cntr = vec2[7](vec2(0, .5), vec2(-.5, 0), vec2(.5, 0), vec2(0, -.5),
                           vec2(-.5, -.5), vec2(.5, -.5), vec2(0, -1));    

    float obj[7], objSh[7], side[7], side2[7], top[7], hgt[7];
    vec4[7] p4I;


    // The block width. The maximum is s.y/2. (.8660254) and will produce a packed 
    // grid with no gaps. However, due to overlap, the height needs to be reduced.
    float sz = .7;
    vec2 szOffs = sz/s.yx;

    // Object shadow.
    float objShad = 1e5;

    for(int i = min(0, iFrame); i<7; i++){

        // Obtain the hexagon and ID for this position. I could probably 
        // streamline the process, but this will do.
        vec4 p4 = getGrid(p + cntr[i]*s);
        // Random 2D offset.
        vec2 offs = vec2(hash21(p4.zw + .1), hash21(p4.zw + .2)) - .5;
        // Random size.
        sz = .7*(hash21(p4.zw + .3)*.35 + .65);
        szOffs = sz/s.yx; 
        // Position based height. 
        float h = height(p4.zw*s);
        vec2 q = p4.xy - cntr[i]*s - offs*vec2(.2, .2); 

        // The extruded column object, which is just two overlayed 
        // hexagons in disguise.
        obj[i] = dist(q, h, sz) - sz;

        /*
        // Failed experiment with different tops.
        float bx = -1e5;
        if(hash21(p4.zw + .23)<.5){
           bx = max(abs(q.x) - sz, 
                    abs(q.y - (sz/.8660254 - .015 - h)*.75 - .15) - sz*.5);
        }
        */

        // Using the column object above and some simple CSG to construt 
        // the top and sides.
        float sd1 = dist(q - vec2(-1)*szOffs, h, sz) - sz;
        float sd2 = dist(q - vec2(1, -1)*szOffs, h, sz) - sz;
        side[i] = max(obj[i], sd1);
        side2[i] = max(obj[i], sd2);
        top[i] = max(obj[i], -min(sd1, sd2));

        // Failed experiment with different tops.
        //top[i] = max(top[i] - .05, bx);  

        // The shadow of the column object.
        objSh[i] = dist(q - ld*.5*sz, h, sz) - sz;

        // Saving the height, and hexagon cell information.
        hgt[i] = h;
        p4I[i] = p4;

        // The shadow is laid down first on the background, so can be
        // calculated here.
        objShad = min(objShad,  objSh[i]);

    }




    // Initiate the scene color to the background.
    vec3 col = vec3(.3, .25, .2);


    // Rendering the object shadows onto the background. 
    col = mix(col, vec3(.05, .05, .1), (1. - smoothstep(0., sf*2., objShad))*.4);


    // Loop through all seven object cells and rendering the objects.
    for(int i = min(0, iFrame); i<7; i++){

        // Dark glow around the objects for more fake AO.
        col = mix(col, vec3(0), (1. - smoothstep(0., sf*8., obj[i]))*.35);

        // Unique position-based cell ID.
        vec2 id = p4I[i].zw;


        // Coloring -- Mildly inspired by the colors in Shadertoy's in-house
        // "Rock Tiles" texture.
        float rnd = hash21(id);
        float fn = dot(sin(id*s/3.5/1.5 - cos(id.yx*s/2.3/1.5)*2.), vec2(.25)) + .5;
        fn = smoothstep(.1, .9, fn);  
        vec3 oCol = .6 + .4*cos(6.2831*mix(fn, rnd, .65)/3. + vec3(0, 1, 2)/1.65);  
        oCol = mix(oCol, oCol.xzy, fn*(hash21(id + .3)*.25 + .25));
        if(hash21(id + .27)<.33) oCol = oCol*.6;
        if(hash21(id + .37)<.66) oCol = min(oCol*1.4, 1.);
        /*
        // Load in the "Rock Tiles" texture for a comparison.
        vec3 tx = texture(iChannel0, id*s/sc/1.5).xyz; tx *= tx;
        tx = smoothstep(-.05, .7, tx);
        vec3 oCol = tx*1.5;
        */

        // Using the height for some extra shadowing.
        float shad = 1. - hgt[i]*.5;


        // The top of the extruded box.
        col = mix(col, vec3(0), 1. - smoothstep(0., sf, top[i])); // Edge, or stroke.
        //col = mix(col, oCol*ao*2., 1. - smoothstep(0., sf, top[i] + .04)); 
        //col = mix(col, vec3(0), 1. - smoothstep(0., sf, top[i] + .1)); 
        col = mix(col, oCol*shad, 1. - smoothstep(0., sf, top[i] + .04)); // Main.
        // Render dark holes on random faces to break up the monotony.
        if(hash21(id + .34)<.5){
        //if(mod(p4I[i].z + p4I[i].w, 2.)<.5){ // Checkered option.
            col = mix(col, vec3(0), 1. - smoothstep(0., sf, top[i] + .22)); 
            col = mix(col, oCol*shad/3., 1. - smoothstep(0., sf, top[i] + .265)); 
        }         


        col = mix(col, vec3(0), 1. - smoothstep(0., sf, side[i])); // Edge, or stroke.
        //col = mix(col, vec3(.25, .5, .75)*shad, 1. - smoothstep(0., sf, side[i] + .04));
        //col = mix(col, vec3(0), 1. - smoothstep(0., sf, side[i] + .1));      
        col = mix(col, oCol*vec3(.25, .5, .75)*shad, 1. - smoothstep(0., sf, side[i] + .04)); // Main.

        col = mix(col, vec3(0), 1. - smoothstep(0., sf, side2[i])); // Edge, or strke.
        //col = mix(col, vec3(2, .75, .5)*shad, 1. - smoothstep(0., sf, side2[i] + .04)); // Edge, or stroke.
        //col = mix(col, vec3(0), 1. - smoothstep(0., sf, side2[i] + .1)); // Edge, or strke.       
        col = mix(col, oCol*vec3(2, .75, .5)*shad, 1. - smoothstep(0., sf, side2[i] + .04)); // Main.


    }

    // Subtle vignette.
    uv = fragCoord/iResolution.xy;
    col *= pow(16.*uv.x*uv.y*(1. - uv.x)*(1. - uv.y) , .0625);


    // Rough gamma correction, and screen presentation.
    fragColor = vec4(sqrt(max(col, 0.)), 1);

}

#define deg (3.14159/180.)
mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

//rgb2hsv2rgb from https://stackoverflow.com/questions/15095909/from-rgb-to-hsv-in-opengl-glsl
// All components are in the range [0…1], including hue.
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

// All components are in the range [0…1], including hue.
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
// 2D Random
float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

// 2D Noise based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

//from https://www.shadertoy.com/view/ttBXRG
float staircase( in float x, in float k )
{
    float i = floor(x);
    float f = fract(x);

    float a = 0.5*pow(2.0*((f<0.5)?f:1.0-f), k);
    f = (f<0.5)?a:1.0-a;

    return i+f;
}


float bitm(vec2 uv,int c) {
    float h = 5.;
    float w = 3.;
    int p = int(pow(2.,(w)));
    float line1 = 9591.;
    uv = floor(vec2(uv.x*w,uv.y*h))/vec2(w,w);
    float c1 = 0.;
    float cc = uv.x + uv.y*w;
    c1 = mod( floor( float(c) / exp2(ceil(cc*w-0.6))) ,2.);
    c1 *= step(0.,uv.x)*step(0.,uv.y);
    c1 *= step(0.,(-uv.x+0.99))*step(0.,(-uv.y+1.6));
    return (c1);
}
