mat2 rot (float a) { return mat2(cos(a),-sin(a),sin(a),cos(a)); }

float map (vec3 p)
{
  float scene = 100.;
  float t = iTime/5.0;
  float falloff = 5.0;
  for (float index = 0.; index < 7.; ++index)
  {
    p.xz *= rot(t/falloff);
    p.yz *= rot(t/falloff);
    p = abs(p)-0.3*falloff;
    p *= 0.98;
    scene = min(scene, max(p.x, max(p.y, p.z)));
    falloff /= 1.85;
  }
  return -scene;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = 2.*(fragCoord.xy-0.5*iResolution.xy)/iResolution.y;
  vec3 pos = vec3(0);
  vec3 ray = normalize(vec3(uv * rot(iTime/5.0), 1));
  float shade = 0.;
  const float count = 20.;
  for (float index = count; index > 0.; index -= 1.0)
  {
    float dist = map(pos);
    if (dist < 0.001)
    {
      shade = index/count + 0.1;
      break;
    }
    pos += ray * dist;
  }
  //fragColor = vec4(1.0 - pow(shade, 0.9));
  fragColor = vec4(shade*0.7, shade*0.7, shade, 1.0);
}

