// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 64
#define MAX_DIST 64.
#define SURF_DIST .0001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)

#define ZERO (min(iFrame,0))

vec3 directions[5] = vec3[](vec3(0,-1.,0),vec3(1.,0,0),vec3(-1.,0,0),vec3(0,0,1.),vec3(0,0,-1.));

float B3D(vec3 p, vec3 s) {
    p = abs(p)-s;
    return max(max(p.x,p.y),p.z);
}

float childBlock(vec3 p){
    float childScale = 0.1;
    float d = B3D(p,vec3(childScale));
    childScale*=0.5;
    int index = 0;
    for(float i = 0.; i<5.0; i++){
        vec3 dir = (directions[index]*0.1);

        vec3 pos = p+(dir+(dir*0.5));

        float d2 = B3D(pos,vec3(childScale));
        d = min(d,d2);
        index++;
    }
    return d;
}

vec2 GetDist(vec3 p) {
    vec3 prevP = p;

    p*=3.0;
    float d = childBlock(p);
    float depth = 5.;
    int index = 0;
    for(float i = 0.; i<depth; i++){
        p*= 1.0+i*0.1;
        p*=matRotateX(radians(i*10.0+iTime*20.1))*matRotateZ(radians(i*20.0+iTime*32.2));
        p = abs(p);
        p-=0.2+i*0.01;
        float d2 = childBlock(p);
        d = min(d,d2);
        index++;
    }
    p = prevP;
    d = min(d,p.y+0.8);
    vec2 model = vec2(d*0.2,0);


    return model;
}

vec2 RayMarch(vec3 ro, vec3 rd, float side, int stepnum) {
    vec2 dO = vec2(0.0);

    for(int i=0; i<stepnum; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

// https://www.shadertoy.com/view/3lsSzf
float calcOcclusion( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<3; i++ )
    {
        float h = 0.01 + 0.15*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

vec3 diffuseMaterial(vec3 n, vec3 rd, vec3 p, vec3 col) {
    float occ = calcOcclusion(p,n);
    vec3 diffCol = vec3(0.0);
    vec3 lightDir = normalize(vec3(1,2,-2));
    float diff = clamp(dot(n,lightDir),0.0,1.0);
    float shadow = step(RayMarch(p+n*0.3,lightDir,1.0, 15).x,0.9);
    float skyDiff = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.0,1.0);
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);
    diffCol = col*vec3(-0.5)*diff*shadow*occ;
    diffCol += col*vec3(1.0,1.0,0.9)*skyDiff*occ;
    diffCol += col*vec3(0.3,0.3,0.3)*bounceDiff*occ;
    diffCol += col*pow(max(dot(rd, reflect(lightDir, n)), 0.0), 20.)*occ; // spec

    return diffCol;
}

float Hash21(vec2 p) {
    p = fract(p*vec2(234.56,789.34));
    p+=dot(p,p+34.56);
    return fract(p.x+p.y);
}

vec3 materials(int mat, vec3 n, vec3 rd, vec3 p, vec3 col){


    return diffuseMaterial(n,rd,p,0.5 + 0.5*sin(iTime+p.xyz+vec3(0,2,3))*cos(iTime+p.xyz+vec3(0,2,3)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 prevUV = uv;
    vec2 m =  iMouse.xy/iResolution.xy -.3;

    vec3 ro = vec3(0, 0, 1.0);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.yz *= Rot(radians(-30.0));
    if(iMouse.z>0.){
        ro.yz *= Rot(-m.y*3.14+1.);
        ro.xz *= Rot(-m.x*6.2831);
    } else {
        ro.xz *= Rot(radians(-15.0+iTime*20.0));
    }
    #endif

    vec3 rd = R(uv, ro, vec3(0,0.0,0), 1.0);
    vec2 d = RayMarch(ro, rd, 1.,MAX_STEPS);
    vec3 col = vec3(1.0);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
        int mat = int(d.y);
        col = materials(mat,n,rd,p,col);
        col *= exp( -0.038*d.x*d.x*d.x );//fog
    } else {
        col = vec3(0.0);
    }


    // gamma correction
    col = pow( col, vec3(0.9545) );    

    fragColor = vec4(col,1.0);
}

#define pi 3.14159

float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

const float num = 32.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    float r = 0.35 + 0.13 * thc(3.5, 8. * length(uv) - 1.5 * iTime);
    //float r = 0.35 + 0.13 * thc(3.5, iTime + 10. * h21(uv));
    vec2 p;
    float s = 0.;
    for (float i = 0.; i < num; i++) {
        p = vec2(r * sin(pi * i / num) * cos(i + iTime), -r + 2. * r * i / num);
        vec2 uv2 = uv;

        float d = 4. * length(uv2 - p);
        float R = r * sin(pi * i / num);
        float k = 0.1 + 0. * cos(10. * i + iTime) + 0.2 * R;
        s += smoothstep(-k, k, 0.5 * R * (1. + sin(i + iTime))-d);
        s += step(d, 0.4 * R * (1. + sin(i + iTime))) - step(d, 0.35 * R * (1. + sin(i + iTime)));
        s *= 0.99 * (1. - 0.22 * length(uv));
       // s += 0.28 * max(s,smoothstep(-0.2,0.5, 0.5 * R * (1. + sin(i + iTime))-d));
    }

    vec3 col = vec3(s);
    col = s * pal(s, vec3(1.), vec3(1.), vec3(1.), length(uv) + 0.35 * vec3(0.,0.33,0.66));
    col += vec3(0.025,0.,0.05);
   // Output to screen
    fragColor = vec4(col,1.0);
}

float random (vec2 st) {
  return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123);
}
