/**/ //[430] Ipafoto update with Fabrice's integrated loop and some math changing

#define M(a,b) min(abs(a-abs(b)),
void mainImage(out vec4 o, vec2 F)
{
    for(float k=2.,s=k,d=k,i=k,y;++i<5e2 && s>.01;) {
        vec3 f,z,p = vec3(d*F*k/iResolution.x,20)-d;
        y = p.y += 8.;
        p.xz*=mat2(cos(iTime/k-vec4(0,11,8,0)));
        f=floor(p/k);
        o=vec4(y>.1?p.z>k:int(f+f.z)%2!=0)/k+9./i;
        d+=s=min( ( min(z=abs(p),8.1)!=z?--o,k:z.z>6.?
                      z-=k,
                      M(28.,s=f.x*8.+y+k)
                      M(6.,s/k)
                      M(4.,.4-s)
                      M(s,4.)
                      -.4+M(y/k,.2)
                      k)))))
                      :M(y,1.)y/k-.2)
                ) + length(vec2(z.z-5.,--p-f*k))-.4, y)/6.;
        }
}



