// 'screenburn' - Del 04/12/2021

vec2 hash2( vec2 p )
{
    // procedural white noise	
	return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}
// Voronoi (IQ) - slightly modified to return get the ID etc.
vec4 VoronoiGrid( in vec2 x, out vec2 id )
{
    vec2 n = floor(x);
    vec2 f = fract(x);

    // first pass: regular voronoi
	vec2 mg, mr;

    float md = 8.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(float(i),float(j));
		vec2 o = hash2( n + g );
        vec2 r = g + o - f;
        float d = dot(r,r);
        if( d<md )
        {
            md = d;
            mr = r;
            mg = g;
        }
    }

    // second pass: distance to borders
    md = 8.0;
    for( int j=-2; j<=2; j++ )
    for( int i=-2; i<=2; i++ )
    {
        vec2 g = mg + vec2(float(i),float(j));
		vec2 o = hash2( n + g );
        vec2 r = g + o - f;

        if( dot(mr-r,mr-r)>0.00001 )
        md = min( md, dot( 0.5*(mr+r), normalize(r-mr) ) );
    }

    id = (n+mg)+vec2(0.5); // ID is n+mg
    return vec4(md, length(mr), mr);
}

vec2 smoothRot(vec2 p,float s,float m,float c,float d)
{
  s*=0.5;
  float k=length(p);
  float x=asin(sin(atan(p.x,p.y)*s)*(1.0-m))*k;
  float ds=k*s;
  float y=mix(ds,2.0*ds-sqrt(x*x+ds*ds),c);
  return vec2(x/s,y/s-d);
}

mat2 rotate(float a)
{
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
} 
#define PI 3.14159

float vDrop(vec2 uv,float t)
{
    if (iMouse.z>0.5)
        uv.y *= 12.0;
    else
        uv.y *= 2.;
    uv.x = uv.x*16.0;						// H-Count
    float dx = fract(uv.x);
    uv.x = floor(uv.x);
    uv.y *= 0.5;							// stretch
    float o=sin(uv.x*215.4);				// offset
    float s=cos(uv.x*33.1)*.3 +.7;			// speed
    float trail = mix(18.0,5.0,s);			// trail length
    //float trail = 5.0;
    float yv = fract(uv.y + t*s + o) * (trail*1.5);
    yv = 1.0/yv;
    yv = smoothstep(0.0,1.0,yv*yv);
    yv = sin(yv*PI)*(s*5.0);
    float d2 = sin(dx*PI);
    return yv*(d2*d2);
}

void mainImage(out vec4 k, vec2 p)
{
    vec2 uv = (p.xy - 0.5 * iResolution.xy) / iResolution.y;
    float dd0 = length(uv);
    float dd1 = smoothstep(0.0,0.3,dd0);
    uv *= rotate(fract(0.6+iTime*-0.01)*6.28);
    vec2 id;
    float scale = 64.0;
    vec4 grid = VoronoiGrid(uv*scale,id);
    uv = id/scale;

//    uv = smoothRot(uv,8.0,0.05,0.0,-0.1);
      uv = smoothRot(uv,4.0,0.35,16.0,0.05);

    uv *= 0.5;
    float drop = vDrop(uv.yx,iTime*0.5);
    vec3 linecol1 = vec3(0.185,0.395,0.5)*2.5;
    vec3 linecol2 = vec3(0.8,0.75,0.325)*1.5;

    vec3 linecol = mix(linecol1,linecol2,0.5+sin(iTime*0.2+dd0*0.7)*0.5);

    vec3 backcol = vec3(0.01,0.04,0.1);
    vec3 col = mix(backcol,linecol,drop)*dd1;
    k = vec4(col,1.0);
}

float rand(vec2 n) { 
return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
vec2 ip = floor(p);
vec2 u = fract(p);
u = u*u*(3.0-2.0*u);
float res = mix(
    mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
    mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
return res*res;
}


float wave(vec2 p, float i){
 float medium = noise((p + i * .77 ) * vec2(7. , .1)),
 small = noise((p + i * .33) * vec2(17.57 , .1)),
 large = noise((p + i * .56) * vec2(.052 , .5));
float offset = large - medium * .78 - small * .1;
float a = p.y + offset * .5;
a *= 1.5;
a -= .2;
float b = .7 - i * .05;
if(mod(i, 2.) > .5){
   a = 1. - a;
}
 return step(a,b);
}

