vec3 getNormal(vec3 p) {
	vec2 e = vec2(0.001, 0.0);
    return normalize( vec3(map(p+e.xyy)-map(p-e.xyy),
                          map(p+e.yxy)-map(p-e.yxy),
                          map(p+e.yyx)-map(p-e.yyx) ) );
}

float castRay(vec3 ro, vec3 rd) {
    float dO = 0.0;
    for (int i=0;i<100;i++)
    {
    	vec3 pos = ro +dO*rd;
        float dS = map(pos);
        if (dS < 0.001)
            break;
        dO += dS;
        if (dO > 20.0)
            break;
    }
    if (dO > 20.0 ) dO = -1.0;
    return dO;
}

