float Band(float t, float start, float end,float blur){
    float step1=smoothstep(start-blur,start+blur,t);
    float step2=smoothstep(end+blur,end-blur,t);

    return step1*step2;
}

float Rect(vec2 uv,float left,float right,float bottom,float top,float blur){
 	float band1=Band(uv.x,left,right,blur);
    float band2=Band(uv.y,bottom,top,blur);

    return band1*band2;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;

    vec3 col=vec3(0.);
    float TimeAdjustment=0.5;

    //Parameters Here
    float BandAmount=20.0;
    float BandOffset=1./BandAmount;
    float NoiseSpeed=0.5;
    float NoiseStrength1=50.0f;
    float NoiseStrength2=NoiseStrength1*0.1f;
    float NoiseTiling1=1000.0f;
    float NoiseTiling2=NoiseTiling1*0.1f;

    float NoiseWave1=noise(vec2(iTime*NoiseSpeed*NoiseTiling1,uv.y*0.01*NoiseTiling1))*NoiseStrength1;
    float NoiseWave2=noise(vec2(iTime*NoiseSpeed*NoiseTiling2,uv.y*0.01*NoiseTiling2))*NoiseStrength2;
    float NoiseWaveY=NoiseWave1*NoiseWave2/iResolution.y;
    uv.x+=NoiseWaveY;

    float Mask=0.0f;
    float OffsetMask1=0.0f;
    float OffsetMask2=0.0f;
    for(int i=0;i<int(BandAmount);i++){
        float TimeOffset=BandOffset*float(i);
        float BandWidth=0.2*pow(length((TimeOffset-0.5)),3.0);
        float FracTime=(fract(iTime*TimeAdjustment+TimeOffset)*2.-1.);    
        Mask+=Band(uv.x,-BandWidth+FracTime,BandWidth+FracTime,0.01);
        OffsetMask1+=Band(uv.x+0.01f,-BandWidth+FracTime+0.01,BandWidth+FracTime+0.02,0.01);
        OffsetMask2+=Band(uv.x-0.01f,-BandWidth+FracTime-0.02,BandWidth+FracTime-0.01,0.01);
    }



    //float mask=Band(uv.x,-BandWidth+FracTime,BandWidth+FracTime,0.01);

    //float mask=Rect(uv,-.1,.1,-.1,.1,.01);


    col=vec3(1.,0.,0.)*Mask+vec3(0.,1.,0.)*OffsetMask1+vec3(0.,0.,1.)*OffsetMask2;
    //col=vec3(uv.x,uv.y,0.0f);

    // Output to screen
    fragColor = vec4(col,1.0);
}

// based on a tutorial: https://www.youtube.com/watch?v=rvDo9LvfoVE

#define NUM_LAYERS 4.0
#define S(a, b, t) smoothstep(a, b, t)
#define sat(x) clamp(x, 0.0, 1.0)

mat2 Rot(float angle)
{
    float s = sin(angle);
    float c = cos(angle);

    return mat2(c, s, -s, c);
}

// Star and Starfield inspired by this tutorial https://www.shadertoy.com/view/tlyGW3
float Star(vec2 uv, float flare) {
    float dist = length(uv);
    float m = 0.05 / dist;

    float rays = max(0.0, 1.0 - abs(uv.x * uv.y * 1000.0));
    m += rays * flare;
  	uv *= Rot(3.1415/4.0);
    rays = max(0.0, 1.0 - abs(uv.x * uv.y * 1000.0));
    m += rays * 0.3 * flare;

    // smooth out the flare
    m *= smoothstep(1.0, 0.2, dist);
    return m;
}

float Hash21(vec2 p)
{
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 45.32);

    return fract(p.x*p.y);
}

vec3 StarLayer(vec2 uv)
{
    vec3 col = vec3(0.0);

    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);

    for(float y=-1.; y<=1.; y++) {
    	for(float x=-1.; x<=1.; x++) {

            vec2 offset = vec2(x,y);

            float rand = Hash21(id + offset); // random between 0 and 1
            float size = fract(rand * 345.32);
            float star = Star(gv - offset - vec2(rand, fract(rand*34.0)) + 0.5, smoothstep(0.9, 1.0, size));
        	vec3 color = sin(vec3(0.2, 0.3, 0.9) * fract(rand * 2565.2)*2.) * 0.5 + 0.5;
            star *= sin(iTime * 1.0 + rand * 6.2831) * 0.5 + 1.0;
            col += star * size * color;
        }
    } 
	return col;    
}

vec4 BlackHole(vec2 uv) {

    vec4 col = vec4(0.0);

    vec4 circle2 = vec4(0.0);

    float radius = 4.0;

    float d = length(uv);
    d /= 2.0;

    // Make the circle smooth
    col.rgb = mix(col.rgb, vec3(1.0), S(0.0, 1.0, d)); 

    // don't color the pixels that are outside of the circle
    // to make the 
    if(length(uv) > radius)
    {
        col.rgb = vec3(0.0);
    }

    return col;
}

