vec2 rot(vec2 a, float c){
float l = length(a);
a/=l;
float ang = (a.y<0.)?acos(a.x):2.*3.14159 - acos(a.x);
ang += c;
return vec2(l*cos(ang), l*sin(ang));
}

float DE(vec3 p0){
vec4 p = vec4(p0, 1.);
for(int i = 0; i < 20; i++){

p*=5.2 + sin(iTime)*1.4;
p.xyz = mod(p.xyz-1.5, 3.)-1.5;
p.xyz -= vec3(0.4,0.2,0.1)*0.5;
p/=dot(p.xyz,p.xyz);
}

return length(p.xyz)/p.w;
}

