#define res iResolution.xy
#define tau 6.283

//runs at about ~38 fps on my nvidia gt 610
//feel free to crank up these parameters if
//you have a more powerful gpu

//...which you probably do, since a low end gpu
//from 8 years ago isnt a very high bar...

//0 = cubic kifs
//1 = pow 8 mandelbulb
#define FRACT 0

//maximum number of light bounces (duh)
#define MAX_BOUNCES 4

//maximum raymarching steps
#define MAX_STEPS 128
//maximum for shadows
#define MAX_SHADOWSTEPS 256

//number of samples
#define NSAMPLES 1
//how much the renderer reuses previous frames
//the lower it is, the less of a trail there is behind the fractal
//but it also increases noise
//also it breaks if you set it to 0, use a small value like .1 instead.
#define REUSE_FACTOR .7

mat2 rotmat(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c,s,-s,c);
}

//sequence of quasirandom 2d points
vec2 quasirand(int i) { // i is supposed to be an int
    return fract(float(i) * vec2(1.618028, 1.324717));
}

//low-bias hash
//from https://nullprogram.com/blog/2018/07/31/
uint hash(uint x) {
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return x;
}

//hashes an uvec4 and returns a float that ranges from 0 to 1
float rand1u(uvec4 p) {
    return (float(hash(hash(hash(p.x) ^ p.y) ^ (hash(p.z) ^ p.w)) >> 8) + .5) / 16777216.;
}

//returns 4 random floats
vec4 rand4u(uvec4 p) {
    return vec4(
        rand1u(p+uvec2(0,0).xxxy),
        rand1u(p+uvec2(0,1).xxxy),
        rand1u(p+uvec2(0,2).xxxy),
        rand1u(p+uvec2(0,3).xxxy)
    );
}

//returns a point on the surface of a sphere
//it does this by generating 3 gaussian distributed
//floats using the box-mueller transform
//then normalizes the result
vec3 rand3s(uvec4 p) {
    vec4 tmp = rand4u(p);
    vec3 gauss = sin((tmp.xxz + vec2(0,.25).xyx) * tau) * sqrt(-log(tmp.yw)).xxy;
    return normalize(gauss.xyz);
}

//returns an orthogonal matrix where the first column is the normal
mat3 ortho(vec3 n) {
    vec3 bt = n.x>.7 ? vec3(0,1,0) : vec3(1,0,0);
    vec3 t = normalize(cross(bt, n));
    bt = cross(t, n);
    return mat3(n, t, bt);
}

