float sdf(vec3 p) {

    #if FRACT==0
    vec3 x = p;
    for(int i = 0; i < 8; i++) {
        x.xy *= rotmat(iTime*.3);
        x.xz *= rotmat(iTime*.3*1.618028);
        x.yz *= rotmat(iTime*.3*1.324717);
        x = abs(x);
        x -= 1.;
        x *= 2.;
    }
    return (length(x) - 3.) / pow(2., 8.);
    #endif

    #if FRACT==1
    //sort-of copied from https://www.shadertoy.com/view/ltfSWn
    //but mixed with spaghetti :D
    p.xz *= rotmat(iTime*.5);
    vec3 a = p;
    float d = 1.;
    for(int i = 0; i < 8; i++) {
        float r2 = dot(a,a); if(r2 > 256.) break;
        float r4 = r2*r2;
        float r8 = r4*r4;
        d *= 8. * sqrt(r2*r4*r8); d += 1.;
        vec3 a2 = a *a ;
        vec3 a4 = a2*a2;
        float k1 = a2.x + a2.z;
        float k2 = 1. / (k1*k1*k1*sqrt(k1));
        float k3 = a4.x + a4.y + a4.z - 6.*a2.x*a2.y + 2.*a2.x*a2.z - 6.*a2.y*a2.z;
        float k4 = a2.x + a2.z - a2.y;
        a = vec3( 64. * a.x*a.y*a.z * (a2.x-a2.z) * k4 * (a4.x - 6.*a2.x*a2.z + a4.z) * k3*k2
                , k3*k3 - 16.*a2.y*k1*k4*k4
                ,-8.*a.y*k4*(a4.x*a4.x - 28.*a4.x*a2.x*a2.z + 70.*a4.x*a4.z - 28.*a2.x*a4.z*a2.z + a4.z*a4.z) * k3*k2);
        a += p;
    }
    float t = dot(a,a);
    return .25 * log(t) * sqrt(t) / d;
    #endif

}

//tetrahedral numerical normals from
//https://iquilezles.org/articles/normalsSDF
vec3 sdf_normal(vec3 p) {
    const float h = 0.01;
    #define ZERO (min(iFrame,0))
    vec3 n = vec3(0.0);
    for(int i=ZERO; i<4; i++) {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*sdf(p+e*h);
    }
    return normalize(n);
}

//raymarching function
bool intersect(int maxsteps, vec3 ro, vec3 rd, out float ot) {
    float t = .01;
    for(int i = 0; i < maxsteps; i++) {
        float d = sdf(ro + rd*t);
        t += d;
        if(abs(d) < .001) {ot=t; return true;}
        if(d > 10.) break;
    }
    return false;
}

//point of no return :DDD
//the only thing missing in this function
//are meatballs
void mainImage(out vec4 col, in vec2 _pos) {
    col = vec4(0.);
    for(int s = 0; s < NSAMPLES; s++) {
        vec2 pos = ((_pos + quasirand(iFrame) - .5)*2. - res) / res.y;

        #if FRACT==0
        vec3 ro = vec3(0,0,-5);
        #endif
        #if FRACT==1
        vec3 ro = vec3(0,0,-2);
        #endif
        vec3 rd = normalize(vec3(pos,1));

        float importance = 1.;
        vec3 add[MAX_BOUNCES];

        int bounce = 0;
        for(bounce = 0; bounce < MAX_BOUNCES; bounce++) {
            uvec4 seed = uvec4(ivec2(_pos), iFrame, 0) + uint(bounce << 4);

            float t;
            if(!intersect(MAX_STEPS,ro,rd,t)) break;
            ro += rd*t;

            vec3 normal = sdf_normal(ro);

            vec3 shadowray;
            {
                float c = pow(rand1u(seed+2u), .5/5.);
                float s = sqrt(1.-c*c);
                float phi = rand1u(seed+3u) * tau;
                shadowray = ortho(vec3(0,1,0)) * vec3(c, s*sin(phi), s*cos(phi));
            }

            bool hit = intersect(MAX_SHADOWSTEPS, ro, shadowray, t);
            add[bounce] = hit ? vec3(0.) : vec3(dot(normal, shadowray));

            {
                float c = sqrt(sqrt(rand1u(seed+0u)));
                float s = sqrt(1.-c*c);
                float phi = rand1u(seed+1u) * tau;
                rd = ortho(normal) * vec3(c, s*sin(phi), s*cos(phi));
                importance /= c;
            }
        }

        vec3 tmp = vec3(0,.5,.7);
        tmp = rd.y>=0. ? mix(vec3(.3,.7,1), vec3(1), rd.y) : mix(vec3(0), vec3(.3,.7,1), rd.y+1.);
        for(int i = bounce-1; i >= 0; i--) {
            tmp *= .7;
            tmp += add[i];
        }

        col.rgb += tmp * importance;
        col.w += importance;
    }
}

