//---Caged Star
// by Catzpaw 2020

//BufferA:scene

#define ITER 120
#define NEAR .01
#define FAR 13.
#define EPS 1e-5

//OBJECTS
float cube(vec3 p){
    p=abs(p)-.1;
    return length(max(p,0.)+min(max(p.x,max(p.y,p.z)),0.));
}
float mandelbox(vec3 p){
    float s=2.,r=.7;
	vec3 o=p;
	for(int i=ZERO;i<8;i++){
		p=clamp(p,-1.008,1.008)*2.03-p; //boxfold
        float l=cube(p);
        if(l<.5){p*=2.1;r*=2.1;}else if(l<3.8){p/=l;r/=l;} //ballfold
        p=o+p*s;r=r*abs(s)+1.;
	}
	return .25-length(p)/abs(r);
}

//SCENE
float map(vec3 p){
    return min(mandelbox(p),length(p)-1.2);
}
// "Log-Bisection Tracing" by Nimitz
// https://www.shadertoy.com/view/4sSXzD
float trace(vec3 ro,vec3 rd){
    float t=NEAR,lt=t;
    vec3 p=ro+rd*t;
    float d=map(p);
    bool s=d>0.?true:false;
    bool b=false;
    for(int i=ZERO;i<ITER;i++){
        if(abs(d)<EPS*t||t>FAR)break;            
       	if((d>0.)!=s){b=true;break;}
        lt=t;
        t+=d>1.?d*.5:log(abs(d)+1.);
        p=ro+rd*t;
       	d=map(p);
    }
    if(b){
        float m=0.;
        p=ro+lt*rd;
        s=map(p)>0.?true:false;;
        for(int i=ZERO;i<6;i++){ 
            m=(lt+t)*.5;
            p=ro+rd*m;
            d=map(p);
            if(abs(d)<EPS*t)break;
            (d<0.)==s?t=m:lt=m;
        }
        t=(lt+t)*.5;
    }
    return t;
}

//MATERIALS & SHADING
vec3 normal(vec3 p){
    const vec2 d=vec2(1,-1);
    const float e=5e-3;
    return normalize(
        d.xyy*mandelbox(p+d.xyy*e)+
        d.yxy*mandelbox(p+d.yxy*e)+
        d.yyx*mandelbox(p+d.yyx*e)+
        d.xxx*mandelbox(p+d.xxx*e));
}
vec3 light(vec3 ld,vec3 rd,vec3 v,vec3 c,vec3 no){
    float ph=pow(max(dot(reflect(ld,no),-rd),0.),4.);
    return v*(c*pow(.5+dot(no,-ld)*.5,2.)+ph)+pow(ph,10.);
}
float reflection(vec3 ro,vec3 no,vec3 rd){
    vec3 rr=normalize(reflect(rd,no));
    float t=.92,n=1.;
    for(int i=ZERO;i<10;i++){float d=mandelbox(ro+rr*t);if(d<1e-3)break;t+=d;n+=1.;}
    return pow(.22-dot(normal(ro+rr*t),no)*(n*.078),4.);
}
float shadow(vec3 ro){
    vec3 rr=normalize(-ro);
    float t=2.2;
    for(int i=ZERO;i<10;i++){float d=mandelbox(ro+rr*t);if(d<1e-2)return .3;t+=d*2.5;}
    return 2.5;
}
float ao(vec3 p,vec3 no){
	float o=1.,s=1.;
    for(float i=1.;i<3.;i++){float r=.3*i;o+=(mandelbox(no*r+p)-r)*s;s*=.9;}
    return clamp(o,.2,1.);
}
vec4 material(in vec3 p,in vec3 rd){
    const vec3 ld=vec3(.577);
    float l=floor(length(p)*.411);
    vec3 c=l<1.?vec3(.85):vec3(sin(l)*.6,sin(l*.6)*.6,sin(l*4.4));
	vec3 no=normal(p);
    vec3 l1=light(ld,rd, vec3(.9,.95,1.),c,no);
    vec3 l2=light(-ld,rd,vec3(.7,.65,.6),c,no);
    vec3 l3=l<1.?c:light(normalize(p),rd,vec3(.6),c,no);
    vec3 l4=(l==1.)?vec3(reflection(p,no,rd)):vec3(0);
         l4=(l==3.)?vec3(reflection(p,no+sin(p*15.+iTime*10.)*sin(p*7.+iTime*8.),rd)):l4;
    float s=l>1.?shadow(p):4.;
    c=clamp(ao(p,no)*(l1+l2+l3*s+l4*s*.2)*.4,-1.,2.8); ////
    return vec4(c,pow(length(c+.1),2.+hash(p+iTime)*2.5));
}

//MAIN
void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec2 uv=(fragCoord-iResolution.xy*.5)/iResolution.y;
    vec3 ro=vec3(0,0,-3.8+clamp(sin(3.14+iTime*.1)*30.,-1.,1.));
    ro.xz*=rot(iTime*.2);
    ro.xy*=rot(iTime*.35);
	vec3 rd=vec3(uv.x,uv.y,.6);
    rd.xz*=rot(iTime*.2);
    rd.xy*=rot(iTime*.43);
    rd=normalize(rd);
	fragColor=material(ro+trace(ro,rd)*rd,rd);
}

