//---Scale Sequencer 2
// by Catzpaw 2020
// https://www.shadertoy.com/view/3tVGz3

//SETUP
const float base =432.;	//A tuning (Hz)
const float bpm  =98.;	//beats per minute
const float steps=240.;	//block length
const float start=1.;   //start count (beats)
const float fade =10.;  //fade out time (sec)
const float sec  =180.; //performance time (includes fade out time and start count)
const float gateseed=441.;
const float noteseed=849.;
const int chord3=4; //3rd tone(default:2)
const int chord5=7; //5th tone(default:4)

//SCALE
int key=2; //0:C 1:C+ 2:D 3:D+ 4:E 5:F 6:F+ ... 11:B
int scale=0;
const uint[12] scl=uint[](
    0xcb975420u, //0:major
    0xca875320u, //1:natural minor
    0xcb875320u, //2:harmonic minor
    0xcb975320u, //3:melodic minor
    0xa9765320u, //4:blues octatonic
    0xa9764310u, //5:blues diminished octatonic
    0xc9974420u, //6:major pentatonic (to heptatonic)
    0xca775530u, //7:minor pentatonic (to heptatonic)
    0xcb775540u, //8:ryukyu pentatonic (to heptatonic)
    0xcb755420u, //9:ryukyu hexatonic (to heptatonic)
    0xba875410u, //10:maqam hijaz octatonic
    0xba876320u);//11:gypsy minor octatonic
int scalenote(int y){
    return y<0?-999:key+((int(scl[scale])>>(y&7)*4)&15);
}

//PATTERN
// melodic:range(4),rest(4),seed1(12),seed2(12)
// percussive:note(2x16)
const uint[36] pat=uint[](
    0x37000000u,0x37011171u,0x47012172u,0x37011773u,  //00-   melody
    0x37054394u,0x37054c95u,0x44226166u,0x34617167u,
    0x34028168u,0x34019119u,0x4401a11au,0x3440417cu,  //  -0B
    0x44001434u,0x44001491u,0x44001022u,0x44001022u,  //0C-0F backing
    0x1162a000u,0x2162a803u,0x4162a802u,0x4162a801u,  //10-13 chord
    0x12118151u,0x1761a890u,0x17143327u,0x17957631u,  //14-17 bass
    0x3a3a3a3au,0x393a393au,0x39393939u,0x99999999u,  //18-1B sd+hh
    0x00000000u,0xb9b979b9u,0xb9b979b9u,0x55555555u,  //1C-1F bd+conga
    0x10000100u,0x10000100u,0x10000100u,0x10000100u); //20-23 chord

//SEQUENCE
// speed(4),degree1(4),pattern1(8),division(4),degree2(4),pattern2(8)
// speed    0:0.5x 1:1x 2:2x 3:4x
// degree   0:mute 1:I  2:II 3:III 4:IV 5:V 6:VI 7:VII
// division 0-f ... timing to switch pattern1 to pattern2 (0:only pattern2)
const uint[8] seq1=uint[]( //snare+hihat
    0x11000119u,0x11000119u,0x11000119u,0x1119711bu,
    0x11000119u,0x11000119u,0x11000119u,0x1119711au);
const uint[8] seq2=uint[]( //bassdrum+conga
    0x1100011eu,0x1100011eu,0x1100011eu,0x111e811fu,
    0x1100011eu,0x1100011eu,0x1100011eu,0x111ee11fu);
const uint[4] seq3=uint[]( //bass
    0x11000414u,0x11000214u,0x11000714u,0x11000114u);
const uint[4] seq4=uint[]( //arpeggio
    0x11000410u,0x11000410u,0x11000310u,0x11000110u);
const uint[4] seq5=uint[]( //backing
    0x1100010cu,0x1100010cu,0x1100040cu,0x150d810eu);
const uint[24] seq6=uint[]( //melody
    0x10000000u,0x10000000u,0x10000000u,0x10000000u,
    0x11000101u,0x11000102u,0x11000703u,0x11000104u,
    0x11000101u,0x11000102u,0x11000703u,0x11000105u,
    0x11000106u,0x11000107u,0x11000708u,0x11000109u,
    0x11000106u,0x11000107u,0x1100070au,0x1100010bu,
    0x11000702u,0x11000501u,0x11000203u,0x1104c000u);
const uint[4] seq7=uint[]( //chord
    0x11000520u,0x11000520u,0x11000420u,0x11000220u);

//BIT OPERATION
int imod(int x,int y){return int(x-y*(x/y));}
int imod(uint x,uint y){return int(x-y*(x/y));}
int get12(uint b,int l){return int(b>>l*4)&4095;}
int get8(uint b,int l){return int(b>>l*4)&255;}
int get4(uint b,int l){return int(b>>l*4)&15;}
int get2(uint b,int l){return int(b>>l*2)&3;}

//NES SOUND GENERATORS(without DPCM)
float osc_duty(float x,float d){return fract(x)<d?1.:-1.;}
//float osc_tri4(float x){return abs(floor(fract(x)*15.9999)/7.5-1.);}
const float[16] nt=float[](2.,4.,8.,16.,32.,48.,64.,80.,101.,127.,190.,254.,381.,508.,1017.,2034.);
float osc_nesnoise(float x,int i){return fract(sin(floor(x*1789772.5/nt[i])*179.2)*204.8);}

//WAVETABLE OSC(namco C30/N163)
//sound0:flute  ADFF DA75 4456 9ABB A852 0025
//sound1:brass  BFED ED93 0378 78CF C621 2104
//sound2:organ  87D7 F7F7 D7A7 8582 8080 8287
//sound3:piano  BEFF EBA9 9998 7666 6541 0014
//sound4:harpsi BDEF 000F EDB9 6421 0fff 0124
//sound5:bass   0033 2266 0033 CCFF 99DD CCFF
//sound6:octsqr 0000 00AA AAAA 5555 55FF FFFF
//sound7:octsaw 0123 4567 89AB 4567 89AB CDEF
/*
const uint[24] wt=uint[](
    0x000bb8bau,0x100de7fdu,0x203efdefu,0x303ff7dfu,0x4020efedu,0x5020b7dau,0x6a60af97u,0x7a6f9735u,
    0x8a0e9d04u,0x9a0d9734u,0xaa3b9a75u,0xba398786u,0x45c67879u,0x55c4658au,0x65f268cbu,0x75f162fbu,
    0x859068cau,0x959f5068u,0xafdf4825u,0xbfdf1012u,0xcfc00820u,0xdfc10210u,0xeff21802u,0xfff44745u);
float osc_c30(float x,int i){return(float((wt[int(mod(x*24.,24.))]>>(i*4))&15u)/15.-.5);}
*/

//AMP
//  vec3 p( drive, pressure, volume)
vec2 amp(vec2 i,vec3 p){return clamp(sign(i)*pow(abs(i*p.x),vec2(1./p.y)),-1.,1.)*p.z;}

//FOLDBACK DISTORTION
float fbdist(float v){return abs(v)>1.?sign(v)*2.-v:v;}

//FREQUENCY FROM MIDI NOTE NUMBER
float freq(float n){return pow(2.,(n-69.)/12.)*base;}

//ENVELOPE
float env_d(float x,float d){return max(0.,1.-x/max(d,1e-4));}

//FM INSTRUMENTS (freq,time)
float fmbass(float f,float x){f*=6.283;return fbdist(sin(f*2.03)*.5+sin(f*2.+sin(f+sin(f*7.51)*env_d(x,.07))*env_d(x,.2)))*env_d(x,.5);}
float fmharpsi(float f,float x){f*=6.283;return (sin(f+sin(f*5.+sin(f*9.))))*env_d(x,.7);}
float fmvibe(float f,float x){f*=6.283;return (sin(f+sin(f*9.)))*env_d(x,.9);}
float fmep(float f,float x){f*=6.283;return (sin(f+sin(f*15.)*env_d(x,.5)*.6))*env_d(x,.7);}
float fmmarimba(float f,float x){f*=6.283;return (sin(f+sin(f*3.99+sin(f*5.3)*env_d(x,.08))*env_d(x,.3)))*env_d(x,.4);}
float fmguitar(float f,float x){f*=6.283;return fbdist(sin(f+sin(f*3.01+sin(f*3.49)*env_d(x,.4))*env_d(x,.7)*.6)*1.3*env_d(x,.9));}
float fmpad(float f,float x){f*=6.283;return sin(f+sin(f*1.01))*(.9+sin(x*60.)*.1);}

//PERCUSSION INSTRUMENTS (time)
float bd(float x){return sin(x*freq(82./(1.+x*.7)))*env_d(x,.5);}
float sd(float x){return (osc_nesnoise(x,10)*.8*env_d(x,.2)+sin(x*freq(83./(1.+x*.05)))*env_d(x,.4));}
//float hh(float x,float d){return osc_nesnoise(x,3)*env_d(x,d);}

/*
//CHIPTUNE INSTRUMENTS (freq,time)
const float detune =.0025;
vec2 dutybass(float f,float x){return vec2(osc_duty(f*(1.+detune),.5),osc_duty(f*(1.-detune),.5))*env_d(x,.2);}
vec2 tri4bass(float f,float x){return vec2(osc_tri4(f*(.5+detune)),osc_tri4(f*(.5-detune)));}
vec2 tri4lead(float f,float x){return vec2(osc_tri4(f*(1.-detune)),osc_tri4(f*(1.+detune)));}
vec2 c30bass(float f,float x){return vec2(osc_c30(f*(1.+detune),5),osc_c30(f*(1.-detune),5))*env_d(x,.5);}
vec2 c30chord(float f,float x){return vec2(osc_c30(f*(1.-detune),7),osc_c30(f*(1.+detune),7));}
vec2 c30bell(float f,float x){return vec2(osc_c30(f*(1.-detune),1),osc_c30(f*(1.+detune),1))*env_d(x,.6);}
vec2 c30lead(float f,float x){return vec2(osc_c30(f*(1.-detune),4),osc_c30(f*(1.+detune),4))*env_d(x,.5);}
*/

//CHIPTUNE PERCUSSION (time)
//float nessd(float x){return osc_nesnoise(x,11)*env_d(x,.2);}
float neshh(float x,float d){return osc_nesnoise(x,0)*env_d(x,d);}
//float nesbd(float x){return osc_duty(2./(x*.5+.1),.5)*env_d(x,.1);}
float neslc(float x){return osc_duty(4./(x*.5+.1),.5)*env_d(x,.05);}
float neshc(float x){return osc_duty(5./(x*.5+.1),.5)*env_d(x,.05);}

//GET PATTERN FROM SEQUENCE
float mp=0.; //note length multiplier(from speed)
int   dg=0;  //degree now playing
uint  pt=0u;  //pattern now playing
int   st=0;  //step now playing
bool getpattern(float t,uint sq){
    // speed(4),degree1(4),pattern1(8),division(4),degree2(4),pattern2(8)
    mp=pow(2.,float(get4(sq,7)-1))*16.;
    st=int(mod(t*bpm*mp/steps,16.));
    bool di=int(mod(t*bpm*16./steps,16.))<get4(sq,3);
    dg=di?get4(sq,6):get4(sq,2);
    pt=di?pat[get8(sq,4)]:pat[get8(sq,0)];
    return dg==0?false:true;
}

//GET NOTE FROM PATTERN
int notegen(int s,int s1,int s2){
    return fract(sin(float(s+s1)+float(s1*20)*gateseed)*50.)<(float(get4(pt,6))*.0625)?-1:
    imod(int(abs(sin(float(s+s1+s*s2)*noteseed)*float(s2))),get4(pt,7));
}
vec4 getnote(float t,float l){
    // melodic:range(4),rest(4),seed1(12),seed2(12)
    int s1=get12(pt,3),s2=get12(pt,0),n=notegen(st,s1,s2);
    float x=mod(t,l/mp);
    if((st>=1)&&(n<0)){n=notegen(st-1,s1,s2);x+=l/mp;}
    n+=n<0?0:dg-1;
    return (dg<1)||(n<0)?vec4(x,-1,-1,-1):vec4(x,scalenote(n),scalenote(n+chord3),scalenote(n+chord5));
}
vec4 getgate(float t,float l){
    // percussive:note(2x16)
    int n=get2(pt,st);
    float x=mod(t,l/mp);
    if((st>=1)&&(n==0)){n=get2(pt,st-1);x+=l/mp;}
    return dg<1?vec4(x,-1,-1,-1):vec4(x,n,n,n);
}

//RENDERER
vec2 track1(float t,float l,int s){//TRACK 1 snaredrum,closehh,openhh
    vec2 o=vec2(0);
    getpattern(t,seq1[imod(s,8)]);
    vec4 n=getgate(t,l);
    o+=(n.y==1.)?sd(n.x)*.8:0.;
    o+=(n.y==2.)?neshh(n.x,.08)*.4:0.;
    o+=(n.y==3.)?neshh(n.x,.2)*.5:0.;
    return s>1?o:vec2(0);
}
vec2 track2(float t,float l,int s){//TRACK 2 bassdrum,lowconga,highconga
    vec2 o=vec2(0);
    getpattern(t,seq2[imod(s,8)]);
    vec4 n=getgate(t,l);
    o+=(n.y==1.)?bd(n.x)*.8:0.;
    o+=(n.y==2.)?neslc(n.x)*.3:0.;
    o+=(n.y==3.)?neshc(n.x)*.3:0.;
    return o;
}
float range(float n,float l){return n<=l?n:n-12.;}
vec2 track3(float t,float l,int s){//TRACK 3 bass
    vec2 o=vec2(0);
    getpattern(t,seq3[imod(s,4)]);
    vec4 n=getnote(t,l);
    o+=(n.y>=0.)?vec2(.8)*fmbass(freq(range(n.y+24.,36.))*n.x,n.x):vec2(0);
    return s>2?o*.8:vec2(0);
}
vec2 track4(float t,float l,int s){//TRACK 4 arpeggio
    vec2 o=vec2(0);
    getpattern(t,seq4[imod(s,4)]);
    vec4 n=getnote(t,l);
    int g=imod(1+imod(int(t*32./l),4),3);
    o+=(n.y>=0.)&&(g==0)?vec2(.8,.8)*fmvibe(freq(n.y+48.)*n.x,n.x):vec2(0);
    o+=(n.y>=0.)&&(g==1)?vec2(.6,1.)*fmvibe(freq(n.z+48.)*n.x,n.x):vec2(0);
    o+=(n.y>=0.)&&(g==2)?vec2(1.,.6)*fmvibe(freq(n.w+48.)*n.x,n.x):vec2(0);
    return s>2?o*.13:vec2(0);
}
vec2 track5(float t,float l,int s){//TRACK 5 backing
    vec2 o=vec2(0);
    getpattern(t,seq5[imod(s,4)]);
    vec4 n=getnote(t,l);
    o+=(n.y>=0.)?vec2(fmharpsi(freq(n.y+59.97)*n.x,n.x),fmharpsi(freq(n.y+60.03)*n.x,n.x)):vec2(0);
    return o*.17;
}
vec2 track6(float t,float l,int s){//TRACK 6 melody
    vec2 o=vec2(0);
    getpattern(t,seq6[imod(s,24)]);
    vec4 n=getnote(t,l);
    o+=(n.y>=0.)?vec2(fmguitar(freq(n.y+60.03)*n.x,n.x),fmguitar(freq(n.y+59.97)*n.x,n.x)):vec2(0);
    return o*.45;
}
vec2 track7(float t,float l,int s){//TRACK 4 chord
    vec2 o=vec2(0);
    getpattern(t,seq7[imod(s,4)]);
    vec4 n=getnote(t,l);
    o+=(n.y>=0.)?vec2(1.,.6)*fmpad(freq(n.y+48.)*n.x,n.x):vec2(0);
    o+=(n.y>=0.)?vec2(.8,.8)*fmpad(freq(n.z+48.)*n.x,n.x):vec2(0);
