#define DELAY_WET       0.20
#define DELAY_FEEDBACK  0.95
#define DELAY_TIME      0.05
float mvol=0.; //for indicator
vec2 mainSound( in int samp,float time){
    float l=steps/bpm,vol=1.,d=DELAY_WET,r=DELAY_TIME;
    vec2 o=vec2(0);
