    time-=start*60./bpm;
    if(time<0.)return o;
    render(time,l,o);
    for(int i=0;i<DELAY_REPEAT;i++){
        time-=r;
        if(time<0.)continue;
        vec2 a=vec2(0);
        render(time,l,a);
        d*=DELAY_FEEDBACK;
        r+=DELAY_TIME;
        o+=a*d;
    }
    mvol=vol;
    return amp(o,vec3(MASTER_DRIVE,MASTER_PRESSURE,vol));
}

