vec3 getRd(vec3 ro, vec3 lookAt, vec2 uv){
	vec3 dir = normalize(lookAt - ro);
	vec3 right = normalize(cross(vec3(0,1,0), dir));
	vec3 up = normalize(cross(dir, right));
	return normalize(dir + uv.x*right + uv.y*up);
}
vec3 glowA = vec3(0);
vec3 glowB = vec3(0);
vec3 glowC = vec3(0);
vec3 glowD = vec3(0);


#define ITERS 4
#define ZOOM (1. + my)
#define pmod(p, x) mod(p,x) - 0.5*x
float[] WS = float[ITERS](1.,1.,1.,1.);

float id;
float sd(vec3 q){
	#define repD 3.97
    id = floor(q.z/repD);
    q.z = pmod(q.z, repD);
    q.xy -= 2.;
    q.xy = pmod(q.xy, 4.);
    vec4 p = vec4(q.xyz, 1.);
    vec4 c = vec4(0.9,0.77,0.4,0.2);
    vec4 u = vec4(0.4,0.54,0.7,0.4);
    for(int i = 0; i < ITERS; i++){
        p.xyz = abs(p.xyz) - c.xyz;
        float dpp = dot(p.xyz,p.xyz);
        p=p*(1.5 + u)/clamp(dpp,.4,1.)- mix(p,c,0.9)*0.9;
        if(i==1)
			p.xy += 0.4;
        p.xy *= rot(-0.1 + sin(id)*0.9);

        p.xz *= rot(0.9);
        //if(i == 2)
        //	p.y += 0.9;
        WS[i] = p.w;
    }
    //w = p.z;
    p.xyz = abs(p.xyz);

    p.xz *= rot(0.2);
    float fr =  max(p.x - 0., max(p.y - 2.4 + sin(id)*0.7, p.z - 2.9))/p.w;
    float d = fr;

	return d*0.5;
}

vec2 map(vec3 p){
	vec2 d = vec2(10e5);

    d.x = min(d.x, sd(p));
    //d.x = min(d.x, length(p) - 0.6);
    return d;
}
float D;
vec2 march(vec3 ro, vec3 rd,inout vec3 p,inout float t,inout bool hit){
	vec2 d = vec2(10e5);
    hit = false;
    p = ro;
    t = 0.;
    for(int i = 0; i < 90; i++){
    	d = map(p);
        d.x *= D;
        glowA += exp(-d.x*20.)*WS[0];
        glowB += exp(-d.x*05.)*WS[1]*0.5;
        glowC += exp(-d.x*10.)*WS[2];
        glowD += exp(-d.x*50.)*WS[3]*0.7;
        if (d.x < 0.001){
        	hit = true;
            break;
        }
        t += d.x;
        p = ro + rd*t;
    }

    return d;
}

