//
// Crash and crumble
// by Timo Kinnunen 2020
//
// I attempted to model a collision with physically-based
// impact damage that changes the shape of the box object
// permanently, a short of half-way between softbody and rigid-body 
// simulation.
//
// The simulation keeps an extra set of vertices to record
// the shape of the object model, in addition to vertex positions at 
// the current and previous timesteps.
//
// On penetrating inside the rigid ground plane, instead of the full
// force being deflected at the vertex to bounce the object outside
// again, a portion of the force is used to nudge the corresponding
// model vertex instead. This allows the movement to continue while
// slowing down, leading to further penetrations and crumbling.
//
// The difference between the current model vertex position and
// the original analytical position of the vertex is used to offset
// the vertex position in subsequent simulation timesteps.
//
// The orientation of the box is not tracked. Instead the rotation
// angle is estimated from the average position of the current vertex
// and its 3 "diagonal" neighbours that correspond to
// 90/180/270 degree rotations.
//
//
// Fork of "Jello!" by qwertsol. https://shadertoy.com/view/ldKBWd
// 2020-01-31 11:47:47

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(vec3(0), 1);
    vec2 uv = fragCoord;

    fragColor = vec4(vec3(0.9 - distance(iResolution.xy / 2.0, uv) / iResolution.y / 10.0), 1.0);
    fragColor.rgb += hash(uvec3(fragCoord, 29.0)) / 50.0;

    fragCoord.x -= iResolution.x / 2.0;
	uv = fragCoord;

    float scale = iResolution.y / 500.0;

    vec4 color = texelFetch(iChannel0, ivec2(0, 1), 0);

    int j, c = 0;
  	for (int i=0, j=N*4-1; i<N*4; j=i, i+=1) 
    {
        vec2 a = (getBody(i, iChannel0).xy+vec2(0,10)) * scale;
        vec2 b = (getBody(j, iChannel0).xy+vec2(0,10)) * scale;

    	if (((a.y > uv.y) != (b.y > uv.y)) &&
     		(uv.x < (b.x-a.x) * (uv.y-a.y) / (b.y-a.y) + a.x))
      	 c++;
  	}

    bool isIn = (c % 2 == 1);

    float totalDist = 0.0;
    float closeDist = 9e10;
    vec4 colors = vec4(0);
    for (int i=0; i<4*N; i++) {
        vec2 p1 = getBody(i, iChannel0).xy+vec2(0,10);
        float sD = distance(fragCoord, p1.xy * scale);
        if(sD<closeDist){
        color = texelFetch(iChannel0, ivec2(i, 1), 0);
            closeDist=sD;
        }
        const float gain =50./800.0*2.0;
        colors = mix(texelFetch(iChannel0, ivec2(i, 1), 0),colors,fract(gain*sD*sD/(totalDist+gain*sD*sD)));
        totalDist = mix(sqrt(sD),totalDist,fract(gain*sD*sD/(totalDist+gain*sD*sD)));
    }
    if (isIn)
        fragColor = colors;


    vec2 p0 = getBody(0, iChannel0).xy+vec2(0,10);
    for (int i=1; i<4*N+1; i++)
    {
        vec2 p1 = getBody(i, iChannel0).xy+vec2(0,10);
        drawLine(fragCoord, p0.xy * scale, p1.xy * scale, 1.0, colors, fragColor);
        p0 = p1;
    }
}

