const vec2 g = vec2(0, -9.8) * 100.0;
const float td = 1.0 / 60.0; 
const float m = 0.1;
const float k = 5.0;
const float b = 0.02;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec3 random =  hash(uvec3(iFrame>>9, 13, 29));
    if ((iFrame&0x1FF) == 0 || texelFetch(iChannel1, ivec2(32, 0), 0).r > 0.0) 
    {
        vec2 start = vec2(0, 900);

        int i = int(fragCoord.x);
        if (i < 4*N && fragCoord.y < 3.0) 
        {
            vec2 coords = initBodyCoords(i);
            vec2 pos = rotate(coords * R - vec2(float(N/2)) * R, random.x * 100.0) + start;

            if (fragCoord.y > 2.0) {
                fragColor = vec4(coords, 0, 0);
            } else if (fragCoord.y > 1.0) {
                if (iFrame > 0)
                    fragColor = vec4(hue(random.y).rgb * .7, 1.0);
                else
                    fragColor = vec4(1, 0, 0, 1);
            } else {
                fragColor = vec4(pos, pos);
                float rand = random.y*2.0-1.0;
                fragColor.xz += sign(rand)*((0.5+0.5*abs(rand))*250.0+vec2(0,0.05/td));
            }
        }
    }
    else
    {
        vec2 params = (iMouse.x<10.0||iMouse.y<10.0) ? random.xy : clamp((iMouse.xy-30.0)/(iResolution.xy-50.0),0.,1.);
        fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
        int i = int(fragCoord.x);  
        if (i<4*N && fragCoord.y < 3.0)
        {
            vec4 p0 = getBody(i, iChannel0);
            vec2 pc0 = getBodyCoords(i, iChannel0);
            vec2 f = vec2(0);

            const int S = int(float(4*N) / 2.8);
            for (int j=-S; j<=S; j++)
            {
                if (j == 0)
                    continue;

                vec4 p1 = getBody(i+j, iChannel0);
                vec2 pc1 = getBodyCoords(i+j, iChannel0);
                float len = distance(pc0, pc1);
                float deformed = clamp(1./5.*(distance(initBodyCoords(i+j),pc1)+distance(initBodyCoords(i),pc0)),0.,1.);
                f += hookesLaw(p1-initBodyCoords(i+j).xyxy+pc1.xyxy, p0-initBodyCoords(i).xyxy+pc0.xyxy, len * R,
                               k+1.*k*deformed, b+2.*b*deformed, td);
            }

            if (p0.y < 1.0)
            {
                vec2 v = (p0.xy - p0.zw) / td;
                vec2 n = g*m + f; 
                if (n.y < 0.0)
                	f += -v.x * m * vec2(1, 0) * .02 * (-n.y);
            }

            vec2 newPos = p0.xy + (p0.xy - p0.zw) * .9999 + (g + f/m) * td * td;
            vec2 oldPos = p0.xy;
            vec2 displacement = vec2(0);
            if (newPos.y < 0.0) {
                if(abs(-p0.y * .3-newPos.y) > 2.5) {
                    displacement=vec2(0,-p0.y * .3-newPos.y);
                    vec2 cmc = 0.25*(pc0+getBodyCoords(i+N, iChannel0)+getBodyCoords(i+2+N, iChannel0)+getBodyCoords(i+3*N, iChannel0));
                    vec2 mc = 0.25*(oldPos+getBody(i+N, iChannel0).xy+getBody(i+2+N, iChannel0).xy+getBody(i+3*N, iChannel0).xy);
                    float srcAngle = atan(mc.y-p0.y,mc.x-p0.x);
                    float dstAngle = atan(cmc.y-pc0.y,cmc.x-pc0.x);
                    float srcLen = distance(mc,p0.xy);
                    float dstLen = distance(cmc,pc0);
                    float c = cos(dstAngle-srcAngle),s=sin(dstAngle-srcAngle);
                    mat2 rot=mat2(c,s,-s,c);
                    displacement = rot*displacement;
                    //displacement*=srcLen/dstLen;
                    displacement*=0.125;
                }
                newPos.y = -p0.y * .3;
            }
            if(fragCoord.y > 2.0) {
                fragColor.xy += (300./450.*8.0-4.0)*displacement;
            } else if(fragCoord.y > 1.0) {
                float oldDmg = 0.5+0.5*smoothstep(5.,0.5,distance(initBodyCoords(i),pc0));
                float newDmg = 0.5+0.5*smoothstep(5.,0.5,
                                                  distance(initBodyCoords(i),pc0+
                                                           (iMouse.y/iResolution.y*8.0-4.0)*displacement));

                fragColor.rgb *= clamp(newDmg/oldDmg,0.5,1.0);
            } else {
                fragColor.xy = newPos + ((450.-params.x*250.)/800.0*8.0-4.0)*displacement;
                fragColor.zw = oldPos + ((params.y*150.+150.)/450.0*8.0-4.0)*displacement;
            }
        }
    }
}

