#define normalize(x) normalize(x+1e-6) 

const int N = 20;
const float R = 10.0;

const uint hk = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*hk;
    x = ((x>>8U)^x.yzx)*hk;
    x = ((x>>8U)^x.yzx)*hk;

    return vec3(x)*(1.0/float(0xffffffffU));
}

vec4 hue( float x )
{
    return .6 + .6 * cos(6.3 * x  + vec4(0,23,21,0));
}

vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

float segmentDistance( vec2 p, vec2 a, vec2 b )
{
    vec2 pa = p - a, ba = b - a;
	float h = clamp(dot(pa,ba) / dot(ba,ba), 0., 1.);	
	return length(pa - ba * h);
}

void drawLine( vec2 coords, vec2 p0, vec2 p1, float thickness, vec4 color, inout vec4 outputColor )
{
    float sD = segmentDistance(coords, p0, p1);
	float a = 1.0 - clamp(sD - thickness / 2.0 + .5, 0.0, 1.0);

    outputColor = mix(outputColor, color, a * color.a);
}

void drawCircle( vec2 coords, vec2 center, float radius, vec4 color, inout vec4 outputColor )
{
    float sD = distance(coords, center);
	float a = 1.0 - clamp(sD - radius + .5, 0.0, 1.0);

    outputColor = mix(outputColor, color, a * color.a);
}

void drawSpring( vec2 coords, vec2 p0, vec2 p1, 
                 float thickness, int loops, vec4 color, inout vec4 outputColor )
{
    vec2 d = p1 - p0;
    if (length(d) < 0.001)
        return;

    vec2 dir = normalize(d);
    vec2 per = vec2(dir.y, -dir.x);

    vec2 st = d / float(loops * 2);
    vec2 last = p0 + per * thickness / 2.0 + st / 2.0;
    vec2 sw = -thickness * per;
    float th = 1.0;

    drawLine(coords, p0, last, th, color, outputColor);

    for (int i=0; i<loops*2-1; i++)
    {
        vec2 next = last + st + sw;
        sw = -sw;
        drawLine(coords, last, next, th, color, outputColor);
        last = next;
    }

    drawLine(coords, last, p1, th, color, outputColor);
}

vec2 hookesLaw( vec4 anchor, vec4 body, float len, float stiffness, float damping, float dt ) 
{
    vec2 dx = anchor.xy - body.xy;
    float x = len - length(dx);

    vec2 d = normalize(dx);
    vec2 v0 = (anchor.xy - anchor.zw) / dt;
    vec2 v1 = (body.xy - body.zw) / dt;
    vec2 dv = v0 - v1;
    float v = dot(dv, d);

    vec2 f = (-stiffness*x) * d;
    f += damping * v * d;
    return f;
}

vec2 getBodyCoords(int i, sampler2D iChannel0)
{
    i = (i % (4*N) + 4*N) % (4*N);

    return texelFetch(iChannel0, ivec2(i, 2), 0).xy;
}
vec2 initBodyCoords( int i )
{
    i = (i % (4*N) + 4*N) % (4*N);

    if (i<N)
        return vec2(i, 0);
    else if (i<2*N)
        return vec2(N, i-N);
    else if (i<3*N)
        return vec2(N-(i-2*N), N);
    else
        return vec2(0, N-(i-3*N));    
}

vec4 getBody( int i, sampler2D iChannel0 )
{   
    i = (i % (4*N) + 4*N) % (4*N);

    return texelFetch(iChannel0, ivec2(i, 0), 0);
}

