/// params:


int seed = 2111;
float sharpness = 1.1;
float movement = 1.0;
float curvyness = 0.0;//3.8;//1.5;
int num = 5;
int type = 2; // 0, random, 1, diamond, 2, triangle
float time = 0.0;
// Palette:
vec3 a = vec3(0.5, 0.5, 0.5);
vec3 b = vec3(0.5, 0.5, 0.5);
vec3 c = vec3(1.0, 1.0, 1.0);
vec3 d = vec3(0.00, 0.10, 0.20);

/////

//uint type[] = uint[](1u, 1u, 1u, 1u, 1u, 1u, 1u, 1u);
//vec2 off[3] = vec2[3](vec2(1.0, 0.0), vec2(1.0, sqrt(3.0)), vec2(-1.0, sqrt(3.0)));

float hash1( uint n ) 
{
    n += uint(seed);
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return float( n & uvec3(0x7fffffffU))/float(0x7fffffff);
}

float leftOf(vec2 a, vec2 b, vec2 p) {
	vec2 e0 = b - a;
	vec2 v0 = p - a;
	vec2 pq0 = v0 - e0*( dot(v0,e0)/dot(e0,e0) );
    vec2 d = vec2( dot( pq0, pq0 ), (v0.x*e0.y-v0.y*e0.x) );

    // sdf distance to line
    float dist = -sqrt(d.x)*sign(d.y);

    float edge = mix(0.2, 0.005, sharpness);
    return 2.0*smoothstep(-edge, edge, dist + movement*sin(6.28318*time/10.0 + curvyness*dot(p,p))) - 1.0;
}

vec4 palette( float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return vec4(a + b*cos( 6.28318*(c*t+d) ), 1.0);
}

vec4 color(float z) {
    return palette( z/float(num), a, b, c, d);
    //return palette( mod(z/float(num), 1.0), a, b, c, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
     time = iTime+0.1*uv.x;//+3.1*uv.y;
vec4 ui = texture(iChannel0, fragCoord/iResolution.xy);    
    uint size = uint(num);
    float c = 0.0;
    float d = 1.0;//0.25/float(size);
    for(uint i = 0u; i < size; ++i) {
        vec2 a = vec2(hash1(i), hash1(i+111u));
        float angle = 6.283*hash1(i+222u);
        float z = sign(mod(float(i), 2.0)-0.5);
        vec2 b = a;
        if(type == 0)
	        b += vec2(sin(angle), cos(angle));
        else if(type == 1)
        	b += vec2(z, sqrt(3.0));
        else if(type == 2)
        	b += vec2(z, ((i % 3u)==0u) ? 0.0 : sqrt(3.0));

        c += d*leftOf(a,b,uv);

        a.y = -a.y;
        b.y = -b.y;
        c += d*leftOf(b,a,uv);

        a.x = -a.x;
        b.x = -b.x;
        c += d*leftOf(a,b,uv);

        a.y = -a.y;
        b.y = -b.y;
        c += d*leftOf(b,a,uv);
    }

    // Output to screen
    fragColor = vec4(mix( color(c).rgb, ui.rgb, ui.a*0.8), 1.0);
}

