const float PI = 3.14159265358979;
vec2 cmpxcjg(in vec2 c) {
	return vec2(c.x, -c.y);
}

vec2 cmpxmul(in vec2 a, in vec2 b) {
	return vec2(a.x * b.x - a.y * b.y, a.y * b.x + a.x * b.y);
}

vec2 cmpxpow(in vec2 c, int p) {
    vec2 temp = vec2(1.0,0.0);
	for (int i = 0; i < p; ++i) {
		temp = cmpxmul(temp, c);
	}
    return temp;
}

vec2 cmpxdiv(in vec2 a, in vec2 b) {
    return cmpxmul(a, cmpxcjg(b));
}

float cmpxmag(in vec2 c) {
    return sqrt(c.x * c.x + c.y * c.y);
}

vec2 uv_scaled(vec2 uv, vec2 top_left, vec2 bot_right)
{
    return mix(top_left.xy,bot_right.xy,uv.xy);
}
float F (float x)
{
	return (cos(PI * (x+1.0))+1.0)/2.0;
}
float mandelbrot(vec2 c, int n)
{
    vec2 z = vec2(0,0);
    for (int i = 0; i < n;i++)
    {
        if (sqrt(z.x*z.x + z.y*z.y)  > 2.0)
        {
            return (1.0-float(i)/float(n));
		}
        z = cmpxmul(z,z) + c;

    }
    return 0.0;
}
vec2 f(vec2 c,vec2 z, float t)
{
    //the function used to define the sequence
	return cmpxpow(z,3) * t + cmpxpow(c,1) + t * z;
}
float fractal(vec2 c, int n, float t)
{
    vec2 z = vec2(0,0);
    for (int i = 0; i < n;i++)
    {
        if (sqrt(z.x*z.x + z.y*z.y)  > 2.0)
        {
            return (1.0-float(i)/float(n));
		}
        z = f(c,z,t);

    }
    return 0.0;
}
