float voronoi (vec2 uv){
    vec2 fuv = fract(uv) - 0.5;
    vec2 uvid = floor(uv);

    vec2 me = r22(uvid);
    vec2 u = r22(uvid + vec2(0,1)) + vec2(0,1);
    vec2 d = r22(uvid - vec2(0,1)) - vec2(0,1);
    vec2 l = r22(uvid - vec2(1,0)) - vec2(1,0);
    vec2 r = r22(uvid + vec2(1,0)) + vec2(1,0);

    vec2 ul = r22(uvid + vec2(1,-1))+ vec2(1,-1);
    vec2 ur = r22(uvid + vec2(1,1))+ vec2(1,1);
    vec2 dl = r22(uvid + vec2(-1,-1))+ vec2(-1,-1);
    vec2 dr = r22(uvid + vec2(-1,1)) + vec2(-1,1);

    vec3 col = vec3(0);


    float Di = distance(me,fuv);
    float Du = distance(u,fuv);
    float Dd = distance(d,fuv);
    float Dl = distance(l,fuv);
    float Dr = distance(r,fuv);

    float Dul = distance(ul,fuv);
    float Dur = distance(ur,fuv);
    float Ddl = distance(dl,fuv);
    float Ddr = distance(dr,fuv);


    float ddd = min(Di, Du);
    ddd = min(ddd, Dd);
    ddd = min(ddd, Dl);
    ddd = min(ddd, Dr);

    ddd = min(ddd, Dul);
    ddd = min(ddd, Dur);
    ddd = min(ddd, Ddl);
    ddd = min(ddd, Ddr);

    float c =  ddd;

    return  c;
}

