#define PI 3.14159265358979323846


vec2 rotate2D (vec2 _st, float _angle) {
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

vec2 tile (vec2 _st, float _zoom) {
    _st *= _zoom;
    return fract(_st);
}

vec2 rotateTilePattern(vec2 _st){

    //  Scale the coordinate system by 2x2
    _st *= 2.0;

    //  Give each cell an index number
    //  according to its position
    float index = 0.0;
    index += step(1., mod(_st.x,2.0));
    index += step(1., mod(_st.y,2.0))*2.0;

    //      |
    //  2   |   3
    //      |
    //--------------
    //      |
    //  0   |   1
    //      |

    // Make each cell between 0.0 - 1.0
    _st = fract(_st);

    // Rotate each cell according to the index
    if(index == 1.0){
        //  Rotate cell 1 by 90 degrees
        _st = rotate2D(_st,PI*0.5);
    } else if(index == 2.0){
        //  Rotate cell 2 by -90 degrees
        _st = rotate2D(_st,PI*-0.5);
    } else if(index == 3.0){
        //  Rotate cell 3 by 180 degrees
        _st = rotate2D(_st,PI);
    }

    return _st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    uv = tile(uv,6.0);
    uv = rotateTilePattern(uv);

    // Make more interesting combinations
    //uv *= tile(uv,2.0);
    //uv *= rotate2D(uv,-PI*sin(iTime));
    //uv = rotateTilePattern(uv*2.);
    uv *= rotate2D(uv,PI*iTime*0.25);

    // step(st.x,st.y) just makes a b&w triangles
    // but you can use whatever design you want.
    float f = step(uv.x,uv.y);
    fragColor = vec4(vec3(f*.5,f*cos(iTime), f*sin(iTime)),1.0);
    float gamma = 2.2;
    fragColor.rgb = pow(fragColor.rgb, vec3(1.0/gamma));
}

float smoothMin(float distA, float distB, float k){
	float h = max(k-abs(distA - distB),0.0)/k;
    return min(distA, distB) - h*h*h*k*1.0/6.0;
}

mat2 m2 = mat2(0.8,  0.6, -0.6,  0.8);
mat2 im2 = mat2(0.8,  -0.6, 0.6,  0.8);


float noise(in vec2 p){

    float res=0.;
    float f=1.;
	for( int i=0; i< 3; i++ ) 
	{		
        p=m2*p*f+.6;     
        f*=1.2;
        res+=sin(p.x+sin(2.*p.y));
	}        	
	return res/3.;
}

float fbmabs( vec2 p ) {

	float f=1.;   
	float r = 0.0;	
    for(int i = 0;i<8;i++){	
		r += abs(noise( p*f )+.5)/f;       
	    f *=2.;
        p=im2*p;    
	}
	return 1.-r*.5;
}
//courtesy of https://www.shadertoy.com/view/4l23Rh
vec3 sky(in vec2 p)
{	
return sin(vec3(1.7,1.5,1)+ .7+ .9*fbmabs(p*4.-.02*iTime))+.25;
}

vec2 r(vec2 ax, float a) {return mat2(cos(a),-sin(a),sin(a),cos(a))*ax;}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float checkers( in vec2 p )
{
    vec2 q = floor(p);
    return mod(q.x+q.y,2.);
}

vec2 map(vec3 pos)
{
    vec3 boxpos = pos;
    boxpos/=1.25;
    boxpos.xy = r(boxpos.xy,1.); 
    boxpos.xz -=.025*sin(pos.x*20.*(mod(sin(iTime/8.)*1.2,2.4)-1.2)*1.25);
    boxpos*=2.;
    float sphere2 = float(length(vec3(sin(iTime)*1.25,-.90-sin(iTime),cos(iTime)*1.25)-pos)-.35);
    float sphere3 = float(length(vec3(cos(iTime+1.43)*1.25,-.90-cos(iTime),sin(iTime)*1.25)-pos)-.35);
    float box = length(boxpos)-1.25;
    float plane = float(pos.y+1.5);

    vec2 scene = vec2(10.);

    //SDF+materials
    scene = vec2(smoothMin(scene.x,sphere2,0.4), scene.x<sphere2 ? scene.y:2.);
    scene = vec2(smoothMin(scene.x,box,0.4), scene.x<box ? scene.y:3.);
    scene = vec2(smoothMin(scene.x,sphere3,0.4), scene.x<sphere3 ? scene.y:4.);
    scene = vec2(smoothMin(scene.x,plane,.4),scene.x<plane ? scene.y:1.);
    return scene;
}

vec2 rm(vec3 ro, vec3 rd)
{
    vec2 dO = vec2(0.);

    for (int i = 0; i < 128; i++)
    {
    	vec2 t = map(ro + dO.x * rd);
        dO.x += t.x;
        dO.y = t.y;
        if(t.x < .01 || dO.x > 50.) break;
    }
    return dO;
}

vec3 n(vec3 pos)
{
    float d = map(pos).x;
    vec2 e = vec2(.001,.0);
    vec3 pn = d - vec3(
        map(pos-e.xyy).x,
        map(pos-e.yxy).x,
        map(pos-e.yyx).x
        );
	return normalize(pn);
}

vec3 l(vec3 pos)
{
    vec3 total = vec3(0.);
    vec3 nor = n(pos);
    vec3 sundir = normalize(vec3(6.0,5.,0.)); 
    vec3 sundif = clamp(dot(nor,sundir),.0,1.)*vec3(.67);  
    vec3 skydif = clamp(dot(nor,vec3(.0,1.,.0)),.0,1.)*vec3(.5,.0,.5);
    vec3 bounce = clamp(dot(nor,-sundir),.0,1.)*vec3(.15);  
    total+=sundif+bounce+skydif;  
    return total;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;   

    //bg
    vec3 col = vec3(.5,-.5+uv.y,uv.y+.75)*sky(vec2(uv.x/3.+iTime/14.,uv.y/3.));

