    vec3 ta = vec3(.0,.0,.0);

    vec3 ww = normalize(ta-ro);
    vec3 uu = normalize(cross(ww,vec3(.0,1.,.0)));
    vec3 vv = normalize(cross(uu,ww));

    vec3 rd = normalize(uv.x*uu+uv.y*vv+ww*1.5);
    vec2 r = rm(ro,rd);
    vec3 pos = ro+rd*r.x;

    if(r.x < 50.)
    {
        //floor
        if(r.y == 1.) 
        {            
            vec3 ro = pos+n(pos)*.02;
            vec3 rd = normalize(reflect(rd,n(pos)));
            vec2 r = rm(ro,rd);
            vec3 prf = ro+rd*r.x;
			float f = checkers(pos.xz);
            if(r.y == 2.) {
            col = l(pos)*vec3(.2)+fbmabs(pos.xy*10.)*vec3(.1,.1,2.9);   
                } 
            else if(r.y == 4.) {
            col = l(pos)*vec3(.2)+fbmabs(pos.xy*10.)*vec3(2.1,.1,.6);   
            } else {
				col=l(prf*vec3(f))*sky(vec2(iTime/4.+uv.x,uv.y))*vec3(.5,.0+uv.y,.5);
               }
        }

        //ballz
        if(r.y == 2.) {  
            col = l(pos)*vec3(.2)+fbmabs(pos.xy*10.)*vec3(.1,.1,2.9);   
        }
        if(r.y == 4.) {  
            col = l(pos)*vec3(.2)+fbmabs(pos.xy*10.)*vec3(2.1,.1,.6);   
        }

        //glass sphere
        if(r.y == 3.) 
        {
            col = vec3(.05,.06,.02);
            vec3 ro = pos+n(pos)*.02;
            vec3 rd = normalize(reflect(rd,n(pos)));
            vec2 r = rm(ro,rd);
            vec3 prf = ro+rd*r.x;  
               if(r.y == 2.) {
            col = l(pos)*vec3(2)+fbmabs(pos.xy*10.)*vec3(.1,.1,2.9);   
               } else if(r.y == 4.) {
	            col = l(pos)*vec3(.2)+fbmabs(pos.xy*10.)*vec3(2.1,.1,.6);   
