float	scene(vec3 p)
{
    up_cap = sdCapsule(vec3(p.y-2.4, p.z, p.x-.6), vec3(-.3, -.0, .33), vec3(.1, .0, -.31), .15);
    bt_cap = sdCapsule(vec3(p.y+2.4, p.z, p.x-.6), vec3(.3, -.0, .33), vec3(.1, .0, .0), .15);


    float banana = sdTorus(p, vec2(2.4, cos(-(p.y)/1.50)));
    if (p.x < -.45)
        banana = 2.;

    banana = min(banana, up_cap);
    banana = min(banana, bt_cap);

    return(banana);
}

vec4	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0);
    vec3	p = vec3(0.0);
    vec4	step = vec4(0.0);

    for (int i = -1; i < I_MAX; ++i)
    {
    	p = pos + dir * dist.y;
        dist.x = scene(p);
        dist.y += dist.x;
        if (dist.x < E || dist.y > 20.)
           break;
        step.x++;
    }
    step.y = dist.x;
    step.w = dist.y;
    return (step);
}


