#define PI 3.1415926

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

mat2 scale(vec2 _scale){
    return mat2(_scale.x,0.0,
                0.0,_scale.y);
}

vec2 tile(vec2 _st, float _zoom){
    _st *= _zoom;
    return fract(_st);
}

vec2 brickTile(vec2 _st, float _zoom){
    _st *= _zoom;

    // Here is where the offset is happening
    _st.x += step(1., mod(_st.y,2.0)) * 0.5 *iTime;

    return fract(_st);
}

vec2 brickTile2(vec2 _st, float _zoom){
    _st *= _zoom;

    // Here is where the offset is happening
    _st.y += step(1., mod(_st.x,2.0)) * 0.5 *iTime;

    return fract(_st);
}

vec2 movingTiles(vec2 _st, float _zoom, float _speed){
    _st *= _zoom;
    float time = iTime*_speed;
    if( fract(time)>0.5 ){
        if (fract( _st.y * 0.5) > 0.5){
            _st.x += fract(time)*2.0;
        } else {
            _st.x -= fract(time)*2.0;
        }
    } else {
        if (fract( _st.x * 0.5) > 0.5){
            _st.y += fract(time)*2.0;
        } else {
            _st.y -= fract(time)*2.0;
        }
    }
    return fract(_st);
}


float rectShape(vec2 position, vec2 scale){
    scale = vec2(0.5) - scale * 0.5;
    vec2 shaper = vec2(step(scale.x, position.x), step(scale.y, position.y));
    shaper *= vec2(step(scale.x, 1.0 - position.x), step(scale.y, 1.0 - position.y));
    return shaper.x * shaper.y;
}

float circleShape(vec2 position, float radius){
    return step(radius, length(position - vec2(0.5)));
}

float polygonShape(vec2 position, float radius, float sides){
    position = position * 2.0 - 1.0;
    float angle = atan(position.x, position.y);
    float slice = PI * 2.0 / sides;
    return step(radius, cos(floor(0.5 + angle / slice) * slice - angle) * length(position));
}


vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = fragCoord/iResolution.xy;
    vec2 position = fragCoord/iResolution.xy;
    // Divide the space in 4
    //uv = tile(uv,4.);
    //uv = brickTile2(uv, 4.);
    uv = movingTiles(uv,4.,0.5);

    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;




    //int tx = int(uv.x*512.0);
    int tx = 3;

	// first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
	float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 

    position -= vec2(0.5);
    // rotate the space
    position = rotate2d( sin(iTime)*PI*fft ) * uv;
    position = scale(vec2(0.8*sin(fft))) * position;
    // move it back to the original place
    position += vec2(0.5);



    vec3 color = vec3(0.0);

    // Use polar coordinates instead of cartesian
    vec2 toCenter = vec2(0.)-uv;
    float angle = atan(toCenter.y,toCenter.x);
    float radius = length(toCenter)*2.0;



    // Map the angle (-PI to PI) to the Hue (from 0 to 1)
    // and the Saturation to the radius
    color = hsb2rgb(vec3(radius-iTime,abs(sin(angle+fft*iTime)),1.));

    color -= vec3(0.,0.,rectShape(position,vec2(1.,abs(sin(fft))-0.5)));
    color -= vec3(rectShape(position,vec2(abs(sin(fft))-0.5,1.)),0.,0.);



    fragColor = vec4(color*1.5*cos(fft),1.0);
    //fragColor.rgb = pow(color, vec3(1./2.2));
    // apply gamma correction
    float gamma = 2.2;
    fragColor.rgb = pow(fragColor.rgb, vec3(1.0/gamma));
}

