    vec3 ap = p - a;

    float t = dot(ab, ap)/dot(ab, ab);
    t = clamp(t, 0., 1.);
    vec3 c = a + t * ab;

    return length(p - c) - r;
}
float sdCylinder(vec3 p, vec3 a, vec3 b, float r){
  	vec3 ab = b - a;
    vec3 ap = p - a;

    float t = dot(ab, ap)/dot(ab, ab);
    //t = clamp(t, 0., 1.);
    vec3 c = a + t * ab;

    float x = length(p - c) - r;
    float y = (abs(t- .5) - .5) * length(ab);
    float e = length(max(vec2(x,y), 0.));
    float i = min(max(x,y), 0.);

    return e + i ;
}
float sdTorus(vec3 p, vec2 r){
	float x = length(p.xz) - r.x;
    return length(vec2(x, p.y)) - r.y;
}
float sdBox(vec3 p, vec3 s){
    return length(max(abs(p) - s, 0.));
}

float GetDist(vec3 p){ //p is the sample point
    vec4 s = vec4(0, 1, 6, 1);
    float sphereDist = length(p - s.xyz) - s.w;
    float planeDist = p.y;
    float CapDist = sdCapsule(p, vec3(2, 1.5,5), vec3(3, 3,5), 0.3);
    float TurDist = sdTorus(p - vec3(-3, 0.5 , 5), vec2(1.5, 0.3));
    float BoxDist = sdBox(p - vec3(1.5,0.5 , 4), vec3(0.5));
    float CylDist = sdCylinder(p, vec3(2, 0.3,2), vec3(6, 0.3,5), 0.3);

    float d= min(sphereDist, planeDist); 
    d = min(CapDist, d);
    d = min(TurDist, d);
    d = min(BoxDist, d);
    d = min(CylDist, d);

    return d;
}

float RayMarch(vec3 ro, vec3 rd)
{
    float dO = 0.;

    for(int i = 0 ; i < MAX_STEPS; i++){
        vec3 p = ro + rd * dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO > MAX_DIST || dS < SURF_DIST) break;
    }
    return dO;
}



vec3 GetNormal(vec3 p){
	float d = GetDist(p);
    vec2 e = vec2(0.01, 0);
    vec3 n = d - vec3(GetDist(p - e.xyy),//same to vec3(d- d1, d- d2, d- d3);
                      GetDist(p - e.yxy),
                      GetDist(p - e.yyx));


    return normalize(n);
}

vec3 GetLight(vec3 p){
	vec3 lightPos = vec3(0, 5, 6);
    vec3 ligthCol = vec3(1, 1 ,1);

    float MoveRadius = 3.;
    float MoveSpeed = 2.;

    float t = iTime * MoveSpeed;
    lightPos.xz += vec2(sin(t), cos(t)) * MoveRadius;
    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);
    float NdotL = dot(n, l);
    //NdotL = NdotL*.5 + .5;
    float dif = clamp(NdotL, 0. , 1.);

    //shadow
    float d = RayMarch(p + n * SURF_DIST *2. , l);// 2. is fixed the center of the sphere sample
    if(d < length(lightPos - p))
    {
        dif *= 0.1;
    }
    return dif * ligthCol;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    //camera position
    vec3 ro = vec3(0,2,-3);

    vec3 rd = normalize(vec3(uv.x,uv.y, 1));

	float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;
    vec3 dif = GetLight(p);


    col = vec3(dif);





    fragColor = vec4(col,1.0);
}

