/*
This is a bare-bones MIDI:
Time(sec), MIDI(0-88), velocity(0-1), duration(sec)
*/
// piano
const float midi[4] = float[4](0.3,58.,0.5,2.);
float g1(float x, float a){
	return 0.25/(a-0.5)+0.5+sqrt(
        x*(-0.5/(a-0.5))+
        (-0.5+-0.25/(a-0.5))*(-0.5+-0.25/(a-0.5))
        );
}
float g2(float x,float a){
    return 0.25/(a-0.5)+0.5-sqrt(
        x*(-0.5/(a-0.5))+
        (-0.5+-0.25/(a-0.5))*(-0.5+-0.25/(a-0.5))
        );
}

float bez(float x,float a){
    if(a==0.5){return x;}
    float b=1.-a;
    if(a>0.5){
        float g=g2(x,a);
    	return 2.*g*b-2.*g*g*b+g*g;
    }
    float g=g1(x,a);
    return 2.*g*b-2.*g*g*b+g*g;
}

// Attack Decay Sustain Release
float ADSR(float time, float[3] A, float[3] D, float[3] S, float[3] R){
    float b=0.;
	if(time<A[0]){
    	return A[1]*bez((time)/A[0],A[2]);
    }
	b+=A[0];
    if(time<b+D[0]){
    	return (A[1]+(D[1]-A[1])*bez((time-b)/D[0],D[2]));
    }
	b+=D[0];
    if(time<b+S[0]){
    	return (D[1]+(S[1]-D[1])*bez((time-b)/S[0],S[2]));
    }
	b+=S[0];
    if(time<b+R[0]){
    	return (S[1]+(R[1]-S[1])*bez((time-b)/R[0],R[2]));
    }
    return 0.;
}

float notePitch(float mid){
    return (440./32.)*(pow(2.,(mid-9.)/12.));
}

float trumpetTone(float t,float hz){
    float time=t+sin(t*16.)*0.0001;
    float freq[8];
    freq[0]=hz;
    freq[1]=hz*2.;
    freq[2]=hz*3.;
    freq[3]=hz*4.;
    freq[4]=hz*5.;
    freq[5]=hz*6.;
    freq[6]=hz*7.;
    freq[7]=hz*8.;

    float mag[8];
    mag[0]=0.1155;
    mag[1]=0.3417;
    mag[2]=0.1789;
    mag[3]=0.1232;
    mag[4]=0.0678;
    mag[5]=0.0473;
    mag[6]=0.0260;
    mag[7]=0.0045;
    float val=0.;
    for(int i=0;i<8;i++){
    	val+=sin(freq[i]*time)*mag[i];
    }
    return val*(0.98+cos(time*30.)*0.02)*2.;
}

// Should make a piano tone
float trumpet(float time, float MIDI, float dur){
    float A[3]=float[3](0.005,0.7,1.);
    float D[3]=float[3](dur*0.55,1.,0.3);
    float S[3]=float[3](dur*0.5-0.06,0.5,0.8);
    float R[3]=float[3](0.05,0.,0.);
	return trumpetTone(time,6.2831853*notePitch(MIDI))*ADSR(time,A,D,S,R);
}

float getWave(in float time){
    float sum=0.;
    for(int i=0; i<1; i+=4){
        if(time>=midi[i]){
			sum+=trumpet(time-midi[i],midi[i+1],midi[i+3])*midi[i+2];
        }
        else if(time>midi[i]+midi[i+3]){
			i=10000;
        }
	}
    return sum;
}

float func(in float x){
    float val = -getWave(x*0.06+iTime);
	return val/2.1+0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iResolution.y<2000.) {
        float c=message((fragCoord/iResolution.y-vec2(.1,.2))*8.);
    	if(c>=0.){
            fragColor=vec4(c);return;
        }
    }

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    float max=1.;
    for(float i=-.005;i<=.005;i+=0.0002){
	    float val = func(uv.x+i);
        //float v = (abs(uv.y-val))*mag;
        float v=50.*sqrt((uv.y-val)*(uv.y-val)+10.*i*i);

        if(v<max){
            max=v;
			fragColor = vec4(1.-v,0.,v,1.);
		}
    }
    if(max==1.){
    	fragColor=vec4(0.,0.,0.2,1.);
    }
}

