// HSB to RGB
vec3 hsb2rgb(in vec3 c) {
    vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    rgb = rgb * rgb * (3.0 - 2.0 * rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

float plot(vec2 st, float pct){
  return  smoothstep( pct-0.02, pct, st.y) -
          smoothstep( pct, pct+0.02, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

        // the sound texture is 512x2
    int tx = int(uv.x*512.0)/10;
    //int tx = 1;

	// first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
	float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 


    // Calculate hue based on current horizontal pixel
    // and cursor position.
    float stepX = fft*9.0;
    float hue = floor(stepX * uv.x) / stepX;

    // Calculate saturation based on current vertical
    // pixel and cursor position.
    float stepY = fft*5.;
    float sat = 1.0 - floor(stepY * uv.y)/ stepY;

    // Generate HSB color.
    vec3 col = hsb2rgb(vec3(
        hue,
        sat,
        1.0
    ));
    float y = fft*cos(uv.x*50.*fft+iTime*50.)*0.5+0.5;

    vec3 color = vec3(y);
    float pct = plot(uv,y);
    color = pct*vec3(0.0,1.0,0.0);

    col -= color;


    // Output to screen
    fragColor = vec4(col,1.0);
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.34, 456.21));
    p += dot(p, p+45.32);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    vec2 uv2 = uv;

    uv2 *= 16.;

    vec2 gv = fract(uv2);
    vec2 id = floor(uv2);

    float r = Hash21(id);

    // Calculate the to center distance
    float d = length(uv - 0.5) * 2.0;

    // Calculate the ripple time
    float t = d * d * 25.0 - iTime * 4.0;

    // Calculate the ripple thickness
    d = (cos(t) * 0.5 + 0.5) * (1.0 - d);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5 * cos(t / 20.0 + uv.xyx + vec3(0.0,2.0,4.0));

    // Set the output color to rgb channels and the thickness to alpha channel
    // AO is automatically calculated
    fragColor = vec4(col, r+(sin(iTime*r)*.5));
}

