// Plento
vec2 R;

vec3 cm(vec3 p){p.yz *= rot(-0.6); return texture(iChannel2, p).xyz;}
vec3 t1(vec2 p){return texture(iChannel1, p).xyz;}

//  from iQ's website
vec2 opRevolution( in vec3 p, float w ){
    return vec2( length(p.xz) - w, p.y );
}

float sdBox2D( in vec2 p, in vec2 b ){
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

// the ring
float ring(vec3 rp){
    rp.xy *= rot(pi/2.);
    return sdBox2D(opRevolution(rp,180.0), vec2(0.1, 16.0));
}


float map(vec3 rp){
    float d = 999.0;

    vec3 p0 = rp - vec3(16.25, 0.25, 0.);
    vec3 p1 = rp - vec3(-16.25, 0.25, 0.);

    // main ring
    d = ring(rp);

	// outer walls
    p0.xy*=rot(pi/2.); p1.xy*=rot(pi/2.);
    d = min(d, sdBox2D(opRevolution(p0,180.0), vec2(.4, .25)));
    d = min(d, sdBox2D(opRevolution(p1,180.0), vec2(.4, .25)));

    return d;
}


vec3 normal( in vec3 pos ){
    vec2 e = vec2(0.002, -0.002);
    return normalize(
        e.xyy * map(pos + e.xyy) + 
        e.yyx * map(pos + e.yyx) + 
        e.yxy * map(pos + e.yxy) + 
        e.xxx * map(pos + e.xxx));
}

vec2 march(vec3 rd, vec3 ro){
    float t = 0., d = 0., md = 999.;

    for(int i = 0; i < 100; i++){
    	d = map(ro + rd*t); 	   
        md = min(d, md);

        if(abs(d) < 0.002){break;}
        if(t >= FAR){t = FAR; break;}

        t += d * 0.75;
    }   
    return vec2(t, md);
}

// Bump map stuff
float height(vec2 p){	
    return t1(p).r;
}

vec3 TextureBump(vec2 uv, float strength){
    float h = 0.002;
    vec3 p = vec3(0);

    p = t1(uv);

    float dx = height(uv) - height(uv + vec2(h, .0));
    float dy = height(uv) - height(uv + vec2(0., h));

    return normalize( vec3(0., 0., -1.) + vec3(dx, dy, 0.)*strength);
}

// inside or outside ring
float checkside(vec3 rd, vec3 ro){
    float d = 0., t = 0.;

    for(int i = 0; i < 10; i++){
    	d = ring(ro + rd*t); 	   
        if(abs(d) < .0001){break;}
        t += d;
    }   
    return t;
}

// Stars and dust
vec3 space(vec3 rd){
    float star = smoothstep(.55,.62, simplex3d(rd*150.));
    float dust = texture(iChannel0, vec3(rd.x, rd.y-.2, rd.z) + simplex3d(rd*10.)*.22 ).r;
    return mix(vec3(star), vec3(dust), .07);
}

// Object color
vec3 render(vec3 ro, vec3 rd, vec3 n, float t, float md){
    vec3 p = ro + rd*t;
    vec3 ref = reflect( rd, n );

    // Light direction for water (we need a real sun)
    vec3 ld = vec3(0., 7., 3.5);
    ld = max(normalize(ld), vec3(.001));

    vec3 col = vec3(0);

    if(t < FAR){
    	// uvs for ring
    	float theta = atan(p.y, p.z);
    	vec2 uv = vec2(p.x, theta * 100.) * .095;

    	// bumpy normals for water
    	vec3 bnw = TextureBump((3.*vec2(uv.x, uv.y*2. +iTime*.02) * .35), 5.);

    	// Water specular
    	float spec = pow(max(dot(reflect(-ld,normalize(n+bnw)), -rd ),.0),8.);

        // Diffuse walls
        float waldif = clamp(dot(n,ld), 0.17, 1.0 );

    	// noise values
    	float nse = 1.-exp(-octnse(vec2(0.4, 1.)*uv + vec2(0.6, 5.4), 7)); 
    	float nse2 = 1.-exp(-octnse(3.*vec2(uv.x, uv.y*2.), 4)); 

    	// land blend value
    	float ls = ss(.62, .62 + .004, nse); 

    	// textures
    	vec3 dirt = mix(vec3(0.4, 0.1, 0.)*.4, vec3(0.2, 0.2, 0.2), nse2);
    	vec3 grass = mix(vec3(0.4, 0.6, 0.), vec3(0.2, 0.2, 0.), nse2);
        vec3 water = mix(vec3(.0, .21, .6), cm(abs(rd) + bnw*.4), 0.23);
    	vec3 wall = vec3(0.8);

    	// dirt grass blend value
    	float ds = ss(.534, .534 + .1, nse);

    	// land color
    	vec3 land = mix(grass, dirt, ds);

    	// add specular to water
    	water += vec3(0.7, 0.5, 0.1) * spec*.75;

    	// main color for water, land and walls
    	col = mix(mix(land, water, ls), wall, step(16., abs(p.x)));

        // distance blueing
        col = mix( col, vec3(0.4, 0.6, 0.9), min(.7,1. - exp( -0.000001*t*t*t)));     
        float dom = ss(-0.1, 0.1, ref.y);
        col += .05*dom*vec3(0.4,0.6,1.3)*dom;

    	// ring side color
    	col = mix(col, wall*waldif, step(.9, abs(n.x)));

        // underside color
    	rd = normalize(-p);
    	float undr = checkside(rd, p);
        col = mix(wall*(.06+bnw.x*.6), col, step(.009, undr));

    }
    else
    	col = space(rd);


	return col;   
}

// Perhaps not the most optimal way to do camera animation but I'll keep it till I come up with a better way 
mat2x3 camera(vec2 uv, float t){
    vec3 forward = vec3(0., 0., 1.);
    vec3 rd = normalize(vec3(uv, .75));

    vec3 pos = cs1;
    vec3 lookAt = vec3(0);

    // v = 0-1 smoothstep based on time, r = rotation animation based on v, z = fov animation based on v
    float v = 0., r = 0., z = 0.;

    t = mod(t, 18.);

    if(t < 1.){
     	v = ss(0., 1., t);
        z = mix(.05, .75, v);
        rd = normalize(vec3(uv, z));
    }
    if(t <= 5.){ 
     	pos = mix(mix(cs1, cs1, ss(0., 1., t)), cs2, ss(1., 5., t));
        // simple pointing camera towards origin. Only works for xz rotations lol
        lookAt = normalize(-pos);
        float ct = dot(forward, lookAt);
        float aX = acos(ct);
        rd.xz *= rot(aX * sign(pos.x));
    }
    if(t >= 5. ){ 
     	pos = mix(cs3, cs4, ss(5., 7., t));
        rd.yz *= rot(-.4);
    }
    if(t >= 7. ){ 
        v = ss(7., 9., t); // Interp value
        z = mix(.75, .55, v); // new fov
     	pos = mix(cs4, cs5, v); // new position

        rd = normalize(vec3(uv, z)); // set fov
        rd.yz *= rot(-.4); // do rotations
        rd.yz *= rot(-v*.2);
    }
	if(t >= 9. ){ 
        v = ss(9., 11., t);
        r = mix(0., 1.17, v);

        rd.yz *= rot(-r);
        rd.xz *= rot(-r*1.34);
    }
	if(t >= 11. ){ 
        v = ss(11., 13., t);
        r = mix(0., 1.17, v);

        rd.xz *= rot(-r*1.34);
        rd.yz *= rot(-r*1.2);
    }
    if(t >= 14. ){ 
        v = ss(14., 20., t);

        rd = normalize(vec3(uv, 1.));
        rd.xz *= rot(-1.1);
        pos = mix(cs6, cs7, v);
    }

	return mat2x3 (pos, rd);
}

