    mat2x3 cam = camera(uv, iTime);

    vec3 ro = cam[0];
    vec3 rd = cam[1];

    // March stuff
    vec2 t = march(rd, ro);
    vec3 n = normal(ro + rd*t.x);

    // Object color
    col = render(ro, rd, n, t.x, t.y);

    // cloud noise and color
    float clouds = 1.-exp(-octnse3D(3.*vec3(rd.x, rd.y, rd.z-time*.16), 4));
    vec3 blue = mix(vec3(0., 0., 0.9), vec3(0.3, 0.3, 0.9), step(6., time)-step(14., time));
    vec3 skycol = mix(blue, vec3(1), clouds);

    // Change amout of glow depending on x position which gives transition from space to sky
    float dcs = mix(20., ro.x, step(4., time));
    float gval = mix(1., .00001, ss(30., 0., abs(dcs)));
    float glow = exp(-t.y*gval);


    vec3 closecol = mix(skycol, col, exp(-.006*t.x)); // clouds + geometry
    vec3 farcol = vec3(0);

    // add glow if miss object
    if(t.y > .01)farcol = mix(col, skycol, exp(-t.y*.23));else farcol = col;

    // Determine final color based on where we are 
    col = mix(closecol, farcol, ss(0., 10., abs(dcs)));

    // Do fades
    col *= fade(time, 0., 8.);
    col *= fade(time, 5., 8.);
    col *= fade(time, 14., 8.);
    col *= fade(time, 18., 6.);

    //col = pow(col,vec3(1./2.2));

    f = vec4(col, 1.0);

}

