// Lots of noise 

vec2 hash22( vec2 x ){
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

// Gradient noise
float gdns(vec2 p ){
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = ss(0., 1., f);

    vec2 a = hash22(i);
    vec2 b = hash22( i + vec2(1.,0.));
    vec2 c = hash22( i + vec2(.0,1.));
    vec2 d = hash22( i + vec2(1));

    float nse = mix( mix( dot( a, f - vec2(0.0,0.0) ), 
                     dot( b, f - vec2(1.0,0.0) ), u.x),
                mix( dot( c, f - vec2(0.0,1.0) ), 
                     dot( d, f - vec2(1) ), u.x), u.y);
    return nse + 0.5;
}


// 3D simplex noise stuff from: https://www.shadertoy.com/view/XsX3zB
const float F3 =  .3333333;
const float G3 =  .1666667;
vec3 random3(vec3 c) {
	float j = 4096.*sin(dot(c,vec3(17., 59.4, 15.)));
	vec3 r;
	r.z = fract(512.*j);
	j *= .125;
	r.x = fract(512.*j);
	j *= .125;
	r.y = fract(512.*j);
	return r-.5;
}

float simplex3d(vec3 p) {
	 vec3 s = floor(p + dot(p, vec3(F3)));
	 vec3 x = p - s + dot(s, vec3(G3));
	 vec3 e = step(vec3(0.), x - x.yzx);
	 vec3 i1 = e*(1. - e.zxy);
	 vec3 i2 = 1. - e.zxy*(1. - e);
	 vec3 x1 = x - i1 + G3;
	 vec3 x2 = x - i2 + 2.*G3;
	 vec3 x3 = x - 1. + 3.*G3;
	 vec4 w, d;
	 w.x = dot(x, x);
	 w.y = dot(x1, x1);
	 w.z = dot(x2, x2);
	 w.w = dot(x3, x3);
	 w = max(.6 - w, 0.);
	 d.x = dot(random3(s), x);
	 d.y = dot(random3(s + i1), x1);
	 d.z = dot(random3(s + i2), x2);
	 d.w = dot(random3(s + 1.), x3);
	 w *= w;
	 w *= w;
	 d *= w;

     float nse = dot(d, vec4(52.));

	 return 1.-exp(-(nse+1.)*.5);

}

// octave noise for 2d and 3d. Coulda done with 1 but oh well
float octnse(vec2 p, int oct){
    float a = 1.;
    float n = 0.;

    for(int i = 0; i < oct; i++){
     	n += gdns(p) * a;	
        p*=2.;
        a *= .5;
    }

    return n;
}
float octnse3D(vec3 p, int oct){
    float a = 1.;
    float n = 0.;

    for(int i = 0; i < oct; i++){
     	n += simplex3d(p) * a;
        p*=2.;
        a *= .35;
    }

    return n;
}

