    float leftOctaSub = sdBox(p - vec3(octaOffset.x,octaOffset.y,0.0),boxSize,boxSize,boxSize);
    float rightOcta = sdOctahedron(p + vec3(octaOffset.x,octaOffset.y-1.,0.0), octaSize);
    float rightOctaSub = sdBox(p + vec3(octaOffset.x,octaOffset.y-1.,0.0),boxSize,boxSize,boxSize);

    leftOcta = sdSubstract(leftOcta,leftOctaSub);
