}

// estimate normal with nudging
vec3 estimateNormal( vec3 p ) {
    return normalize(vec3(
    	sdScene(p+xe) - sdScene(p-xe),
        sdScene(p+ye) - sdScene(p-ye),
        sdScene(p+ze) - sdScene(p-ze)
    ));
}

// ray marching algorithm
float getSDF( Ray r ) {
    float depth = MIN_DISTANCE;
    for (int i=0; i<MAX_MARCHING_STEP; i++) {
        float dist = sdScene( r.o + depth * r.d );

        if (dist < EPSILON) { // inside a scene
            return depth;
        }

        depth += dist; // circle marching!

        if (depth >= MAX_DISTANCE) {
            return MAX_DISTANCE;
        }
    }

    return MAX_DISTANCE;
}

vec3 ambientLight( vec3 ambientColor ) {
    return ambientColor;
}

vec3 phongLight( vec3 diffuseColor, vec3 specularColor, float shininess, Ray ray, float dist, Light light ) {
    vec3 p = ray.o + dist * ray.d;

    vec3 N = estimateNormal(p); // surface normal
    vec3 L = normalize(light.pos - p); // surface to light direction
    vec3 V = -ray.d; // surface to camera direction
    vec3 R = reflect(-L, N); // mirror of L by the axis N

    float dotLN = dot(L, N); // project light direction to surface normal
    float dotRV = dot(R, V); // project light reflection direction to direction to camera

    vec3 color = vec3(0.);
    if (dotLN > 0.) { // we can see the diffuse
        color += diffuseColor * dotLN;
    }
    if (dotRV > 0.) { // we can see the specular
        color += specularColor * pow(dotRV, shininess);
    }
    color *= light.color; // influence of color of the light
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // convert from fragCoord (0.0 -> iResolution.xy) to uv (0.0 -> 1.0)
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 circleMovement = vec3(0.0,3.0 + sin(iTime*.2)*4.,-4.0);

    // set camera
    Camera cam;
    cam.sensorSize = iResolution.xy; // set sensor size to the screenSize
    cam.focal = 200.; // set the focal length (distance between sensor (screen) to the aperture
    cam.lookAt = vec3(0., 0., 0.); // where to look at (this camera use two point system)
    cam.pos = circleMovement; // the position of the camera

    // get ray
    Ray ray = getRay(cam, uv); // get corresponding ray, given the uv

    // create 8 points of cube
    vec3 points[8];
    for (int i=0; i<2; i++) {
        for (int j=0; j<2; j++) {
            for (int k=0; k<2; k++) {
                points[i*4+j*2+k] = vec3(float(i) * 2. - 1., float(j) * 2. - 1., float(k) * 2. - 1.);
            }
        }
    }

    // render
    float dist = getSDF(ray);
    if (dist > MAX_DISTANCE - EPSILON) { // don't hit anything, set to background
        fragColor = vec4(0.);
        return; // or discard?
    }

    vec3 ambientColor = vec3(0.1, 0.1, 0.2);
    vec3 diffuseColor = vec3(0.7, 0.2, 0.2);
    vec3 specularColor = vec3(1.0, 1.0, 1.0);
    float shininess = 50.0;

    Light light1;
    light1.pos = vec3(-2,2,-3);
    light1.color = vec3(1., 0.5, 0.3);

    Light light2;
    light2.pos = vec3(2,2,3);
    light2.color = vec3(1., 0.8, 0.5);

    vec3 color = vec3(0.);
    color += ambientLight(ambientColor);
    color += phongLight(diffuseColor, specularColor, shininess, ray, dist, light1);
    color += phongLight(diffuseColor, specularColor, shininess, ray, dist, light2);
    fragColor = vec4(color, 1.);
}

