void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    uint size = uint(num);
    float c = 0.0;
    float d = 1.0;//0.25/float(size);
    for(uint i = 0u; i < size; ++i) {
        vec2 a = vec2(hash1(i), hash1(i+111u));

        float z = sign(mod(float(i), 2.0)-0.5);
        vec2 b = a + vec2(z, sqrt(3.0));
        c += d*leftOf(a,b,uv);

        a.y = -a.y;
        b.y = -b.y;
        c += d*leftOf(b,a,uv);

        a.x = -a.x;
        b.x = -b.x;
        c += d*leftOf(a,b,uv);

        a.y = -a.y;
        b.y = -b.y;
        c += d*leftOf(b,a,uv);
    }

    // Output to screen
    fragColor = color(c);
}

