        	//col += diff;
        	col += mix(vec3(diff),fres*lightCol + spec*lightCol, 0.2);
        	//col += 2.4;
        }
        if (d.y == 2.){    
            vec3 tex = tex3D(iChannel1,p*0.8, n);
			tex = pow(tex, vec3(0.4 ,0.8,0.5));
        	col += mix(diff*tex, 1.* vec3(spec)*lightCol, 0.7)*2.;
        }
        col += 0.08*lightCol;

        //col += max(dot(n, normalize(vec3(1,-2,-1))),0.)* lightCol*0.09;
        ///col += 1.;
        //col += 0.02;

    } else {

    }
    #define pal(a,b,c,d,e) (a + b*sin(c*d + e))


    col = max(col, 0.);
    //col += lightCol*(glow*0.04)*0.7;

    if (t > 20.){
    	p = normalize(ro + rd)*20.;
    }
                //col -= vec3(1.,1.9,1.5)*lightCol*(1. -glow*0.05)*0.1;

    float n = noise(p*0.9);
    vec3 c = col;
    col.r = c.b;
    col.b = c.r;

    //col = mix(col,vec3(0.6+ length(p) *0.09,0.24+ n * 0.02,0.08 )*4. , smoothstep(0.,1., clamp(t*0.03 - 0.2, 0., 1.) - n*0.0));
    col = mix(col,vec3(0.2 - length(p) *0.0002,0.24+ n * 0.02,0.38 )*4. , smoothstep(0.,1., clamp(t*0.03 - 0.2, 0., 1.) - n*0.0));
    //col = mix(col,pal(0.5, 0.5, vec3(2.5,0.3,0.6), 0.5 , 9. + p.z*0.2 ) , smoothstep(0., 1.,t*0.1));
    col.b *= 1.2;
            //col -= vec3(1.,1.9,3.9)*lightCol*(1. -glow*0.05)*0.1;
    col -= vec3(1.,1.9,3.9)*lightCol*(1. -glow*0.05)*0.03;


    col *= 1. - pow(length(uv)*0.8,1.1)*1.2;
    col = smoothstep(0.,0.9,col);
    col *= 1.1;
    col = pow(col, vec3(0.4545));
    col.r *= 1.5;
