ivec3 get_facet(in int f) {
    return ivec3(
        get_data(MESH_DATA_OFFSET,3*f),
        get_data(MESH_DATA_OFFSET,3*f+1),
        get_data(MESH_DATA_OFFSET,3*f+2)
    );
}

const float FARAWAY=1e30;

struct Camera {
    vec3 Obs;
    vec3 View;
    vec3 Up;
    vec3 Horiz;
    float H;
    float W;
    float z;
};

struct Ray {
    vec3 Origin;
    vec3 Dir;
};

Camera camera(in vec3 Obs, in vec3 LookAt, in float aperture) {
   Camera C;
   C.Obs = Obs;
   C.View = normalize(LookAt - Obs);
   C.Horiz = normalize(cross(vec3(0.0, 0.0, 1.0), C.View));
   C.Up = cross(C.View, C.Horiz);
   C.W = float(iResolution.x);
   C.H = float(iResolution.y);
   C.z = (C.H/2.0) / tan((aperture * 3.1415 / 180.0) / 2.0);
   return C;
}

Ray launch(in Camera C, in vec2 XY) {
   return Ray(
      C.Obs,
      C.Obs+C.z*C.View+(XY.x-C.W/2.0)*C.Horiz+(XY.y-C.H/2.0)*C.Up 
   );
}

struct Material {
   vec3 Ka;
   vec3 Kd;
   vec3 Ks;
   float s;
   vec3 Ke;
};

struct Intersection {
   float t;
   Material material;
   vec3 P;
   vec3 N;
};

Intersection intersection() {
   Intersection I;
   I.t = FARAWAY;
   return I;
}

struct Sphere {
   vec3 Center;
   float R;
};

bool intersect_sphere(in Ray R, in Sphere S, out float t) {
   vec3 CO = R.Origin - S.Center;
   float a = dot(R.Dir, R.Dir);
   float b = 2.0*dot(R.Dir, CO);
   float c = dot(CO, CO) - S.R*S.R;
   float delta = b*b - 4.0*a*c;
   if(delta < 0.0) {
      return false;
   }
   t = (-b-sqrt(delta)) / (2.0*a);
   return (t > 0.0);
}

void sphere(in Ray R, in Sphere S, in Material M, inout Intersection I) {
   float t;
   if(intersect_sphere(R, S, t) && t < I.t) {
      I.t = t;
      I.material = M;
      I.P = R.Origin + t*R.Dir;
      I.N = normalize(I.P - S.Center);
   }
}


bool intersect_triangle(
    in Ray R, in vec3 A, in vec3 B, in vec3 C, out float t, 
    out float u, out float v, out vec3 N
) { 
   // Mller et Trumbore,  Fast, Minimum Storage Ray-Triangle Intersection , 
   // Journal of Graphics Tools, vol. 2, 1997, p. 2128 
   // https://en.wikipedia.org/wiki/M%C3%B6ller%E2%80%93Trumbore_intersection_algorithm 
   // (with small adaptations: branchless, and reusing the normal vector)
   // 
   // Notation: O = R.Origin; D = R.Dir)
   //           E1 = B-A; E2 = C-A
   // 
   // (1) O + tD = A + uE1 + vE2 
   // (2) uE1 + vE2 -tD = O-A
   // 
   //                [u]
   // (3) [E1|E2|-D] [v] = O-A
   //                [t]
   // 
   //  (where [E1|E2|-D] is the 3x3 matrix with E1,E2,-D as its columns)
   //
   //  Using Cramer's formula for the solution of:
   //
   //    [a11 a12 a13][x1]   [b1]
   //    [a12 a22 a23][x2] = [b2]
   //    [a31 a32 a33][x3]   [b3]
   //
   //  gives: 
   // 
   //        |b1 a12 a13|   |a11 a12 a13|
   //   x1 = |b2 a22 a23| / |a21 a22 a23|
   //        |b3 a32 a33|   |a31 a32 a33|
   //
   //        |a11 b1 a13|   |a11 a12 a13|
   //   x2 = |a21 b2 a23| / |a21 a22 a23|
   //        |a31 b3 a33|   |a31 a32 a33|
   //
   //        |a11 a12 b1|   |a11 a12 a13|
   //   x3 = |a21 a22 b2| / |a21 a22 a23|
   //        |a31 a32 b3|   |a31 a32 a33|
   // 
   // Now we get:
   //
   //   u = (O-A,E2,-D) / (E1,E2,-D)
   //   v = (E1,O-A,-D) / (E1,E2,-D)
   //   t = (E1,E2,O-A) / (E1,E2,-D)
   // 
   // where (A,B,C) denotes the determinant of the 3x3 matrix with A,B,C as its
   //  column vectors.
   // 
   // Now we use the following identities:
   //   (A,B,C) = dot(A,cross(B,C))  (develop the determinant w.r.t. first column)
   //   (B,A,C) = -(A,B,C)           (swapping two vectors changes the sign)
   //   (B,C,A) =  (A,B,C)           (circular permutation does not change the sign)
   // 
   // Now we get:
   //
   // u = -(E2,O-A,D)  / (D,E1,E2)
   // v =  (E1,O-A,D)  / (D,E1,E2)
   // t = -(O-A,E1,E2) / (D,E1,E2)  
   //
   // Using N=cross(E1,E2); AO = O-A; DAO = cross(D,AO)
   //  gives the following code : 
   vec3 E1 = B-A;
   vec3 E2 = C-A;
         N = cross(E1,E2);
   float det = -dot(R.Dir, N);
   float invdet = 1.0/det;
   vec3 AO  = R.Origin - A;
   vec3 DAO = cross(AO, R.Dir);
   u =  dot(E2,DAO) * invdet;
   v = -dot(E1,DAO) * invdet;
   t =  dot(AO,N)  * invdet; 
   return (det >= 1e-6 && t >= 0.0 && u >= 0.0 && v >= 0.0 && (u+v) <= 1.0);
}

void triangle(in Ray R, vec3 A, vec3 B, vec3 C, in Material M, inout Intersection I) {
   float t,u,v;
   vec3 N;
   if(intersect_triangle(R, A,B,C, t, u, v, N) && t < I.t) {
      I.t = t;
      I.material = M;
      I.P = R.Origin + t*R.Dir;
      I.N = normalize(N);
   }
}

bool segment_box_intersection(
  in vec3 q1,
  in vec3 dirinv,
  in vec3 boxmin,
  in vec3 boxmax,
  in float t // t of current intersection, used for pruning, see iq's comment.
) {  
   // References: 
   //    https://tavianator.com/fast-branchless-raybounding-box-intersections/ 
   vec3 T1 = dirinv*(boxmin - q1);
   vec3 T2 = dirinv*(boxmax - q1);
   vec3 Tmin = min(T1,T2);
   vec3 Tmax = max(T1,T2);
   float tmin = max(max(Tmin.x, Tmin.y),Tmin.z);
   float tmax = min(min(Tmax.x, Tmax.y),Tmax.z);    
   return (tmax >= 0.0) && (tmin <= tmax) && (tmin <= t);
}

int stack_top = -1;
int stack_b[10];
int stack_e[10];

bool stack_empty() {
  return (stack_top < 0);
}

void stack_push(in int b, in int e) {
  stack_top++;
  stack_b[stack_top] = b;
  stack_e[stack_top] = e;  
}

void stack_pop(out int b, out int e) {
  b = stack_b[stack_top];
  e = stack_e[stack_top];
  --stack_top;
}

vec3 decode_vertex(int xyz) {
   int x = (xyz & 1023);
   int y = (xyz & (1023 << 10)) >> 10;
   int z = (xyz & (1023 << 20)) >> 20;
   return bbox_min + vec3(float(x), float(y), float(z)) / 1023.0 * (bbox_max - bbox_min);
}

vec3 min3(in vec3 p1, in vec3 p2, in vec3 p3) {
    return min(min(p1,p2),p3);
}

vec3 max3(in vec3 p1, in vec3 p2, in vec3 p3) {
    return max(max(p1,p2),p3);
}

void mesh_AABB(in Ray R, in Material M, inout Intersection I) {
  // References:
  //   http://www.codercorner.com/ZeroByteBVH.pdf 
  // With some adaptations:
  //   a) here the tree is balanced
  //   b) sorting coord. simply cycles between X,Y,Z   
  //   c) for now I always use 6 triangles to compute bbox
  // Consequence of a,b,c: the AABB is fully implicit, fully described by
  //  the order of the triangles in the mesh.
  // Mesh sorting program will be posted here shortly.

#ifdef NAIVE    
  for(int i=0; i<facets_data_size; ++i) {
      ivec3 F = get_facet(i);
      vec3 p1 = decode_vertex(F.x);
      vec3 p2 = decode_vertex(F.y);
      vec3 p3 = decode_vertex(F.z);
      triangle(R, p1, p2, p3, M, I);      
  }
#else    
    vec3 invDir = vec3(1.0/R.Dir.x, 1.0/R.Dir.y, 1.0/R.Dir.z);
    stack_push(0, facets_data_size);
    while(!stack_empty()) {
        int b,e;
        stack_pop(b,e);
        if(e-b <= 16) {
            for(int i=b; i<e; ++i) {
                ivec3 F = get_facet(i);
                vec3 p1 = decode_vertex(F.x);
                vec3 p2 = decode_vertex(F.y);
                vec3 p3 = decode_vertex(F.z);
                triangle(R, p1, p2, p3, M, I);
            }
        } else {
           ivec3 F = get_facet(b);
           vec3 p1 = decode_vertex(F.x);
           vec3 p2 = decode_vertex(F.y);
           vec3 p3 = decode_vertex(F.z);
           const int nbb = 6; // Number of triangles used to compute the bbox
                              // (for now fixed, could be stored in the mesh as well).

           // Compute bbox (and test triangles used to compute the bbox, they
           // are not part of the tree).
           triangle(R, p1, p2, p3, M, I);
           vec3 bbox_min = min3(p1,p2,p3); 
           vec3 bbox_max = max3(p1,p2,p3);
           for(int i=b+1; i<b+nbb; ++i) {
              ivec3 F = get_facet(i);
              vec3 p1 = decode_vertex(F.x);
              vec3 p2 = decode_vertex(F.y);
              vec3 p3 = decode_vertex(F.z);
              triangle(R, p1, p2, p3, M, I);
              bbox_min = min(bbox_min, min3(p1,p2,p3)); 
              bbox_max = max(bbox_max, max3(p1,p2,p3));
           }

           if(segment_box_intersection(R.Origin, invDir, bbox_min, bbox_max, I.t)) { 
              b += nbb;
              int m = b + (e-b)/2;
              // Small optimization: traverse nearest child first (so that pruning is
              // more likely to occur).
              int coord = (stack_top + 1)%3; // coord along which current node is split
              if(R.Dir[coord] < 0.0) {
                stack_push(b,m);
                stack_push(m,e);                  
              } else {
                stack_push(m,e);
                stack_push(b,m); // positive -> process this child first                                    
              } 
              // TODO: elimination of the terminal recursion (latest pushed node is
              // popped immediatly after, see iq's comment).
           }     
        }
    }
#endif    
}  


void sky(out vec4 fragColor, in Ray R) {
   vec3 V = normalize(R.Dir);
   float s = 0.5 * (V.z + 1.0);
   fragColor = mix( 
      vec4(0.0, 0.0, 0.0, 1.0),
      vec4(1.0, 1.0, 2.0, 1.0),
      s
   );
}


vec3 lighting( in Intersection I, in vec3 LightPos) {
   vec3 L = LightPos - I.P;
   float diffuse = max(dot(I.N, L)/length(L),0.0);
   return diffuse * I.material.Kd;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
