      vec3(0.0, 0.0, 0.0),
      0.0,
      vec3(0.0, 0.0, 0.0)
   );

   mesh_AABB(R, WHITE, I);
   sphere(R, Sphere(vec3(0.0, 0.0, -1001.0), 1000.0), WHITE, I);

   if(I.t == FARAWAY) {
      sky(fragColor, R);
   } else {
      fragColor.xyz = I.material.Ka;
      fragColor.xyz += lighting(I, vec3(0.5, 1.3, 0.5));
   }
}

