#pragma optimize(off)

// An array implemented as a huge switch() statement.
// Inspired from https://www.shadertoy.com/view/tlX3W7 (see 'microcode RAM')
// See also my earlier experiment here: https://www.shadertoy.com/view/3lK3Wd
//   I did not find a better way of sending a large array,
//   * using a big const array eats up all the registers in some implementations,
//      See for instance my previous version: https://www.shadertoy.com/view/wtG3z3
//        Works well on my Linux box (driver probably uses a SSBO), 
//           but lags at 0.5 FPS :-( under windows. 
//   * using a function instead stores all the values in the code segment. The function
//      is only called at the first frame to send all the data to BufferA. Then all this
//      code is probably evicted from the GPU code cache.
//   * one can send large arrays using this trick 
//   * note the #pragma optimize(off) (since this shader is only executed once, it is OK),
//     without it compilation time becomes veeeery long !
//     (https://shadertoyunofficial.wordpress.com/2017/11/19/avoiding-compiler-crash-or-endless-compilation/)
//     ... argh, under windows, even with the flag, it takes ages (100 seconds if THE_BIG_ONE is defined)
//     Got it: it is the angle GLSL to HLSL transpiler that takes all the time. 
//      You can try disable it and using OpenGL directly instead (about:config in firefox).

/*
// MeshCompiler [Bruno Levy Jan 2020]
// Converts a .obj (Alias|Wavefront) tri mesh into an AABB, encoded in GLSL, for ShaderToy.
//
// Fits in 256 lines :-)

#include <iostream>
#include <vector>
#include <algorithm>

// Sorts a vector and removes duplicated entries.
template <typename VECTOR> inline void sort_unique(VECTOR& v) {
    std::sort(v.begin(), v.end()); v.erase(std::unique(v.begin(), v.end()), v.end());
}

// -------- GLSL-compatible types //-----------------------------------------------------

template <class T> class gvec3 {
public:
    gvec3() {  }
    gvec3(T x_in, T y_in, T z_in) : x(x_in), y(y_in), z(z_in) {  }
    T& operator[](int i)             { return (&x)[i]; }
    const T& operator[](int i) const { return (&x)[i]; }
    T x,y,z;
};

template <class T> inline gvec3<T> operator+(gvec3<T> u, gvec3<T> v) {
    return gvec3<T>(u.x+v.x,u.y+v.y,u.z+v.z);
}

template <class T> inline gvec3<T> min(gvec3<T> u, gvec3<T> v) {
    return gvec3<T>(std::min(u.x,v.x),std::min(u.y,v.y),std::min(u.z,v.z));
}

template <class T> inline gvec3<T> max(gvec3<T> u, gvec3<T> v) {
    return gvec3<T>(std::max(u.x,v.x),std::max(u.y,v.y),std::max(u.z,v.z));
}

typedef gvec3<double> vec3; typedef gvec3<int> ivec3;

// -------- Encode/Decode //----------------------------------------------------------

// Encodes a vertex, using 10 bits per coordinate, relative to bbox
int encode_vertex(vec3 p, vec3 bbox_min, vec3 bbox_max) {
    double x = (p.x - bbox_min.x) / (bbox_max.x - bbox_min.x);
    double y = (p.y - bbox_min.y) / (bbox_max.y - bbox_min.y);
    double z = (p.z - bbox_min.z) / (bbox_max.z - bbox_min.z);
    int ix = int(x * 1023.0); int iy = int(y * 1023.0); int iz = int(z * 1023.0);
    return ix | (iy << 10) | (iz << 20);
}

// Decodes the three integer coordinates of a vertex (10 bits per coordinate)
ivec3 decode_vertex(int xyz) {
   return ivec3( 
       (xyz & 1023),	
       (xyz & (1023 << 10)) >> 10, 
       (xyz & (1023 << 20)) >> 20
   );
}

// Encodes the coordinates of the three vertices of a triangle in an ivec3
ivec3 encode_facet(vec3 p1, vec3 p2, vec3 p3, vec3 bbox_min, vec3 bbox_max) {
    return ivec3(
	encode_vertex(p1, bbox_min, bbox_max),
	encode_vertex(p2, bbox_min, bbox_max),
	encode_vertex(p3, bbox_min, bbox_max)	    
    );
}

// -------- Mesh geometry //-------------------------------------------------------------------

// Computes (three times) the barycenter of a facet
ivec3 facet_center(ivec3 f) {
    return decode_vertex(f.x) + decode_vertex(f.y) + decode_vertex(f.z);
}

// Computes the (floating-point) bbox of a pointset
void get_bbox(const std::vector<vec3>& pts, vec3& bbox_min, vec3& bbox_max) {
    bbox_min = pts[0]; bbox_max = pts[0];
    for(int i=1; i<pts.size(); ++i) {
	bbox_min = min(bbox_min, pts[i]); bbox_max = max(bbox_max, pts[i]);	
    }
}

// Computes the (integer) bbox of a triangle
void get_facet_bbox(ivec3 f, ivec3& bbox_min, ivec3& bbox_max) {
    bbox_min = decode_vertex(f.x); bbox_max = decode_vertex(f.x);
     ivec3 p = decode_vertex(f.y); bbox_min = min(bbox_min, p); bbox_max = max(bbox_max, p);
           p = decode_vertex(f.z); bbox_min = min(bbox_min, p); bbox_max = max(bbox_max, p);
}

// -------- I/O //---------------------------------------------------------------------------

// Outputs a floating point number, 6.6 digits
std::string format(double x) {
    static char buff[300]; sprintf(buff,"%-6.6f",x); return std::string(buff);
}

// Outputs an integer in hexa, 8 digits
std::string format_int(int x) {
    static char buff[300]; sprintf(buff,"0x%08x",x); return buff;
}

// Outputs an integer in hexa, 4 digits
std::string format_small_int(int x) {
    static char buff[300]; sprintf(buff,"0x%04x",x); return buff;
}

// Generates a GLSL function that encodes an array of ivec3, as a (huge) switch statement
// (It is stupid, I know, but this is the only way I found to send a big array to ShaderToy).
void gen_ivec3_func(std::ostream& out, const std::vector<ivec3>& array, std::string name) {
    out << "#pragma optimize(off)" << std::endl;
    out << "const int " << name << "_size = " << array.size() << ";" << std::endl;
    out << "ivec3 " << name << "(in int i) {" << std::endl;
    out << "#  ifdef R"   << std::endl;
    out << "#    undef R" << std::endl;
    out << "#  endif"     << std::endl;
    out << "#  define R(i,a,b,c) case i: r=ivec3(a,b,c); break;" << std::endl;
    out << "  ivec3 r;" << std::endl;
    out << "  switch(i) {" << std::endl;
    for(int i=0; i<int(array.size()); ++i) {
       out << "    R(" << format_small_int(i) << ","
	               << format_int(array[i].x) << "," << format_int(array[i].y) << "," << format_int(array[i].z) << ")";
       if(!((i+1)%10)) { out << std::endl; }
    }
    out << "  }" << std::endl;
    out << "  return r;" << std::endl;
    out << "}" << std::endl;
}

// Loads a mesh from a .obj (Alias|Wavefront) file. Gets the point coordinates and triangle vertex indices.
bool load_mesh(const std::string& filename, std::vector<vec3>& points, std::vector<ivec3>& triangles) {
    points.clear();
    triangles.clear();
    FILE* F = fopen(filename.c_str(),"r");
    if(!F) { return false;  }
    char* line=nullptr;
    size_t linelen=0;
    int lineno = 0;
    while(getline(&line,&linelen,F) >= 0) {
	++lineno;
	switch(line[0]) {
	    case 'v': {
		float x,y,z;
		bool ok = (sscanf(line+1, "%f %f %f", &x, &y, &z) == 3);
		if(!ok) {
		    std::cerr << lineno << ": malformed vertex" << std::endl;
		    std::cerr << line << std::endl;
		    return false;
		}
		points.push_back(vec3(double(x),double(y),double(z)));
	    } break;
	    case 'f': {
		int v1 =-1,v2 =-1,v3 =-1;
		int vt1=-1,vt2=-1,vt3=-1;
		int vn1=-1,vn2=-1,vn3=-1;
		bool ok = false;
		ok = ok || (sscanf(line+1,"%d %d %d",                   &v1,&v2,&v3                              ) == 3);
		ok = ok || (sscanf(line+1,"%d/%d %d/%d %d/%d",          &v1,&vn1,&v2,&vn2,&v3,&vn3               ) == 6);
		ok = ok || (sscanf(line+1,"%d/%d/%d %d/%d/%d %d/%d/%d", &v1,&vn1,&vt1,&v2,&vn2,&vt2,&v3,&vn3,&vt3) == 9);
		ok = ok || (sscanf(line+1,"%d//%d %d//%d %d//%d",       &v1,&vt1,&v2,&vt2,&v3,&vt3               ) == 6);
		if(!ok ||v1 < 1 ||v2 < 1 ||v3 < 1) {
		    std::cerr << lineno << ": malformed facet" << std::endl;
		    std::cerr << line << std::endl;
		    return false;
		}
		triangles.push_back(ivec3(v1-1,v2-1,v3-1));
	    } break;
	}
    }
    free(line);
    fclose(F);
    return true;
}

// Loads a mesh from a .obj (Alias|Wavefront) file. Gets the bbox and for each triangle its vertices coordinates packed
// in a single integer (10 bits per component).
bool load_and_convert_mesh(const std::string& filename, std::vector<ivec3>& triangles_out, vec3& bbox_min, vec3& bbox_max) {
    std::vector<vec3> points;
    std::vector<ivec3> triangles;
    if(!load_mesh(filename, points, triangles)) { return false; }
    get_bbox(points, bbox_min, bbox_max);
    triangles_out.resize(triangles.size());
    for(int t=0; t<triangles.size(); ++t) {
	ivec3 T = triangles[t];
	triangles_out[t] = ivec3(
	    encode_vertex(points[T.x], bbox_min, bbox_max),
	    encode_vertex(points[T.y], bbox_min, bbox_max),
	    encode_vertex(points[T.z], bbox_min, bbox_max)
	);
    }
    return true;
}

// -------- Zero-memory AABB //----------------------------------------------------------------

// Creates a zero-memory Axis-Aligned Bounding Box tree.
// Nothing needs to be stored, it is completely defined by the order of the triangles.
//  - The sequence [b,e[ of triangles is processed
//  - coord: the axis along which bboxes are sorted (one of 0,1,2)
void init_ZMAABB(std::vector<ivec3>& mesh, int b, int e, int coord) {
    if(e-b <= 8) { return; }
    // Sort facets along coord
    std::sort(
	mesh.begin() + b, mesh.begin() + e,
	[coord](const ivec3& F1, const ivec3& F2) {
	    return (facet_center(F1)[coord] < facet_center(F2)[coord]);
	}
    );
    // Get sequence's bbox, and the indices of the triangles that touch the bbox
    ivec3 bbox_min, bbox_max;
    get_facet_bbox(mesh[b], bbox_min, bbox_max);
    ivec3 index_bbox_min(b,b,b), index_bbox_max(b,b,b);
    for(int i=b+1; i<e; ++i) {
	ivec3 cur_bbox_min, cur_bbox_max;
	get_facet_bbox(mesh[i], cur_bbox_min, cur_bbox_max);
	for(int coord=0; coord<3; ++coord) {
	    if(cur_bbox_min[coord] < bbox_min[coord]) {
		bbox_min[coord] = cur_bbox_min[coord];
		index_bbox_min[coord] = i;
	    }
	    if(cur_bbox_max[coord] > bbox_max[coord]) {
		bbox_max[coord] = cur_bbox_max[coord];
		index_bbox_max[coord] = i;
	    }
	}
    }
    // Put the triangles that touch the bbox first (there are at most 6 of them)
    std::vector<int> box_indices;
    box_indices.push_back(index_bbox_min.x); box_indices.push_back(index_bbox_min.y); box_indices.push_back(index_bbox_min.z);
    box_indices.push_back(index_bbox_max.x); box_indices.push_back(index_bbox_max.y); box_indices.push_back(index_bbox_max.z);
    sort_unique(box_indices);
    int nbb = int(box_indices.size());
    for(int i=0; i<nbb; ++i) {
	std::swap(mesh[b+i], mesh[box_indices[i]]);
    }
    b += 6; // The triangles that touch the bbox are no longer in the tree
    // Recurse in the rest of the triangles
    int m = b + (e-b)/2; // We use a balanced tree (a bit less efficient, but then we do not need storing number of triangles)
    init_ZMAABB(mesh, b, m, (coord + 1)%3);
    init_ZMAABB(mesh, m, e, (coord + 1)%3);	
}

int main(int argc, char** argv) {
    if(argc != 2) {
	std::cerr << "Usage: " << argv[0] << " <filename>" << std::endl;
	return -1;
    }
    vec3 bbox_min, bbox_max;
    std::vector<ivec3> facets;
    load_and_convert_mesh(argv[1], facets, bbox_min, bbox_max);
    init_ZMAABB(facets, 0, int(facets.size()), 0);
    std::cout << "const vec3 bbox_min = vec3(" << bbox_min.x << "," << bbox_min.y << "," << bbox_min.z << ");" << std::endl;
    std::cout << "const vec3 bbox_max = vec3(" << bbox_max.x << "," << bbox_max.y << "," << bbox_max.z << ");" << std::endl;
    gen_ivec3_func(std::cout, facets, "facets_data");
    return 0;
}
