vec4 encode_int(in int x) {
    ivec4 col = (ivec4(x)>>ivec4(0,8,16,24))&255;
    return vec4(col)/255.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Test whether data should be sent. Data is sent if:
    //  this is the first frame or
    //  rendering area was resized (previous width/height stored in first pixel)
    bool reset = (iFrame < 10);
    reset = reset || (get_data(RESOLUTION_OFFSET,0) != int(iChannelResolution[0].x));
    reset = reset || (get_data(RESOLUTION_OFFSET,1) != int(iChannelResolution[0].y));
    if(reset) {  
       ivec2 uv = ivec2(fragCoord); 
       int index = uv.x + uv.y * int(iResolution.x); 
       if(index < 2) {
           fragColor = encode_int(int(iResolution[index]));
       } else {
          index -= MESH_DATA_OFFSET;
          if(index < facets_data_size*3) {
            int comp = index % 3;
            index = index / 3;
            ivec3 P = facets_data(index);
            fragColor = encode_int(P[comp]);    
          } else {
            fragColor = vec4(0.0, 0.0, 0.0, 1.0);
          }
       }
    } else {
        fragColor = texelFetch(iChannel0,ivec2(fragCoord),0);
    }
}

