// Reads a 32 bits integer encoded in a pixel of BufferA
// (implementation of get_data(), do not call directly)
int get_data_raw(in sampler2D channel, in int width, in int index) {
   vec4 col = 255.0*vec4(texelFetch(channel, ivec2(index%width,index/width), 0));
   return (int(col.r)      ) | 
          (int(col.g) << 8 ) |
          (int(col.b) << 16) |
          (int(col.a) << 24) ;
}


// Reads a 32 bits integer encoded in a pixel of BufferA
//  offset: one of RESOLUTION_OFFSET, MESH_DATA_OFFSET
//  index: the index of the data to be read
#define get_data(offset, index) \
    get_data_raw(iChannel0, int(iChannelResolution[0].x), (offset)+(index))

// In BufferA, data is organized in sections
// RESOLUTION_OFFSET: 2 integers (previous width and height, used to detect win resize)
// MESH_DATA_OFFSET: facets_data_size * 3 integers. Each integer encores the x,y,z 
//                    coordinates of a vertex (x:10 bits, y:10 bits, z:10 bits). 
const int RESOLUTION_OFFSET=0;
const int MESH_DATA_OFFSET=2;

