    vec2 uv = 6. * fragCoord/iResolution.xy;

        // the sound texture is 512x2
    //int tx = int(uv.x*512.0);
    int tx = 1;

	// first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
	float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 

    // second row is the sound wave, one texel is one mono sample
    float wave = texelFetch( iChannel0, ivec2(tx,1), 0 ).x;

    for(int n=1; n<8; n++){

        float i = float(n);
        uv += vec2(.7 / i * sin(fft * i *uv.y + iTime*5. + 0.3 *i)+0.8, 0.4 / i * sin(uv.x*0.5 +iTime +0.3 * i *fft) +1.6);

    }



    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 col = vec3(0.5 * sin(uv.x) +0.5, 0.5*sin(uv.y) + 0.5, sin(uv.x + uv.y)+0.5+wave);
    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

        // the sound texture is 512x2
    //int tx = int(uv.x*512.0);
    int tx = 1;

	// first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
	float fft  = texelFetch( iChannel0, ivec2(tx,0), 0 ).x; 

    // second row is the sound wave, one texel is one mono sample
    float wave = texelFetch( iChannel0, ivec2(tx,1), 0 ).x;


    col += sin(uv.x * cos(iTime/30.*fft/20.) * 60.) + sin(uv.y * cos(iTime / 20.) * 18.);

    col += cos(uv.x* sin(iTime/60.) * 60.) + cos(uv.y * sin(iTime / 10.*wave) * 18. * fft);

    //col *= sin(iTime / 30.);

    col += mix(col,(cos(iMouse.yxy+vec3(0,2,4))),uv.y);
    // Output to screen
    fragColor = vec4(col,1.0);
}

const int MAXD = 400;
const float EPSI  =0.0001;
int matId = 0;
mat2 rotate(float a){
    return mat2(cos(a),-sin(a),
                sin(a),cos(a));
}

float sm( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdSphere(vec3 p,float size){
    return length(p)-size;
}

float map(vec3 p,bool id){
    float smo = 2.;
    float sphere = sdSphere(p,1.);
    float sphere2 = sdSphere(p+vec3(sin(iTime)*2.,sin(iTime)*2.,sin(iTime)*2.),0.4);
    float sphere3 = sdSphere(p+vec3(sin(iTime)*2.,sin(iTime)*2.,cos(iTime)*4.),0.4);
    float sphere4 = sdSphere(p+vec3(sin(iTime)*2.,cos(iTime)*4.,sin(iTime)*2.),0.4);
    float sphere5 = sdSphere(p+vec3(sin(iTime)*2.,cos(iTime)*4.,cos(iTime)*4.),0.4);
    float sphere6 = sdSphere(p+vec3(cos(iTime)*4.,sin(iTime)*2.,sin(iTime)*2.),0.4);
    float sphere7 = sdSphere(p+vec3(cos(iTime)*4.,sin(iTime)*2.,cos(iTime)*4.),0.4);
    float sphere8 = sdSphere(p+vec3(cos(iTime)*4.,cos(iTime)*4.,sin(iTime)*2.),0.4);
    float sphere9 = sdSphere(p+vec3(sin(iTime)*4.,sin(iTime)*4.,sin(iTime)*4.),0.4);
    float sphere10 = sdSphere(p-vec3(sin(iTime)*2.,sin(iTime)*2.,sin(iTime)*2.),0.4);
    float sphere11 = sdSphere(p-vec3(sin(iTime)*2.,sin(iTime)*2.,cos(iTime)*4.),0.4);
    float sphere12 = sdSphere(p-vec3(sin(iTime)*2.,cos(iTime)*4.,sin(iTime)*2.),0.4);
    float sphere13 = sdSphere(p-vec3(sin(iTime)*2.,cos(iTime)*4.,cos(iTime)*4.),0.4);
    float sphere14 = sdSphere(p-vec3(cos(iTime)*4.,sin(iTime)*2.,sin(iTime)*2.),0.4);
    float sphere15 = sdSphere(p-vec3(cos(iTime)*4.,sin(iTime)*2.,cos(iTime)*4.),0.4);
    float sphere16 = sdSphere(p-vec3(cos(iTime)*4.,cos(iTime)*4.,sin(iTime)*2.),0.4);
    float sphere17 = sdSphere(p-vec3(sin(iTime)*4.,sin(iTime)*4.,sin(iTime)*4.),0.4);


    float plane =1.-(length(p)-550.);
    float sp = sm(sphere9,sm(sphere8,sm(sphere7,sm(sphere6,sm(sphere5,sm(sphere4,sm(sphere3,sm(sphere2,min(sphere,plane),smo),smo),smo),smo),smo),smo),smo),smo);
    float sp2 = sm(sphere17,sm(sphere16,sm(sphere15,sm(sphere14,sm(sphere13,sm(sphere12,sm(sphere11,sm(sphere10,min(sphere,plane),smo),smo),smo),smo),smo),smo),smo),smo);
    float mn = min(sp,sp2);
    if(id){

        if(mn == plane){
            matId = 3;
        }
        else{
            matId = 2;
        }
    }
    return  mn;
}

vec3 normal(vec3 p){
    vec2 e = vec2(EPSI,0);
    return normalize(vec3(map(p+e.xyy,false)-map(p-e.xyy,false),
                            map(p+e.yxy,false)-map(p-e.yxy,false),
                            map(p+e.yyx,false)-map(p-e.yyx,false)));
}
vec3 rayMarch(vec3 ro,vec3 rd, bool id){

    float tot = 0.;
    float dst = 0.;
    vec3 p;
    for(int i = 0; i<MAXD; i++){
        p = ro+rd*tot;
        dst = map(p,id);
        tot+=dst;
        if(dst<EPSI || tot > float(MAXD)){
            break;
        }
    }
    if(dst > EPSI){
       // matId = 1;
    }
    return p;
}
vec3 light(vec3 p,vec3 ro){
    vec3 lightCol = vec3(1);
    vec3 objCol = vec3(0);
    vec3 lightPos = vec3(10,10,12);
    vec3 viewDir = normalize(ro-p);
    vec3 l = normalize(lightPos - p);
    vec3 n = normal(p);
    vec3 reflectDir = reflect(-l,n);
    float diff = max(dot(l,n),0.);
    float spec = pow(max(dot(viewDir,reflectDir),0.),25.);
    float spcStr = 3.;    
    float ambi = 0.45;



    bool rm = length(rayMarch(p+n*EPSI*2.,l,false))<length(lightPos-p);
    if(rm){
        diff *= 0.1;
    }
    if(matId == 2){
        objCol = vec3(1,0.9,0.);
    }

    else if(matId == 3){

        objCol = vec3(0,0,0);
    }
    vec3 diffuse = lightCol*diff;
    vec3 ambient = ambi*lightCol;    
    vec3 specular = spcStr*spec*lightCol;
    return (ambient+diffuse+specular)*objCol;
}



void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (fragCoord.xy-0.5 * iResolution.xy)/iResolution.x;
    vec3 ro = vec3(0,0,-20);
    vec3 rd = normalize(vec3(uv,1.));
    ro.zy*=rotate(iTime*2.);
    rd.zy*=rotate(iTime*2.);
    ro.xz*=rotate(iTime);
    rd.xz*=rotate(iTime);

    vec3 p = rayMarch(ro,rd,true);
    vec3 color = (light(p,ro));
    if(matId == 1){
        color = vec3(0);
    }
    fragColor = vec4(color,1.0);
}

// lots of inspiration from IQ and hg_sdf

const float PI = 3.14159265359;
const int MAX_MARCHING_STEPS = 50;
const float EPSILON = 0.0001;

vec2 rotate2d(vec2 v, float a) {
	return vec2(v.x * cos(a) - v.y * sin(a), v.y * cos(a) + v.x * sin(a)); 
}

// Rotate around a coordinate axis (i.e. in a plane perpendicular to that axis) by angle <a>.
// Read like this: R(p.xz, a) rotates "x towards z".
void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float sdTorus( vec3 p, vec2 t ) {
	vec2 q = vec2(length(p.xz)-t.x,p.y);
	return length(q)-t.y;
}

float opTwist( vec3 p, float fftValue, float time ) {
    float c = cos((fftValue*1.5) *p.y);
    float s = sin((fftValue+0.25)*p.y);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m * p.xz, p.y);

    return sdTorus(q, vec2(abs(sin(iTime*0.1))+0.5*(fftValue*0.03), fftValue*0.0001));

}

float opRep( vec3 p, vec3 c ) {

    float idx = mod((floor((p.x)/c.x)), 32.0);
    float idy = mod((floor((p.y)/c.y)), 32.0);
    float idz = mod((floor((p.z)/c.z)), 32.0);

	float id = length(vec3(idx, idy, idz));

    vec3 q = mod(p, c) - 0.5 * c;

    vec3 r = q;  

    float rotationAmount = (id * 5.0) + (iTime * 2.0);

    bool xmod2 = mod(idx, 2.0) == 0.0;

    // offset even rows
    if (xmod2) {
    	q.y += 1.5;
        r.y -= 1.5;
    }

	pR(q.xy, rotationAmount);
    pR(q.xz, rotationAmount * 0.1);

    float shape1 = opTwist(q, 1.0, iTime);

    if (xmod2) {

        pR(r.xy, rotationAmount);
        pR(r.xz, rotationAmount * 0.1);

        float shape2 = opTwist(r, 1.0, iTime);

    	return min(shape1, shape2);

    } else {

        return shape1;

    }
}


float sceneSDF(vec3 samplePoint) {
	return opRep(samplePoint, vec3(3.0, 3.0, 3.0));
}

vec3 castRay(vec3 pos, vec3 dir) {
	for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
		float dist = sceneSDF(pos);
        if (dist < EPSILON) {
			return pos;
        }
		pos += dist * dir;
	}    
	return pos;
}


float lightPointDiffuse(vec3 pos, vec3 lightPos) {
	float lightDist = length(lightPos - pos);
	float color = 3.0 / (lightDist * lightDist);
	return max(0.0, color*10.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec4 mousePos = (iMouse / iResolution.xyxy) * 2.0 - 1.0;

	mousePos *= vec2(PI / 2.0, PI / 2.0).xyxy;

    if (iMouse.zw == vec2(0.0)) {
        mousePos.xy = vec2(0.5, -0.2); 
    }

	vec2 screenPos = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;

	vec3 cameraPos = vec3(0.0, 0.0, -8.0);

	vec3 cameraDir = vec3(0.0, 0.0, 1.0);
	vec3 planeU = vec3(2.0, 0.0, 0.0);
	vec3 planeV = vec3(0.0, iResolution.y / iResolution.x * 2.0, 0.0);
	vec3 rayDir = normalize(cameraDir + screenPos.x * planeU + screenPos.y * planeV);

	cameraPos.yz = rotate2d(cameraPos.yz, mousePos.y);
	rayDir.yz = rotate2d(rayDir.yz, mousePos.y);

	cameraPos.xz = rotate2d(cameraPos.xz, mousePos.x);
	rayDir.xz = rotate2d(rayDir.xz, mousePos.x);

    cameraPos.zy += iTime;

	vec3 rayPos = castRay(cameraPos, rayDir);

    // base color
	vec3 color = vec3(0.0);


    color += lightPointDiffuse(rayPos, cameraPos) * 9.0;

    color = pow(color, vec3(0.5));

    float contrast = 0.5;

    color.rgb = (color.rgb - 0.5) / (1.0 - contrast) + 0.5;

    float grey = color.r * 0.21 + color.g * 0.71 + color.b * 0.07;

	fragColor = vec4(color, min(grey*0.1, 0.1));
}

float sdSphere(vec3 pos, float size)
{
    return length(pos) - size;
}

float sdBox(vec3 pos, vec3 size)
{
    pos = abs(pos) - vec3(size);
    return max(max(pos.x, pos.y), pos.z);
}

float sdOctahedron(vec3 p, float s)
{
    p = abs(p);
    float m = p.x+p.y+p.z-s;
    vec3 q;
         if( 3.0*p.x < m ) q = p.xyz;
    else if( 3.0*p.y < m ) q = p.yzx;
    else if( 3.0*p.z < m ) q = p.zxy;
    else return m*0.57735027;

    float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
    return length(vec3(q.x,q.y-s+k,q.z-k)); 
}

float sdPlane(vec3 pos)
{
    return pos.y;
}

mat2 rotate(float a)
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c, s, -s, c);
}

vec3 repeat(vec3 pos, vec3 span)
{
    return abs(mod(pos, span)) - span * 0.5;
}

float getDistance(vec3 pos, vec2 uv)
{
    vec3 originalPos = pos;

    for(int i = 0; i < 3; i++)
    {
        pos = abs(pos) - 4.5;
        pos.xz *= rotate(1.0);
        pos.yz *= rotate(1.0);
    }

    pos = repeat(pos, vec3(4.0));

    float d0 = abs(originalPos.x) - 0.1;
    float d1 = sdBox(pos, vec3(0.8));

    pos.xy *= rotate(mix(1.0, 2.0, abs(sin(iTime))));
    float size = mix(1.1, 1.3, (abs(uv.y) * abs(uv.x)));
    float d2 = sdSphere(pos, size);
    float dd2 = sdOctahedron(pos, 1.8);
    float ddd2 = mix(d2, dd2, abs(sin(iTime)));

    return max(max(d1, -ddd2), -d0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    // camera
    vec3 cameraOrigin = vec3(0.0, 0.0, -10.0 + iTime * 4.0);
    vec3 cameraTarget = vec3(cos(iTime) + sin(iTime / 2.0) * 10.0, exp(sin(iTime)) * 2.0, 3.0 + iTime * 4.0);
    vec3 upDirection = vec3(0.0, 1.0, 0.0);
    vec3 cameraDir = normalize(cameraTarget - cameraOrigin);
    vec3 cameraRight = normalize(cross(upDirection, cameraOrigin));
    vec3 cameraUp = cross(cameraDir, cameraRight);
    vec3 rayDirection = normalize(cameraRight * p.x + cameraUp * p.y + cameraDir);

    float depth = 0.0;
    float ac = 0.0;
    vec3 rayPos = vec3(0.0);
    float d = 0.0;

    for(int i = 0; i < 80; i++)
    {
        rayPos = cameraOrigin + rayDirection * depth;
        d = getDistance(rayPos, p);

        if(abs(d) < 0.0001)
        {
            break;
        }

        ac += exp(-d * mix(5.0, 10.0, abs(sin(iTime))));        
        depth += d;
    }

    vec3 col = vec3(0.0, 0.3, 0.7);
    ac *= 1.2 * (iResolution.x/iResolution.y - abs(p.x)) ;
    vec3 finalCol = col * ac * 0.06;
    fragColor = vec4(finalCol, 1.0);
    fragColor.w = 1.0 - depth * 0.1;
}

vec3 drawCircle(vec2 pos, float radius, float width, float power, vec4 color)
{
    vec2 mousePos = iMouse.xy - vec2(0.5);
    float dist1 = length(pos);
    dist1 = fract((dist1 * 5.0) - fract(iTime));
    float dist2 = dist1 - radius;
    float intensity = pow(radius / abs(dist2), width); 
    vec3 col = color.rgb * intensity * power * max((0.8- abs(dist2)), 0.0);
    return col;
}

vec3 hsv2rgb(float h, float s, float v)
{
    vec4 t = vec4(1.0, 2.0/3.0, 1.0/3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // // -1.0 ~ 1.0
    vec2 pos = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    float h = mix(0.5, 0.65, length(pos));
    vec4 color = vec4(hsv2rgb(h, 1.0, 1.0), 1.0);
    float radius = 0.5;
    float width = 0.8;
    float power = 0.1;
    vec3 finalColor = drawCircle(pos, radius, width, power, color);

    pos = abs(pos);
    // vec3 finalColor = vec3(pos.x, 0.0, pos.y);

    fragColor = vec4(finalColor, 1.0);
}

