// LENS FLAIR EFFECT
#define R iResolution.xy
#define T(U) texture(iChannel0,(U)/R)
#define D(U) texture(iChannel1,(U)/R)
vec4 F (vec2 U,vec2 r) {
	vec4 t = T(U+r);
    return exp(-.01*dot(r,r))*(exp(2.*t)-1.);
}
void mainImage( out vec4 Q, vec2 U )
{

   Q = vec4(0);
    for (float i = 0.; i < 8.; i+=1.1) {
    	Q += F(U,+vec2(-i,i));
    	Q += F(U,+vec2(i,i*.5));
    	Q += F(U,-vec2(-i,i));
    	Q += F(U,-vec2(i,i*.5));
    } 

    Q = T(U)*0.15+ 1e-5*Q;
    Q -= texture(iChannel2,D(U).xy/R)*.47;
    //Q -= texture(iChannel2,(U-D(U).xy*.5)/iResolution.y)*.47;
    Q = atan(Q);

}

