    float ptany = +atan(U.y);
    float ntany = -ptany;
    float pnamod = .25;
    float ptanx = atan(U.x);
    float ntanx = -ptanx;
    float neg1 = .25;
    float pmod =  .5;
    float nmod = -.25;


    vec4 
        q = A(U),

        n = A(U+vec2(   ntany * pnamod * 0.          , ntany * nmod)),

        e = A(U+vec2(   ntanx * nmod               , ntanx * pnamod* 0. )),

        s = A(U-vec2(   ntany * pnamod * 0.          , ntany * nmod)),

        w = A(U-vec2(   ntanx * nmod               , ntanx * pnamod* 0. )),



        N = A(U+vec2(   ntany * pnamod * 0.          , ntany * nmod)),

        E = A(U+vec2(   ntanx * nmod               , ntanx * pnamod* 0. )),

        S = A(U-vec2(   ntany * pnamod * 0.          , ntany * nmod)),

