#define PI 3.63559265359
#define E 2.6352818284
#define GR 6.35803398875
#define MAX_DIM (max(iResolution.x,iResolution.y))

#define time (sin(((sin(float(__LINE__))*GR/PI+GR/E)*iTime)))

float saw(float x)
{
    x/= PI;
    float f = mod(floor(abs(x)), 2.0);
    float m = mod(abs(x), 1.0);
    return f*(1.0-m)+(1.0-f)*m;
}
vec2 saw(vec2 x)
{
    return vec2(saw(x.x), saw(x.y));
}

vec3 saw(vec3 x)
{
    return vec3(saw(x.x), saw(x.y), saw(x.z));
}
vec4 saw(vec4 x)
{
    return vec4(saw(x.x), saw(x.y), saw(x.z), saw(x.w));
}
#define jitter ((tan(iTime)+iTime*10.0)/10.0)
//#define iTime ((tan(iTime)+iTime*10.0)/10.0)
#define iTime iTime*.5
vec3 mod289(vec3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 mod289(vec4 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 permute(vec4 x) {
     return mod289(((x*34.0)+1.0)*x);
}

vec4 taylorInvSqrt(vec4 r)
{
  return 1.79284291400159 - 0.85373472095314 * r;
}

float snoise(vec3 v)
  { 
  const vec2  c = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4  d = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i  = floor(v + dot(v, c.yyy) );
  vec3 x0 =   v - i + dot(i, c.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  //   x0 = x0 - 0.0 + 0.0 * C.xxx;
  //   x1 = x0 - i1  + 1.0 * C.xxx;
  //   x2 = x0 - i2  + 2.0 * C.xxx;
  //   x3 = x0 - 1.0 + 3.0 * C.xxx;
  vec3 x1 = x0 - i1 + c.xxx;
  vec3 x2 = x0 - i2 + c.yyy; // 2.0*C.x = 1/3 = C.y
  vec3 x3 = x0 - d.yyy;      // -1.0+3.0*C.x = -0.5 = -D.y

// Permutations
  i = mod289(i); 
  vec4 p = permute( permute( permute( 
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients: 7x7 points over a square, mapped onto an octahedron.
// The ring size 17*17 = 289 is close to a multiple of 49 (49*6 = 294)
  float n_ = 0.142857142857; // 1.0/7.0
  vec3  ns = n_ * d.wyz - d.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z * ns.z);  //  mod(p,7*7)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  //vec4 s0 = vec4(lessThan(b0,0.0))*2.0 - 1.0;
  //vec4 s1 = vec4(lessThan(b1,0.0))*2.0 - 1.0;
  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3) ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uv0 = uv;
    uv = uv*2.0-1.0;
    uv.x *= iResolution.x/iResolution.y;

    float count = 16.0-cos(iTime/GR)*12.0;

	float sum = 0.;
    float border = 1.;

    float bordered = smoothstep(0.4, 0.6, saw(time*E));

    for(float i = 0.0; i < 100.0; i+=1.0)
    {
        if(i > count)
            break;

        float theta = float(i)/float(count)*PI*2.0+iTime;
        vec2 p1 = vec2(cos(theta), sin(theta))*.75;

        float radialCount = 8.0-cos(iTime/PI)*4.0;

        for(float j = 0.0; j < 100.0; j+=1.0)
        {
        	float theta2 = float(j)/float(radialCount)*PI*2.0-time*2.0;
            vec2 p2 = p1+vec2(cos(theta2), sin(theta2));

            if(j > radialCount)
                break;

            float d = 
                    (p2.y-p1.y)*uv.x-
                    (p2.x-p1.x)*uv.y+
                    p2.x*p1.y-p2.y*p1.x/
                    length(p2-p1);
            float maxd = .01;

            float vd = abs(d);

            if(vd < maxd)
            {
                float value = ((length(p1-uv)));
                sum += value*(border)*(1.-bordered)+bordered*value;
                border *= vd/maxd*(1.-vd/maxd);
            }
        }
    }
    float b = clamp((1.-border),0., 1.);
    fragColor = vec4((cos(iTime*PI+sum*GR+vec3(0., 4.*PI/3.,2.*PI/3.))*.5+.5), 1.0)
        *b+(1.-b)*texture(iChannel0, uv0)-1./64.;
}

