const float LINE_WIDTH = 0.05;
const float LINE_SPACING = 0.1;
const float LINE_VELOCITY = 0.2;

/**
Check if coordinate is in the series of moving
lines. Expects the coordinate to be normalized [0,1]
*/
bool isInScrollingLines(vec2 coord){
    float halfwidth = LINE_WIDTH/2.0;
    float offset = mod(iTime * LINE_VELOCITY, LINE_SPACING);

    for(float pos = 0.0 - offset; pos <= 1.0 + LINE_WIDTH; pos=pos+LINE_SPACING){
        if(coord.x > (pos-halfwidth) && coord.x < (pos+halfwidth)){
            return true;
        }
    }

    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0.0);

    if(isInScrollingLines(uv))
        col = vec3(0.0);
    else{
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

        float bgOpacity = (sin(iTime) + 1.0) / 2.0;
        col = col * bgOpacity;
    }


    // Output to screen
    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Calculate the to center distance
    float d = length(uv - 0.5) * 2.0;

    // Calculate the ripple time
    float t = d * d * d * 35.0 - iTime * 13.0;


    // Calculate the ripple thickness
    d = (sin(t * 0.1) * cos(t * 2.1) * 0.5 + 0.5) * (1.0 - d);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5 * cos(t / 20.0 + uv.xyx + vec3(0.0,2.0,4.0));
	col.rgb = vec3(pow(col.r, 20.0) + 0.2);
    // Set the output color to rgb channels and the thickness to alpha channel
    // AO is automatically calculated
    fragColor = vec4(col, d);
}

  vec2 getScreenSpace() {
    vec2 uv = (gl_FragCoord.xy - 0.5 * iResolution.xy) / min(iResolution.y, iResolution.x);

    return uv;
  }

vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0); }

float snoise(vec2 v){
  const vec4 C = vec4(0.211324865405187, 0.366025403784439,
           -0.577350269189626, 0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod(i, 289.0);
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
  + i.x + vec3(0.0, i1.x, 1.0 ));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy),
    dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = getScreenSpace();

    float w = 30./iResolution.y;

    uv *= 30.;
    vec2 grid = floor(uv);
    vec2 subuv = fract(uv);

    vec3 colour = vec3(0);
    float d = 0.;

    for(int x = -1; x < 2; x++) {

      for(int y = -1; y < 2; y++) {
        vec2 os = vec2(x,y);
        vec2 _grid = grid + os;
        vec2 _subuv = subuv - os;

        float r = snoise(_grid*.1 + iTime*.5)*.2+.3;
          d = r;
        colour += vec3(smoothstep( r+w, r-w, length(_subuv-.5) ));
      }

    }


    fragColor = vec4(colour, mix(0., d, colour.x));
}

