vec3 rotate3D(vec3 point, vec3 rotation) {
    vec3 r = rotation;
	mat3 rz = mat3(cos(r.z), -sin(r.z), 0,
                   sin(r.z),  cos(r.z), 0,
                   0,         0,        1);
    mat3 ry = mat3( cos(r.y), 0, sin(r.y),
                    0       , 1, 0       ,
                   -sin(r.y), 0, cos(r.y));
    mat3 rx = mat3(1, 0       , 0        ,
                   0, cos(r.x), -sin(r.x),
                   0, sin(r.x),  cos(r.x));
    return rx * ry * rz * point;
}

float sdfSphere(vec3 position, vec3 center, float radius) {
    return distance(position, center) - radius;
}

float sdfPlane( vec3 position, vec4 n )
{
  // TODO (28 Jan 2020 sam): Figure out how this works.
  // Currently, I'm just fiddling values till I get the result I want.
  return dot(position,n.xyz) + n.w;
}

float distanceField(vec3 position) {
	// sphere 1
    float d = sdfSphere(position, vec3(0.0, 0.0, 0.0), 0.55);
    //return d;
    vec2 moon = mat2(cos(iTime),-sin(iTime),sin(iTime),cos(iTime)) * vec2(1.0,0.0);
    float d1 = sdfSphere(position, vec3(moon.x, 0.1, moon.y), 0.25);
    float d2 = sdfPlane(position, vec4(0.0, 0.5, 0.0, 1.0));

    return min(d2, min(d, d1));
}

vec3 calcNormal( vec3 p ) 
{
    // We calculate the normal by finding the gradient of the field at the
    // point that we are interested in. We can find the gradient by getting
    // the difference in field at that point and a point slighttly away from it.
    const float h = 0.0001;
    return normalize( vec3(
        			       -distanceField(p)+ distanceField(p+vec3(h,0.0,0.0)),
                           -distanceField(p)+ distanceField(p+vec3(0.0,h,0.0)),
                           -distanceField(p)+ distanceField(p+vec3(0.0,0.0,h)) 
    				 ));
}

float raymarch( vec3 direction, vec3 start) {
    // We need to cast out a ray in the given direction, and see which is
    // the closest object that we hit. We then move forward by that distance,
    // and continue the same process. We terminate when we hit an object
    // (distance is very small) or at some predefined distance.
    float far = 15.0;
    float d = 0.0;
    vec3 pos = start;
    for (int i=0; i<100; i++) {
    	float sphereDistance = distanceField(pos);
        pos += sphereDistance*direction;

        d += sphereDistance;
        if (sphereDistance < 0.01) {
        	break;
        }
        if (d > far) {
        	break;
        }
    }
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalise and set center to origin.
    vec2 p = fragCoord/iResolution.xy;
    p -= 0.5;
    p.y *= iResolution.y/iResolution.x;

    vec3 cameraPosition = vec3(0.0, 0.0, -6.0);
    //vec3 cameraOrientation = vec3(0.0, 0.0, 0.0);
    vec3 planePosition = vec3(p, -5.0);
    planePosition = rotate3D(planePosition, vec3(0.0, 0.0, iTime));

    vec3 lookingDirection = (planePosition - cameraPosition);

    // Rotate light around origin in xz plane
    float angle = iTime;
    vec2 lightPos2D = mat2(cos(angle),-sin(angle),sin(angle),cos(angle))*vec2(0.0,1.0); 
    vec3 lightPoint = normalize(vec3(1.0, 1.0, -1.0));
    vec3 lightFacing = lightPoint - vec3(0.0);

    // raymarch to check for colissions.
    float dist = raymarch(lookingDirection, planePosition);
    vec3 color = vec3(0.1);
    if (dist < 15.0) {
        color = vec3(0.2, 0.2, 0.4);
    	vec3 normal = calcNormal(planePosition+ dist*lookingDirection);
        color += 0.3*dot(lightFacing, normal);
    }
    fragColor = vec4(color,1.0);
}

#define TAU 6.283185307179586

vec3 dark = vec3(0.27);
vec3 mid = vec3(0.41);
vec3 light = vec3(0.62);

vec4 sine(vec2 k, float w, float t, vec2 p) {
    float y = k.y * sin(k.x*p.x);
    vec3 c = fract(k.x*(p.x+t)/TAU) < 0.5 ? light : dark;
    return y - 0.5*w <= p.y && p.y <= y + 0.5*w ? vec4(c, 1.0) : vec4(0.0);
}

float map(float x, float l0, float r0, float l1, float r1) {
	return (x - l0) / (r0 - l0) * (r1 - l1) + l1;
}

float imod(float x, float l, float r) {
	return map(fract(map(x, l, r, 0.0, 1.0)), 0.0, 1.0, l, r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord/iResolution.xy*2.0 - 1.0)*vec2(1.0, iResolution.y/iResolution.x);
   	p = fragCoord * 0.005;

    float kt = 0.01*iTime;
    vec2 sine_k = vec2(32.0, 0.013);
    float dy = 0.1;
    float w = 0.01;
    float r = sine_k.y+w;
    float l = -r;
    float df = map(p.y + dy, l, r, 0.0, 1.0/3.0);
    float d = floor(df);
    float dir = mod(d, 2.0) < 1.0 ? 1.0 : -1.0;
    float o = d * TAU * (dir >= 0.0 ? 0.5 : 0.0) / sine_k.x;
    vec2 kp = vec2(kt * dir, dy);
    vec2 p1 = p + kp;
    p1.x += o;
    p1.y = imod(p1.y, l, r); 

    vec3 bg = mid;
    vec3 color = bg;
    vec4 sine_color = sine(sine_k, w, -kt * dir + (dir >= 0.0 ? 0.5*TAU/sine_k.x : 0.0), p1);
    color = mix(color, sine_color.rgb, sine_color.a);     

    fragColor = vec4(color,1.0);
}

float sun(vec2 uv, float battery)
{
 	float val = smoothstep(0.3, 0.29, length(uv));
 	float bloom = smoothstep(0.7, 0.0, length(uv));
    float cut = 3.0 * sin((uv.y + iTime * 0.2 * (battery + 0.02)) * 100.0) 
				+ clamp(uv.y * 14.0 + 1.0, -6.0, 6.0);
    cut = clamp(cut, 0.0, 1.0);
    return clamp(val * cut, 0.0, 1.0) + bloom * 0.6;
}

float grid(vec2 uv, float battery)
{
    vec2 size = vec2(uv.y, uv.y * uv.y * 0.2) * 0.01;
    uv += vec2(0.0, iTime * 4.0 * (battery + 0.05));
    uv = abs(fract(uv) - 0.5);
 	vec2 lines = smoothstep(size, vec2(0.0), uv);
 	lines += smoothstep(size * 5.0, vec2(0.0), uv) * 0.4 * battery;
    return clamp(lines.x + lines.y, 0.0, 3.0);
}

float dot2(in vec2 v ) { return dot(v,v); }

float sdTrapezoid( in vec2 p, in float r1, float r2, float he )
{
    vec2 k1 = vec2(r2,he);
    vec2 k2 = vec2(r2-r1,2.0*he);
    p.x = abs(p.x);
    vec2 ca = vec2(p.x-min(p.x,(p.y<0.0)?r1:r2), abs(p.y)-he);
    vec2 cb = p - k1 + k2*clamp( dot(k1-p,k2)/dot2(k2), 0.0, 1.0 );
    float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
    return s*sqrt( min(dot2(ca),dot2(cb)) );
}

float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

float opSmoothUnion(float d1, float d2, float k){
	float h = clamp(0.5 + 0.5 * (d2 - d1) /k,0.0,1.0);
    return mix(d2, d1 , h) - k * h * ( 1.0 - h);
}

float sdCloud(in vec2 p, in vec2 a1, in vec2 b1, in vec2 a2, in vec2 b2, float w)
{
	//float lineVal1 = smoothstep(w - 0.0001, w, sdLine(p, a1, b1));
    float lineVal1 = sdLine(p, a1, b1);
    float lineVal2 = sdLine(p, a2, b2);
    vec2 ww = vec2(w*1.5, 0.0);
    vec2 left = max(a1 + ww, a2 + ww);
    vec2 right = min(b1 - ww, b2 - ww);
    vec2 boxCenter = (left + right) * 0.5;
    //float boxW = right.x - left.x;
    float boxH = abs(a2.y - a1.y) * 0.5;
    //float boxVal = sdBox(p - boxCenter, vec2(boxW, boxH)) + w;
    float boxVal = sdBox(p - boxCenter, vec2(0.04, boxH)) + w;

    float uniVal1 = opSmoothUnion(lineVal1, boxVal, 0.05);
    float uniVal2 = opSmoothUnion(lineVal2, boxVal, 0.05);

    return min(uniVal1, uniVal2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
