void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord*2.0 - iResolution.xy)/min(iResolution.x,iResolution.y);
    vec3 ro = vec3(0.0,0.0,-45.0);
    float screenZ = 1.0;
    vec3 rd = normalize(vec3(uv,screenZ));


    // For raymarching const values.
    const int sampleCount = 34;
    const int sampleLightCount = 2;
    const float eps = 0.01;

    // Raymarching step settings.
    float zMax = 65.0;
    float zstep = zMax / float(sampleCount);

    float zMaxl = 20.0;
    float zstepl = zMaxl / float(sampleLightCount);

    // Easy access to the ray origin
    vec3 p = ro;

    // Transmittance
    float T = 1.0;

    // Substantially transparency parameter.
    float absorption = 60.0;

    // Light Direction
    vec3 sun_direction = normalize(vec3(1.0, 0.0, 0.0));

    vec4 col = vec4(0.0);

    vec2 mat;


    for (int i = 0; i < sampleCount; i++)
    {


        mat = map(p);
        float density = mat.x;

        if (density > 0.0)
        {

            float tmp = density / float(sampleCount);

            T *= 1.0 - (tmp * absorption);

            if (T <= 0.01)
            {
                break;
            }

            float Tl = 1.0;

            vec3 lp = p;

            for (int j = 0; j < sampleLightCount; j++)
            {
                float densityLight = map(lp).x;

                if (densityLight > 0.0)
                {
                    float tmpl = densityLight / float(sampleCount);
                    Tl *= 1.0 - (tmpl * absorption);
                }

                if (Tl <= 0.01)
                {
                    break;
                }

                lp += sun_direction * zstepl;
            }

            float opaity = 50.0;
            float k = opaity * tmp * T;            

            vec4 cloudColor = vec4(vec3(0.01,0.11,0.5),1.0);

            if(mat.y == 1.0){
                cloudColor.rgb = hsv(pmodid/40.0,1.,1.5);           
            }else if(mat.y == 3.0){
                cloudColor.rgb = vec3(.0,.8,.9); 

            }else{
                pmod(p.xy,12.0);
                cloudColor.rgb = hsv(pmodid/12.0,1.0,1.8);                   
            }

            vec4 col1 = cloudColor * k;

            float opacityl = 30.0;
            float kl = opacityl * tmp * T * Tl;
            vec4 lightColor = vec4(.0, 0., .1, 1.0);
            vec4 col2 = lightColor * kl;

            col += col1 + col2*3.0;
        }

        p += rd * zstep;
    }

    col.a *= 0.5;

    if(col.a == 0.0){
        col.rgb = vec3(0.0);
    }
    // Output to screen
    fragColor = vec4(col);
}

const float pi=acos(-1.0);
const float pi2=pi*2.0;

mat2 rot(float a){
    float c=cos(a), s=sin(a);
    return mat2(c, s, -s, c);
}

vec2 pmod(vec2 p, float r){
    float a=atan(p.x, p.y)+pi/r;
    float n=pi2*(-sin(0.42*iTime)*sin(0.42*iTime)+0.35)/r;
    a=floor(a/n)*n-pi;
    return p*rot(-a);
}

float crossf(vec3 p, float r){
    p=abs(p);
    float dxy=max(p.x, p.y);
    float dyz=max(p.y, p.z);
    float dxz=max(p.z, p.x);
    return min(dxy, min(dyz, dxz))-r;
}

float boxf(vec3 p, vec3 b){
    p=abs(p)-b;
    return length(max(p, 0.0))+min(max(p.x, max(p.y, p.z)), 0.0);
}


float mengersponge(vec3 p, float scale, float width){
    float d=boxf(p, vec3(1.0));
    float s=1.0;
    for(int i=0; i<5; i++){
        vec3 a=mod(p*s, 2.0)-1.0;
        s*=scale;
        vec3 r=1.0-scale*abs(a);
        float c=crossf(r, width)/s;
        d=max(d,c);
    }
    return d;
}

float map(vec3 p){
    p=mod(p, 4.0)-2.0;
    return mengersponge(p, 3.0, 1.0);
}

