//Buffer A
//swap and Voronoi particle tracking learned from user Wyatt on Shadertoy.
void swap (inout vec4 a, vec2 U, vec2 r) {
    vec4 n = A(U+r);
    if (length(U-n.xy)<length(U-a.xy)){
        a = n;
    }
}

void mainImage( out vec4 Q, in vec2 U)
{
    // FIND NEAREST PARTICLE
    vec4 a = A(U);
    swap(a,U,vec2(1,0));
    swap(a,U,vec2(0,1));
    swap(a,U,vec2(-1,0));
    swap(a,U,vec2(0,-1));

    Q = a;
    Q.w -= .005;
    Q.xy += Q.zw;
    Q.zw *= AIR;

    if(length(Q.zw) < 0.001){ // firework explodes
      	Q.zw = normalize(U - Q.xy);//* VELOCITY;
