        float iAsFloat = float(i);

        float randomSpeed = rand(iAsFloat+1.0);


        float curRotation =  randomSpeed * speed ;
     	planets[i] = rotationMatrix(iTime * curRotation, vec3(0.0,1.0,0.0)) * planets[i];   
    	planets[i].x += 3.0+ iAsFloat*3.0;
        float newPlanet = sdSphere(planets[i].xyz, .30 * randomSpeed * 2.5);

        planetShapes[i] = newPlanet;

        speed -= .3;

        vec4 newMoonPos = planets[i];
        newMoonPos.x += .6* randomSpeed;
        newMoonPos = rotationMatrix(iTime*20. * randomSpeed, vec3(0.0,1.0,0.0)) * newMoonPos;
        //newMoonPos += 1.3;
        //planets[i];



        newMoonPos = rotationMatrix(iTime*15., vec3(0.0,1.0,0.0))*planets[i]+.7;
        newMoonPos.y -= 1.0;
