        float newMoon = sdSphere(newMoonPos.xyz, .1);
        moonShapes[i] = newMoon;
    }



    float sun = sdSphere(p, 1.50);

    finalShape = min(planetShapes[0],planetShapes[1]);
    finalShape = min(sun,finalShape);
